INSERT INTO emlog_blog VALUES('12','七月，随想','1438268704','<p align=\"center\">\r\n	<p align=\"center\">\r\n		<a target=\"_blank\" href=\"http://www.mryu.top/content/uploadfile/201507/932a1438268825.jpg\" id=\"ematt:12\"><img src=\"http://www.mryu.top/content/uploadfile/201507/thum-932a1438268825.jpg\" title=\"点击查看原图\" alt=\"502e42ed93b7f.jpg\" height=\"237\" border=\"0\" width=\"420\" /></a> \r\n	</p>\r\n</p>\r\n<p align=\"center\">\r\n	<br />\r\n</p>\r\n<p align=\"center\">\r\n	七月，随想\r\n</p>\r\n<p align=\"center\">\r\n	文 素手纤云\r\n</p>\r\n<p align=\"center\">\r\n	<br />\r\n</p>\r\n<p align=\"center\">\r\n	走进七月，忧伤，有些淡忘\r\n</p>\r\n<p align=\"center\">\r\n	念与想，一如光阴滋长\r\n</p>\r\n<p align=\"center\">\r\n	蛙鸣声里，有些曲子开始荒凉\r\n</p>\r\n<p align=\"center\">\r\n	&nbsp; 远方的足音，早已穿透风尘时光<span style=\"position:relative;left:-100000px;\"> )</span> \r\n</p>\r\n<p align=\"center\">\r\n	点水蜻蜓，穿花蝴蝶\r\n</p>\r\n<p align=\"center\">\r\n	依然恋着的，不知道是谁的梦乡\r\n</p>\r\n<p align=\"center\">\r\n	||\r\n</p>\r\n<p align=\"center\">\r\n	抚摸着七月的天空\r\n</p>\r\n<p align=\"center\">\r\n	单纯，热烈，似旧时情怀\r\n</p>\r\n<p align=\"center\">\r\n	梧桐花开处，有诗落在风里\r\n</p>\r\n<p align=\"center\">\r\n	又近秋色，纤薄的字符\r\n</p>\r\n<p align=\"center\">\r\n	填不满，许多愁\r\n</p>\r\n<p align=\"center\">\r\n	远岫青山，疏雨晚晴\r\n</p>\r\n<p align=\"center\">\r\n	守在小石子路上的秋千架\r\n</p>\r\n<p align=\"center\">\r\n	抱了一怀，清凉\r\n</p>\r\n<p align=\"center\">\r\n	||\r\n</p>\r\n<p align=\"center\">\r\n	萧萧竹叶，花落沉香\r\n</p>\r\n<p align=\"center\">\r\n	七月里的故事，不诉春风离殇\r\n</p>\r\n<p align=\"center\">\r\n	暮色苍茫中，星星闪耀的眸子\r\n</p>\r\n<p align=\"center\">\r\n	远不如萤火虫晶亮\r\n</p>\r\n<p align=\"center\">\r\n	归巢的倦鸟，你情我浓\r\n</p>\r\n<p align=\"center\">\r\n	守护着一处安稳\r\n</p>\r\n<p align=\"center\">\r\n	在今世里地久天长\r\n</p>\r\n<p align=\"center\">\r\n	||\r\n</p>\r\n<p align=\"center\">\r\n	七月的邮票，有阳光的烙痕\r\n</p>\r\n<p align=\"center\">\r\n	炙烤着，忧伤，念想\r\n</p>\r\n<p align=\"center\">\r\n	风，剪下一纸窗花\r\n</p>\r\n<p align=\"center\">\r\n	贴在，落花深埋的小院墙\r\n</p>\r\n<p align=\"center\">\r\n	蝉蜕，悄悄将尘埃收藏\r\n</p>\r\n<p align=\"center\">\r\n	生命，在轮回中，次第开放\r\n</p>\r\n<p align=\"center\">\r\n	<br />\r\n</p>\r\n<p align=\"center\">\r\n	<br />\r\n</p>\r\n<p align=\"center\">\r\n	<br />\r\n</p>','','','1','8','blog','126','2','1','n','n','n','y','y','','','0');
INSERT INTO emlog_blog VALUES('13','问佛','1438315605','<p align=\"left\">\r\n	问佛\r\n</p>\r\n<p align=\"left\">\r\n	文/仓央嘉措\r\n</p>\r\n<p align=\"left\">\r\n	<br />\r\n</p>\r\n<p align=\"left\">\r\n	<strong><span style=\"color:#337FE5;\">我问佛：为何不给所有女子羞花闭月的容颜？ </span></strong><br />\r\n佛曰：那只是昙花的一现，用来蒙蔽世俗的眼 <br />\r\n没有什么美可以抵过一颗纯净仁爱的心 <br />\r\n我把它赐给每一个女子 <br />\r\n可有人让它蒙上了灰 <br />\r\n<br />\r\n<strong><span style=\"color:#337FE5;\">我问佛：世间为何有那么多遗憾？ </span></strong><br />\r\n佛曰：这是一个婆娑世界，婆娑即遗憾 <br />\r\n没有遗憾，给你再多幸福也不会体会快乐 <br />\r\n<br />\r\n<strong><span style=\"color:#337FE5;\">我问佛：如何让人们的心不再感到孤单？ </span></strong><br />\r\n佛曰：每一颗心生来就是孤单而残缺的 <br />\r\n多数带着这种残缺度过一生 <br />\r\n只因与能使它圆满的另一半相遇时 <br />\r\n不是疏忽错过，就是已失去了拥有它的资格 <br />\r\n<br />\r\n<strong><span style=\"color:#337FE5;\">我问佛：如果遇到了可以爱的人，却又怕不能把握该怎么办？ </span></strong><br />\r\n佛曰：留人间多少爱，迎浮世千重变 <br />\r\n和有情人，做快乐事 <br />\r\n别问是劫是缘 <br />\r\n<br />\r\n<strong><span style=\"color:#337FE5;\">我问佛：如何才能如你般睿智？ </span></strong><br />\r\n佛曰：佛是过来人，人是未来佛 佛把世间万物分为十界：佛,菩萨,声闻,缘觉,天,阿修罗,人,畜生,饿鬼,地狱； <br />\r\n天,阿修罗,人,畜生,饿鬼,地狱.为六道众生； <br />\r\n六道众生要经历因果轮回,从中体验痛苦。 <br />\r\n在体验痛苦的过程中,只有参透生命的真谛,才能得到永生。 <br />\r\n凤凰，涅盘 <br />\r\n<br />\r\n佛曰，人生有八苦：生，老，病，死，爱别离，怨长久，求不得，放不下。 <br />\r\n佛曰：命由己造，相由心生，世间万物皆是化相，心不动，万物皆不动，心不变，万物皆不变。 <br />\r\n佛曰：坐亦禅，行亦禅，一花一世界，一叶一如来，春来花自青，秋至叶飘零，无穷般若心自在，语默动静体自然。 <br />\r\n<br />\r\n佛说：万法皆生，皆系缘份，偶然的相遇，暮然的回首，注定彼此的一生，只为眼光交汇的刹那。 <br />\r\n缘起即灭，缘生已空。 <br />\r\n<br />\r\n我也曾如你般天真 <br />\r\n佛门中说一个人悟道有三阶段：“勘破、放下、自在。” <br />\r\n的确，一个人必须要放下，才能得到自在。 <br />\r\n<br />\r\n<strong><span style=\"color:#337FE5;\">我问佛：为什么总是在我悲伤的时候下雪 </span></strong><br />\r\n佛说：冬天就要过去，留点记忆 <br />\r\n<br />\r\n<strong><span style=\"color:#337FE5;\">我问佛:为什么每次下雪都是我不在意的夜晚 </span></strong><br />\r\n佛说:不经意的时候人们总会错过很多真正的美丽 <br />\r\n<br />\r\n<strong><span style=\"color:#337FE5;\">我问佛:那过几天还下不下雪 </span></strong><br />\r\n佛说:不要只盯着这个季节，错过了今冬\r\n</p>\r\n<p align=\"left\">\r\n	<br />\r\n</p>\r\n<h1>\r\n	<br />\r\n</h1>\r\n<p>\r\n	<br />\r\n</p>','','','1','9','blog','125','0','0','n','n','n','y','y','','','0');
INSERT INTO emlog_blog VALUES('20','香港中文大学推荐书单各书简介','1438669915','<p>\r\n	1 《一百分妈妈》 马以工\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 这本书的内容是教导大家如何生活健康生活在不健康的环境中。第一章谈“如何避免生下畸形儿”，第二章介绍“健康的食物在哪里？”第三章指出“生活周边的有毒物”，第四章分析“为什么癌症患者越来越多？”第五章是“我不……运动环保的第一步”，第六章告诉大家“节约能源就是节省开支”，最后一章马以工勉励大家“积极参与！争取健康的环境”。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	2 《一路走来一路读 》 林达\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; &nbsp;作者用优美而流畅的文笔，带领读者走进一座座美国历史名镇，并展现美国当今有历史意义的事件。有别于一般历 史述事，林达的笔触掀开的是人性、人格及制度后面的人文细节。作者相信那是民主制度的根基，由此影射对中国文化及制度的反省。作品对东西文化的异同而形成 的制度不同步，轻描淡写，却令人深思。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	3 《二胡》 陈若曦\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; &nbsp;小说《二胡》写了美国华人胡为恒与在中国大陆妻子梅玖、美国华人胡景汉与大陆妻子绮华的婚姻家庭关系，展示 出东、西方两种文化的碰撞冲突，同时也通过梅玖临终前的谅解丈夫与胡景汉决定回大陆与绮华团聚的情节，反映了两种文化是可以包容融合的，作者自觉或不自觉 地立足于唯物史观而使这部长篇小说的主题的丰富与深刻性达到新高度。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	4 《人在欧洲》 龙应台\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; &nbsp;《人在欧洲》是台湾作家龙应台旅居瑞士期间的“跨域”书写。双重视域互为镜像是其文本最显著的特征．双重视域既是作者在文本中呈现出来的本土与母土两个疆界不同的文化现象，同时也是观察这两个疆界不同的文化视角．在《人在欧洲》中，作者一方面从中国文化视角出发观察欧洲社会现象．另一方面则以欧洲为参照系反观台湾社会现实。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	5 《十万个为什么》 严济远\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 此书带领读者探索奇妙的大自然，逐一解开“臭氧洞”、“厄尔尼诺”、“龙卷风”、“海市蜃楼”……之谜。它以科学的态度，通俗的手法，引发读者对自然现象的好奇，再以趣味的方式，深入浅出地解释地球的形成及各种地理、地质、气象上常见现象对人类的影响。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 6 《三十五年的新闻追踪: 一个日本记者眼中的中国》 吉田实着； 王武云, 朱新建译\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 这是一位日本记者遍历中、港、台三地的新闻生涯的纪实。作者是资深的新闻工作者，曾现场采访过中日邦交正常化、巴基斯坦军事政变、中印战争等重大历史事件。在他笔下再现了二十世纪下半的亚洲风云突变，既是中日关系史的现代篇章备忘录，也是新闻学的一本不可多得的实用教材。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 作者生于东京，少年时代在台湾渡过，自称是“吃台湾米粉长大的日本人”。在日本外国语大学毕业后任职《朝日新闻》，并派到中国、香港和台湾等多个亚洲国家任特派员，至1984年升至《朝日新闻》亚洲总局长。自1990年5月起任朝日中国文化学院院长。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 作者任职记者期间，经历了中国数次重要的历史事件，包括文化大革命、乒乓外交、四人帮等。作者用轻松、平实的手法，和新闻工作者的触觉去报导其见闻，让读者从另一个角度去了解中国和亚洲近代发生的大事。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 7 《小王子》 圣修伯理\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 你儿时有没有遇过用尽心机画一些自以为表达心中所想的图画，而大人却不明所以，还着你不要浪费时间，不如专心读书的情况？你的绘画天份有否给扼杀了？本故 事的主人翁驾驶的飞机意外落在撒哈拉沙漠，遇到从另一个星球而来小王子，小王子要求机师给他画一只小绵羊，可是自从六岁时因画蛇吞象未能为人所明白而感到 沮丧后，他已放弃了画画，因此他画来画去也画不到像样的绵羊，最后画了一个箱子便算数, 还说绵羊就在里面， 小王子却十分满意。看这本书需要运用丰富的想象力。作者以童话式的笔触, 阐述含意深远的哲理寓言，透过小王了天真浪漫却富含意的对话，引领我们去深思，究竟什么才是生命最终的期待。不同年龄阅读这本书，感受会不同。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 8 《千年一叹》 余秋雨\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 余秋雨以一个中国文化人的身份，用三个月的时间，踏上一个充满惊险的旅程：从希腊出发，南下埃及，再穿过中东的以色列、巴勒斯坦、约旦、伊拉克、伊朗，再 走访巴基斯坦、印度、尼泊尔等十个国家，探索“人类文明的经络系统”。作者在书中对古代文明的兴衰、当今文明冲突的根源、中华文明的独特性都作出了细微的 观察与深刻的反省，为读者带来惊喜、感叹与深思。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 9《大汗之国：西方眼中的中国》（The Chan\'s Great Continent） Jonathan D. Spence\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 从马可波罗到利马窦、伏尔泰、奥斯汀、赛珍珠，七百年来中国在西方人眼中的地位不断变化却又始终引人入胜。这是一本由学识渊博，文笔生动的汉学家所写的好书。作者把西方从十三世纪到二十世纪作家笔下新奇多样的中国，尽皆搜罗在这本足以见证其间中西文化交流的重要著作当中。透过这面“西洋镜”所看到的，也是足足八百年以来西方人对中国之记录与幻想、洞察与偏见。“无论是中国在西方引起的强烈情感，一波又一波尝试描述并分析这个国家及其人民的企图，或是西方人对有关中国消息的强烈兴趣，都明确道出了这个国家所散发的魅力。”\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 10 《大学之理念》 金耀基\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; &nbsp;本书是金耀基教授对大学的理念、性格及发展的精微论述，并涉及到人文与科学的文化观的论争、大学的世界精神、通识教育，以及在世纪之交大学理念与角色的转换、现代性、全球化与华人教育等问题。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; What is a university? This is a question particularly relevant to us as students and faculty members. At this time of rapid social changes, what are the enduring values that we should continue to cherish? Ambrose King(金耀基) - currently Vice Chancellor of The Chinese University of Hong Kong, is a famous sociologist and educator. Anyone who aspires to the ideals of a university will find this well-written book enlightening, insightful, interesting and highly readable.\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 11 《大学道上：教育理想与实践的反思》 郭少棠\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 作者对教育工作充满了理想和热情，他关注香港教育的发展、关心大学生的成长；又慨叹教育工作者的重担、实践理想时遇到的无奈和困惑。在大学道上走了超过三十年，作者不断的探索、反思，深信只有勇于承担与超越，大学师生才能面对新时代的挑战。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 12 《文华集》 林太乙\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 《文华集》里面的文章篇篇短小精彩，很配合香港人节奏急促的生活，在工作之馀、晚饭之后，细读一两篇，不但有所启发，更是一种享受。本书共选辑了四十多篇 文章，全都是中国当代着名作家的作品，作者包括林语堂、陈若曦、梁实秋、张晓风、杏林子、三毛等，形式包括散文、传记、游记、短篇小说等，文笔流畅，内容 感人，其中更有多篇曾翻译成外文，在外国杂志上发表。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 13 《文化苦旅》 余秋雨\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 《文化苦旅》是余秋雨教授80年代在海内外讲学和考察途中写下的作品，是他的第一部文化散文集，也是他的代表作。全书所收作品主要包括两部分，一部分是历史、文化散文，散点论述，探寻文化；另一部分是回忆散文。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; Renown for his prose, Yu Qiuyu (余秋雨) in this book brings his readers through his cultural excursions to various locations in China. Reading this book is a cultural experience that no “packaged tours” can match. Lucid and beautiful, the lines are teeming with cultural sensitivity and profound knowledge. This book is as enjoyable as other works that Yu has written, including Shuang Leng Chang He (霜冷长河: 台北时报出版社，1999) and Shan Ju Bi Ji (山居笔记：台北尔雅出版社，1995). Pick anyone and start the journey from there.\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 14 《少年凯歌（龙血树）》 陈凯歌\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 认识别人不容易，认识自己更难。陈凯歌的《少年凯歌》是一部自传式散文，讲述他如何在文革中成长。文革对他而言是一种自我认识的过程，给予读者一种很大的 启示。人确实需要面对自己，需要有勇气否定自己，亦要有勇气面对将来，陈凯歌在书中有深彻的自我反省，在认识自己之馀亦顿悟了人生的意义。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 15 《中国大历史》黄仁宇\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 黄仁宇教授的《中国大历史》(China?A Macro History) 很值得推荐, 因为: (一) 它易读，作者用很平易近人的英文写作，对任何想促进英语能力的读者都是很好的入门书。 (二) 作者对中国历史有透彻的看法，不是以道德观点看历史人物，而是看人在环境中发挥的作用及环境的回应，是一本客观的历史书。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 16 《中国文化要义》 梁漱溟\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 介绍这本书给大家有两个原因。第一个是关于中国文化对经济发展的影响：在以前，大家都觉得影响是负面的，本书作者梁漱溟也认为中国文化阻碍了中国产业革命 的发生和资本主义的萌芽。不过，深受中国文化影响的东亚四小龙，近二十年的经济成长却很快。究竟我们应该怎样看中国文化的优劣呢？第二个原因是关于政治方 面的：本书有很大篇幅讲述中国文化局限了民主政制的发展。近年来，台湾、中国大陆和香港都有一股走向民主政治改革的力量。究竟这些改革会否受到中国文化的 束缚而产生重大的问题呢？这是值得大家探讨的。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 17 《中国美术史百题》 谭天\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 本书共有一百个专题，每个专题由一千至二千字不等，从中国美术的起源一直写到清末民初的着名画家吴昌硕，重点地介绍具代表性的艺术品、作品、遗迹、艺术理 论，并且顺时代的次序来编排，从而勾划出中国美术发展的概况。由于这一百篇短文都独立成篇，因此初接触中国美术的读者无须由第一页读至最后一页，可以挑选 自己有兴趣的专题来读。作者归纳了近年来有关中国美术的一些研究成果，并扼要地评述和介绍，无疑是认识中国美术的一个好门径。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 18 《分成两半的子爵》（II Visconte Dimezzato） 卡尔维诺\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 好小说有很多，但有的太长，像《追忆似水年华》；有的太深，像《尤利西斯》；有的太难读，像《跳飞机》；有的太沉闷，像《城堡》；有的太出奇，像《恋人絮 语》，总有这样那样困难，不一定适合年轻读者。卡尔维诺的《分成两半的子爵》则有趣易读，以超现实、谐趣的手法，写主人翁在一场战争中被轰成两半，从破碎 到辗转争斗，然后重新找到完整的真实。这小说既有创意，又有深意；而且可以作为认识这位文学大师，以至进入文学世界的开始。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 19 《未来城》（A Scientist in The City） 詹姆斯·特菲尔\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 科学家告诉我们：现代都市是一连串科学发现的结果。他认为操纵原子的能力，释放储存能量的能力，利用电子储存、传送资讯的能力 , 这三种能力改变了我们所居住的城市, 似乎很深奥。但作者詹姆斯·特非尔身为物理学教授，深入浅出地以科技的角度检视了都市，他谈到现代都市的诞生、都市的生态，谈到钢铁城市、琉璃城、城市地下庞杂的世界，以至未来的都市以及都市的死亡。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 20 《失窃的未来：生命的隐形浩劫》（Our Stolen Future） Theo Colborn, Dianne Dumanoski, John Peterson Myers\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 身在毒中不知毒？身在危中不知危？青蛙多了两条腿、北极熊长出双性器官、野生雏凋身躯畸变、雌雄难辨、男性精虫降低了百分之五十、女性患生殖癌的年龄下 降、男性患睾丸癌上升，雌性荷尔蒙威胁人类有增无减。过去半世纪以来，人类不断将人工化学物质倾倒在土地上、河流中、海洋里，光是杀虫剂一种，就有五十亿 磅，干扰荷尔蒙，改变人类的行为智商和生殖能力，对人类造成慢性的毁灭危机。人工化学革命引发荷尔蒙的浩劫是否传给后代的毒药？偷走了孩子的未来？看不见 的伤害就继续“偷生”？《失窃的未来》为人类的环境悲剧敲响警钟，呼吁共同关注这扭转生命的隐形浩劫，是一本令读者读后震撼的书。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 21 《生命的奋进》 梁漱溟、牟宗三、唐君毅、徐复观\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 四位作者都属于“新儒家”，他们的成就已为世公认，甚至收进了大英百科全书；他们的学问、思想、为人，都影响广泛。《生命的奋进》是他们以散文方式来写自 己青少年时代的生活，文字清澈明白，而且感情丰富。他们在中国处于忧患的年代成长，由童年起就一直奋斗下去，真诚地做学问的工夫和寻求做人的目的。这种人 生态度十分难得，对读者很有启发作用；从他们记述当年生活的文章中，更可以寻回许多已经失落了的中国传统优美精神。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 22 《失明给我的挑战》 程文辉\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 本书是一部感情真挚的作品，作者挣扎奋斗的过程尤其感人。作者在六个月大的时候，因用了不良中医的眼药而引致失明。在当时的社会，许多人都对失明人士存有 鄙视和侮辱的态度。不过，作者对此不但没有怨恨的心理，反而因为这些人无知而觉得他们很值得可怜，认为自己有责任帮助他们改变过来。作者对于自己的不幸， 也无怨天尤人或抱消极的态度。她觉得人生必定会遇到困难，因此应该努力去帮自己或他人克服彼此的困难。她这种宽大为怀、积极乐观的人生态度和精神，是很值 得欣赏和学习的。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 23 《生活简单就是享受》（Simplify Your Life） Elaine St. James\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 原来“生活简单”这回事，说时容易做时难，必须时刻有颗醒觉的心，在繁忙琐碎、太多诱惑而不知所措时，可以硬下心肠，不作无谓的事、无谓的花费，不要为别 人的想法而活，不强求自己及别人。简单的生活源自简单的思维，任何事复杂化只会令自己走入死胡同。陶渊明谓：“悠然见南山。”南山就在眼前，你看到了吗？\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 24 《生命中不能承受之轻》 米兰·昆德拉\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 本书的英文名是「Unbearable Lightness Of Being」，其实轻来自重，生命可以是轻，也可以是沉重，书中的两个女主角就分别代表了生命中的轻与重。一个是典型的现代女性，有不少的经历，觉得自己 的生命轻得像羽毛，经常飘来飘去，没有一处地方是据点。另一个是个较为简单的女性，她要求的就是爱情，生活很重很痛苦，但唯有这样她才感到自己真真正正的 存在。本书已改篇成电影《布拉格之恋》，读者不妨一看。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 25 《生命中不可错过的智慧》（All I Really Need to Know I Learned in Kindergarten） 罗勃·傅刚 (Robert Fulghum)\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 《生命中不可错过的智慧》提供了一个新鲜的角度看人生：少一些竞争、不自私、勇于自我反省及思考。本书反璞归真的精神对香港人具有醒神作用，它提醒读者要从人生真正意义着眼，而非依从一般“醒目仔”功利主义的价值观看人生。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 26 《世界着名科学家演说精粹》 朱长超\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 这是一本发人深省的好书。书中收录许多篇着名科学家，如巴斯德、爱因斯坦、吴大猷及李远哲等在不同年代和不同场合所发表的讲辞。阅读这些演讲辞让我们了解和学习这些科学家的伟大人格和理想。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 27 《打倒符碌──做个有料的大学生》 刘绍麟\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 作者毕业于香港中文大学社会系，对中大的情况相当熟悉。本书内容也十分充实，足以引导同学于短短三年的大学生涯里，成为一个有真才实学的大学生。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 28 《西潮》 蒋梦麟\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 本书以传记文学的方式述说中国人一百年来追求现代化的努力与甘苦，文笔流畅，情理兼备，宛如一部精美的近代史。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 29 《共悟人间：父女两地书》 刘再复、刘剑梅\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 这本书是刘再复和他女儿刘剑梅的通信集。两人都是文字优美、见解独到的当代作家，以温馨可爱的情怀，表达深刻的思想。读此书的时候，就像是在作者美国科罗拉多家中的花园树荫下，跟他父女俩谈论文学与人生。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 30 《朱元璋传》 吴晗\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 本书不但史实有趣，而且作者的文笔很好, 把朱元璋描写得活灵活现。平常农家子弟出身的明朝开国君主朱元璋，为甚么在后来会变得那么凶残，杀掉那么多人？中国历史家都不大了解。吴晗以现代化的历史 观来写这个传记，同时考虑到当时整个社会的情况、政治状况、经济变化等，从各方面的因素解释一个人的行为。迄今，没有人能像吴晗那样把朱元璋这段故事讲得 那么生动透彻。作者在资料搜集和考证工夫做得一丝不苟，务求每句话都有出处，这使读者一方面可以从丰富的史料中欣赏到有趣的故事，同时对史实更加尊重。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 31 《成功，你敢吗？》 周融\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 本书由人生道上经验丰富的作者所写。文中有我们熟识的古今历史例子及故事，是一本能启发今时今日的青年，积极进取的导航书。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 32 《成长? 发现最好的自己 》 凌志军\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 作者以北京的微软亚洲研究院的精英作为分析对象，指出他们不见得更聪明，所受的教育亦跟其它人没有太大的不同，但是他们却是成功、快乐、富有的典型。以个 桉式的介绍，作者以轻快的文笔描绘出每位精英在成长过程中关键性的情况和事例，归纳出他们成长的共通的法则。中大学生务必一读，而中大教授亦应细读，看如 何引导学生成长。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 33 《老虎．伍兹传奇》（A Biography of Tiger Woods） 史崔吉\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 本书讲述哥尔夫球坛新贵伍兹的成长过程及其心路历程，其最吸引之处是伍兹人生的两个阶段。首先是作为一个黑人，在哥尔夫球这“上流社会活动”的圈子中，由 备受冷落以至为人接受的经过；其次是在攀上高峰后求突破的艰苦岁月。他的经验正好说明成功必须经过考验，而在过程中，亦必须具备足够勇气、忍耐及不屈不挠 的精神，披荆斩棘，方可踏上坦途。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 34 《安杰拉的灰烬》（Angela\'s Ashes） Frank McCourt\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 世上基本有两种人：拥有快乐童年的人与拥有悲哀童年的人。其实，世上还有第三种人，他拥有一个快乐地悲哀——或者悲哀地快乐——的童年，就像《安杰拉的灰烬》，尽管一切灰飞烟灭，字里行间毕竟仍觉血肉相连。童年的回忆，恋恋不去，悲喜交杂地萦绕作者与读者一生。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 35 《地图集: 一个想象的城市的考古学》 董启章\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 董启章是香港新一代作家中最有才华的其中一位。本书以异常丰富的想象力，采取近乎卡夫卡式的寓言手法，叙说香港的历史、文化、政权转移现象，游走于现实描写与虚拟阅读之间，是一位优秀的土生土长作家的优秀之作。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 36 《沈从文自传》 沈从文\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 如果要看一个作家的性情，看他的散文就最清楚了；而自传通常是散文体。本书是作者沈从文二十岁左右时自撰的前半生传记，由十八篇散文组成。沈从文成长的年代正值清末至民国期间, 有许多事情发生。当时社会非常动荡，死人无数。看这本自传令人非常感动，从作者的描写中，读者可以感觉整个历史在变化，但作者很客观，没有悲伤，也没有涉入那个情景埸面。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 37 《我是谢坤山》 谢坤山\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 在此刻，香港人经历数年的经济低潮、破产裁员等，委实不好受。但当看到不少人毫不珍惜生命时，我们不禁要问：“甚么是生命的意义？人的生存价值又是甚 么？”在这方面，作者谢坤山给了我们很好的启示，他说：“我不是从‘拥有’的角度；而是以‘成为’的角度来探索人生。我深信‘命’是没有好、坏之分，因为 一切都是体验。人的一生，不只是求温饱, 而是追求生命的丰富与开阔。”\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 38 《我对总理说实话》 李昌平\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 作者出身农家，任乡镇干部18年。这本“为民请命”之书，生动而真实，让读者看到一个哭声处处的农民中国，看到中国改革的艰难，农民与基层政府的利害冲突，种种制度上的弊病及民众的无奈。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 39 《李光耀回忆录》 李光耀\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 本书不但是李光耀的自传：关于他的成长和奋斗以至成为政坛上成功的领袖的心路历程；更是一本充满智慧的历史鉅著，处处展现出一个政治人物应有的宏远目光， 缜密思维，敏锐的判断和果敢的作为。在他非凡成就的背后蕴含着高尚的情操，“择善而固执”地为理想坚持到底，无惧无悔，堪为年轻一代的借镜。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 40 《见证香港五十年》 周永新\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 作者以浅易的笔法写本书，做到入世而不说教。对香港的观察细致入微，对社会现象从微观至宏观加以剖析，分析香港人更是从表面看到骨子里。作者又从人与人之 间的相互关系，以至群体与社会和国家的互动，反映了香港人在这五十年间的不断改变，不断融汇贯通中西文化的价值观。作者以香港为家，藉本书对这个家作出入 木三分的描述，既抒发对香港的深厚情怀，又对香港的多方面演变作出中肯的分析，是少有具历史参考价值的好书。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 41 《余纯顺孤身徒步走西藏》 余纯顺\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 阅读游记可以把读者带往各处鲜为人知的境域，如果读者把自身当为作者，投入感更可倍增。近代探险家余纯顺徒步走遍西藏，所目睹及经历的事迹，都是常人难以接触到的，其坚毅不挠的性格，也在书中表露无遗。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 42 《波动》 赵振开\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 作者用“意识流”的手法，从不同人物的角度和人际关系展现年青一代在文化大革命后，面临的空虚和失落。尤其难能可贵的，是作者能藉着讲述这些人的恋爱故 事、亲情，以及人与人之间的关系，流露出对人生的看法。它既能触动人心，并且对这个世界有一套看法。全书布局巧妙，用字恰当，而且影像强烈，经常用“横 切”和“跳接”的写法，很有法国电影的味道。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 43 《林语堂传》 林太乙\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 作者替父亲立传，将我们带回一个温馨的人文世界，让我们重温清末民的历史；在作者眼中看到当时民众的遭遇，亲族的流离巅沛，俟抗战胜利以后，文化人又再遭劫难。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 44 《幸福之路》（The Conquest of Happiness） 罗素 (B. Russell)\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 罗素的《幸福之路》虽然写于大半个世纪前，但书中分析快乐与不快乐的原因却是历久常新。无论处于任何时代，人都会经历快乐与不快乐的时刻，作者特别指出导致不快乐的原因，教读者多点认识自己，更提出一些可以引领大家走向幸福之路的方法，甚富启发性。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 45 《物理五千年》 朱恒足\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 写一本有关科学尤其是物理方面的大众化读物是不容易的，因为既要有趣又要有相当程度的正确性。这本由朱恒足先生编着的《物理五千年》却做到了这一点。本书以讲述故事的方式阐明了物理学的历史发展，内容丰富，选材平均，相信对青年读者而言是一本颇富启发性的读物。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 46 《奇的巧克力工厂》（Charlie And The Chocolate Factory） 罗·达尔\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 罗·达尔 (Roald Dahl) 这位英国作家曾说：“我写儿童故事只有一个目的，就是要儿童爱上阅读。”如果小时候不看书，长大后就不会有如此习惯。罗·达尔 (Roald Dahl)所写的《奇异的巧克力工厂》非常有趣，故事精彩紧张，甚富幻想力，娱乐性和人情味都很浓厚。小朋友甚至成年人都应该看看。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 47 《阿城小说: 棋王、树王、孩子王》 阿城\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 这是令锺阿城在八零年代中一炮而红的三篇中篇小说，它们都以中国大陆的文化大革命作为社会背景。《棋王》探讨一个年青人在物质极度贫乏的环境之下，如何为 生命找到意义。《树王》对由于农业政策失误引致的环境破坏，作了深刻的描绘与反省，可能是中国第一篇环保小说。《孩子王》对文化大革命时期农村儿童教育的 荒谬处境作了深入的观察，发人心省。锺阿城是当代中国文笔最洗练、意象最清新，以及社会洞察力最深刻的作家之一。本书已是当代中国文学经典。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 48 《红楼梦》 曹雪芹\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 由清朝流传至今，《红楼梦》是一部极受欢迎的巨着。作者用心描写了几十个女性，个个性格不同，有可敬可畏的宝钗、利害嘴刁的探春、爽直可爱的史湘云，还有充满女强人本色的王熙凤等等，全都活生生；她们各有不同面谱和内心世界，作者都写得非常细腻，绝不平面化或黑白分明; 其中一些对白，能够充分表现出人物性格的?杂性。此书最精彩的地方是写人际关系，例子众多，是无法从其它小说中找到的。如果将这些人际关系加以研究，真的有助我们待人处世。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 49 《胡雪严》（上,中,下）《红顶商人》《灯火楼台》（上,下） 高阳\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 近代人的目光都集中在胡雪岩如何从身无分文，摇身变成中国鉅富，兼能头戴红顶；高阳这六册历史小说用更宽阔的角度，去看他的传奇：在清末中国的官场中，在 列强的政治和经济侵略中，在农民的生产工业化中，如何找到一个中国商人的定位；作者亦对胡雪岩最后破败的结局，寄以无限同情。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 50 《城市心灵》 郭少棠\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; “社区情怀，像轻烟般的思念，转眼飞越蓝天，旧街老巷，灯光不明不暗。无名的坊众，默默地辛劳度日。社区是沉默的、被动的，旧的社区尤其浮现的老态，令人 容易忽略旧区埋藏的历史。发掘这些旧区的旧人旧事，既是历史的工作，也是社区重建必须作出深入的考虑。社区旧情新生命。年轻一代的热诚，正好替旧区注入新 的生命。”\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 作者在油麻地渡过童年，对这个社区充满了感情和回忆，他用真挚、细腻而热情的笔法，带领读者走进这个即将重建的社区，书中也呈现了藏在作者心底对逝去双亲的思念，非常感人。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 51 《革命之子》 梁恒、夏竹丽\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 本书的特点是所写所载都是真人真事，提供了许多中国现代史的资料，例如文化大革命以及近十多年来的大事等，虽然这些消息可以从新闻中获悉，但没有看书那么 多感受。这段历史给人的印象是相当恐怖的，因为人与人之间互不信任，并且受到很大的约束，差不多完全没有自由，连做过甚么梦也要供出来，可想而知是多么的 悲惨了。由于这本书讲一个人的成长过程，其中有家庭变迁、做流氓、爱情等情节，令人看完之后，觉得自己也好像有这许多的经历。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 52 《美丽新世界》 赫胥黎\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 近几十年来，关于极权政治制度的书有两本最为突出：一本是George Orwell的《一九八四》，另一本就是赫胥黎的《美丽新世界》。这本书的好处是用小说形式，把一个十分严肃的课题介绍给读者，其中还加插了许多很有趣和 很富想象力的东西。本书写于1931年，当时正是纳粹主义、法西斯主义及共产主义等极权政治抬头的前夕。作者先知先觉，看到这些情况，加上担心应用科学会 为人类带来不幸，因此就写了本书来警告世人，指出我们要是不小心的话，将来便可能产生这样的“美丽新世界”。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 53 《娃娃看天下：玛法达的世界》（共六册） 三毛\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 这本源自阿根廷的漫画，作者利用一班幼稚园小孩子的世界，勾画出成年人世界的千奇百态，用轻松讽剌的手法，反映社会现实，随着三毛和她丈夫的先后离世，这原为西班牙文的三毛中文译本己成绝响。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 54 《科学史上的悬案》 江苏科普创作协会基础科学委员会\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 科普书籍虽然种类繁多，但一般都流于说理，比较沉闷，又或者只局限于某个领域，范畴过于狭窄。本书与别不同，讲述了许多不同科目的小故事，而且都是科学上 的“大件事”，例如万有引力、圆周率。许多地方写得像侦探小说，非常有趣，文笔亦很流畅。读者可以藉此书提高他们阅读科普书籍的兴趣。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 55 《思想方法五讲新编》（修订版） 劳思光\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 劳思光是当代中国的哲学大师，中文大学荣休教授。是书用简短的篇幅，以浅易的文字和通俗的例子，说明思想方法的意义和重要性，解释方法论的任务和范围，并扼要介绍了形式逻辑及经验科学方法的基本知识，为同类作品中的首选，适合希望增强思考能力的年青人阅读。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 56 《美德书: 伟大励志故事的宝藏》（The Book of Virtues: A Treasury of Great Moral Stories） William J. Bennett\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 本书收集大量着名经典故事、寓言、诗歌、散文、神话、名人逸事等，提供学习素材；作品优美隽永，激励人心。这些看来“老土”的、耳熟能详的故事，正是现今世代，每一个大学生要重温的，从中汲取经验和启发，培养道德标准和正确判断，从而装备自己，接受挑战，改造未来的命运。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 57 《时间简史》（A Brief History of Time） Stephen W. Hawking\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 身患顽疾并缺乏活动能力的人，要写出一本与最尖端科学研究有关的普及读物的困难之处，绝非常人可以想象。难得的是霍金(Stephen W. Hawking)用充满乐观及风趣的笔法，将读者带往时间研究的最前线。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 58 《记忆像铁轨一样长》 余光中\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 大诗人余光中的《记忆像铁轨一样长》，文字优美卓绝，感情丰富多姿。写香港的山水，呈现家国的情怀，每一篇章，每一段落，都令人感受无穷的欣悦。余光中的作品，是中国现代文学的不朽风范。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 59 《站在时代的转捩点上》 沈清松\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 现今时代的政治、经济、科技各方面都不断改变和发展，令年青人的看法和理想与昔日的有所不同。有鉴于此，作者写成本书，鼓励及启发这一代的年青人要掌握这 个时代，充实自己，奋发向上；为自己的前途订下正确的方向，从而使理想得以实现。这本书虽然是根据台湾的情况来写，但亦适用于香港现时的环境。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 60 《站在美妙新世纪的门槛上：陈方正论文自选集1984-2000 》陈方正\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 物理学家的训练，对国学及西方历史知识的素养，令作者对中国的现代化及文化、科学发展等问题见解独到。文集共50篇，反思传统，置疑人类发展目标；无论科 学评介抑或人物回顾，都包涵着不露声色的文化探讨。作者对文章结构，遗词造句，一丝不苟，几乎每一篇都是文章及论文写作的典范。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 61 《犁耙集》 农妇\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 《犁耙集》收录农妇由1970到1981年间写作的散文，内容包括身边琐事，她的家庭、朋友、海外生活以至家国情怀等。作者的文笔平易近人，全没说教的意 味，就像坐在你身旁跟你聊天一样。作者经历过战乱苦难，最大的期望是下一代能为她完成自己做不来的事，或看到她看不到的事。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 62 《健康忠告》 洪昭光\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 看一遍《健康忠告》这本小书的代价仅仅是九元八角加两个多小时，然而得到的却是健康的观念和知识，终生受用无穷。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 63 《从一到无穷大》 G·盖莫夫\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 本书的内容包括数学、物理、化学、生物、天文学等。作者从数论开始，带出所谓三度和四度空间的问题，并简略地介绍了爱因斯坦的《相对论》，接着介绍微观世 界如原子、细胞等，最后以天体和宇宙的来源来作结。本书有一套科学发展的整体概念，不但令读者对每个学科有一定程度的认识，并且能够激发他们对科学的思 考。虽然科学发展一日千里，本书也早于1964年写成，距今已久，但由于它主要是介绍普及的科学问题，这些基本知识是不会变更的，即使有变，也只是让我们 有更深入的了解，能带动我们去思考，所以至今仍很有阅读价值。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 64 《带一本书去巴黎》 林达\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 作者学建筑艺术出身，却对西方近代文明的发展与形成了解透彻，带领读者从巴黎的一座座建筑走进法国大革命百年史，以平静的语调，深邃的智慧，讲述血语腥风的历史事件，展示人类认知的艰难过程，荒诞背后的逻辑。对中国文化大革命有了解的读者，处处可见作者巧妙的用心。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 65 《陈之藩文集》 陈之藩\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 这本书收录了《剑河倒影》、《在春风》、《旅美小简》、《一星如月》等散文集，内容主要是作者的个人经验，比方在外国的生活，以及一些生活的联想。虽然全都是身边的事，但他想到很远很阔，看他的散文可以学到写文章的技巧、做人处事应有的态度等。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 66 《梵高传》 欧文·斯通\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 本书趣味性十分高，是欧文·斯通的成名作。其内容是关于大画家梵高离奇颠倒而成功的故事。作者描写梵高短短一生中的奋斗事迹，包括恋爱、友情，以及献身艺 术的无比勇气，令读者对他产生无限同情。作者写本书之前，用了十分科学的方法去研究梵高的资料，例如参考梵高给弟弟提奥的书信，到梵高生活过的地方亲身体 验，对与梵高有关的人也都一一调查了解，可说做足准备工夫。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 67 《从活字版到万维网 》 张信刚\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 张信刚教授将其近三十年来发表的文章和讲词收集成书，分文化、社会和教育三部分。作者通过流畅而隽永的文字，带领读者漫游人类文化大观园。无论是有关古今中外的文学创作或科学发明，张教授都能娓娓道来，引人入胜。读本书既是一种享受和乐趣，又可增进知识、扩宽视野。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 68 《从牛顿定律到爱因斯坦相对论》 方励之、褚耀泉\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 香港的一般科学书籍大都比较着重实用性，甚少接触科学知识以及科学为我们揭示的新世界。《相对论》是近代物理学重要的基础理论，也是很多人感兴趣的理论。 本书的页数虽然不多，但很扼要和有系统地介绍了物理学对时空观念的演变，以及《相对论》在物理学，特别是天文上的应用。所以这本书很适合对这方面有兴趣而 非主修自然科学的人阅读。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 69 《规范与对称之美——杨振宁传》 江才健\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 杨振宁教授的《规范与对称之美》一书，可说是近代中国传记文学中写得最好的一本。作者以客观平实的手法描述杨教授的生平，得力于他十年的努力，访问了杨教 授本人和他的朋友，对杨教授的家庭亦有相当的认识，作者看到造就杨教授伟大贡献的种种背景，也看到杨教授有血有肉的一面。从杨教授跟父母、老师、朋友和后 辈的交往，看到他的中国儒家传统，也看到他浓厚的西方思想。杨教授在科学界的伟大贡献, 使中国人改变了自卑的心理，年青读者应当一看本书。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 70 《围城》 钱锺书\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 本书最独特的地方是作者对人物的描写很细腻贴切，并且全都以讽刺和幽默的手法来处理，非常尖锐和独特。此外，作者对一些情节的描写非常生动、戏剧化，他一方面描述表面发生的事情，同时又带读者了解每一个人物的心理状态，让读者看到人物的多面，有置身其中的感觉, 可读性很高。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 71 《乡土中国》 费孝通\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 这本社会学经典作品，是一本未经介绍大家未必会阅读的书，希望经过介绍之后，大家就会喜欢。作者在书中提出不同的民族之间有许多不同之处，包括生理、心理 以及文化思想。换言之，在不同的民族中，可能有不同的生活和思维方式方式、是非、概念，需要作出比较。本书非常难得，它在一九四七年出版，却能够引用外国 的观点而又并非纯粹用这种观点来看中国，并且看到中国人跟外国人的观点不同之处。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 72 《傅雷家书》 傅雷\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 本书是着名翻译家傅雷写给儿子傅聪的书信辑录。虽然书中不少内容和音乐有关，若单从艺术的观点来说，代入任何一种艺术，同样行得通。书中书信言词亲切坦诚，作者对儿子的劝喻提点、对人对事的看法，不论大道理小事情，都很值得年青读者思考。作者文笔简洁流畅, 可读性十分高。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 73 《为什么我不敢告诉你我是谁》 包约翰\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 本书以问题作为书名，很有吸引力。作者能够深入浅出，帮助读者去分析和了解现代人，尤其是青少年那种自我怀疑和自我收藏的原因。在今天这种经济挂帅及高度 分工的社会中，人与人之间的关系有愈来愈澹薄和疏离的趋向。书中的一句话：“我不敢告诉你我是谁，因为你未必喜欢这个我，而其实这个我就是我的一切。”指 出为何现代人会自我隐藏和封闭，而不能够真情流露。这本书帮助读者懂得如何学习面对和认识自己，如何自我坦白和与人坦诚相处。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 74 《富饶的贫困》 王小强、白南风\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 本书的内容非常充实，能够带给读者一些其它同类书本无法给予的东西，也使读者对中国有较全面的认识。一般人只熟悉中国沿海省份的情况，对于内陆实际生活状 况的认识很少。本书提供的资料和分析，对于想全面了解中国经济发展情况的读者来说，会很有参考价值。虽然书中罗列了许多数据和许多陌生的社会学名词，但只 要大家怀着一股热切了解和全面认识中国的心情去阅读，就不会觉得太艰涩了。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 75 《干校六记》 杨绛\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 推介本书的原因有三：第一，这本书是现代写得比较好的散文，即好的中文；第二，它是杨绛记述她下放干校劳改的情形，内容多为生活小事，反映了某个历史转折 时期的小人物及其心态；第三，它有较高层次的视野，就是我们中国人的美学观点，所谓“哀而不伤”，从中亦可瞥见文学家宽阔的胸襟和敏感的笔触。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 76 《倾城之恋》 张爱玲\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 读张爱玲的《倾城之恋》最大益处在于一读三补，补什么？补人情世故之深；补中国人讲述中国故事的手段和方法之课；补这时代匮乏的冷静、细腻和苍凉的文学精神。读《倾城之恋》可说是品嗜人生的五味汤了，必将是“大补”。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 77 《万历十五年》 黄仁宇\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 历史不易写，中国历史尤其不易写。黄仁宇的《万历十五年》，以独特的手法，从尊权黑暗明朝的统治阶层中取了一个横切面，再把它放在他的“显微镜”式的史笔下，写出了中国传统政治文化中，表面冠冕堂皇，而骨子里却是腐朽衰败的反差。全书末段尤其发人深省，足以为后人鉴。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 78 《与法有缘》（The Seventh Child and The Law） 余叔韶\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 本书作者余叔韶，是大律师行业中的传奇人物。1950年取得大律师资格，曾是港首位华人检察官，后转私人执业。处理官司无数，在当时社会，名气极高。但他 对御用大律师的名衔却极为澹薄，认为是不必要的虚荣。又拒不接受委任为大法官。余叔韶执业逾三十年，退休后执笔撰写回忆录，为本港法律界之第一人。作者以 生动流畅，风趣幽默的文笔撰述，使人读来趣味盎然。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 79 《谁搬走了我的奶酪？》（Who Moved My Cheese?） Spencer Johnson\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 此书讲述两只老鼠和小矮人哈哈在重新找寻「奶酪」的过程中，学会面对转变、观察转变、接受转变、适应转变，以及最重要的：享受转变。作者让读者明白只有勇于面对环境改变，而非沉溺过去美好时光才是走出人生迷宫的妙方。本书确实能启发我们如何面对当前经济困局，走出逆境。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 80 《蒋廷黻回忆录》 蒋廷黻\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 作者为近代史家，在南开清华任教多年，三十年代投笔从戎，进入行改院及国民政府外交部工作，其后代表台湾国府出席驻美大使、联合国代表等要职。作者出生湖 南，最初在乡间接受传统教育。民初留学美国，20年代才返国工作。因为作者修习史学回忆录中对乡间的风土习惯，当时教会学校在湖南的情况，及后在美国留学 时的各各方面都有详尽们记述。本书可以透过作者记忆，活生生地重温几十年的近代史。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 81 《激发心灵潜力》Unlimited Power） 安东尼·罗宾\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 这是一本教人积极、乐观、进取的书，值得我们学习，并有助加深读者对思维的了解。文中有许多生活经验的反省，譬如一天中顺利地完成了99件事情，却有一件 不如意事，这事便足够令人沮丧到不得了。究其原因，只因我们太看重那件不如意事而忽略了其它顺利的99件事。可见世事好坏如莎士比亚所说的全看你怎么想。 书中亦有许多诗人金句，有助激励读者勇敢面对生活挑战。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 82 《禅学的黄金时代》 吴经熊\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 认识禅是相当困难的，推介本书是因为它有两个好处。第一，作者先展示历史事实，由达摩禅师来中国开始，一直写到明代各禅师的经历，让读者易于掌握和理解； 第二，作者并非以说教或讲道的形式来介绍禅，而是纯粹描述十多位禅师的日常生活及与门徒的对话，澹澹地将禅牵引出来，藉此令读者从中开悟禅道。作者透过讲 故事、讲公桉，以及讲禅师生活的方式来介绍禅，读者会较易悟出其中道理。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 83 《鸿》（Wild Swans） 张戎\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 本书讲述作者张戎、她的母亲与外祖母三个女人的故事，除了富于自传性外，还反映了现代中国历史。这部小说情节非常丰富，处处触动人心。既反映了人的勇敢、仁爱的行为，亦反映了时代的悲剧性、残暴及不公平的一面。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 84 《戏曲故事》 张晓风\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 如果年青读者直接阅读中国古代戏剧的原版本，会有许多困难，甚至会因此而对这些作品完全失去兴趣。但作者张晓风用白话短篇小说的体裁，将古代戏曲故事改 写，编成本书。内容以元代杂剧为主，选用的剧本横跨约九百年，较能引发年青读者进一步对中国文学产生兴趣。此外，伟大的文学作品通常都有一些永恒的因素和 价值观念存在，这些东西不会因朝代递嬗而改变。由于作者采用了现代手法和迁就现代读者的口味，把上述这些价值重新带了出来。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 85 《艰难的日出: 中国现代教育的20世纪》 杨东平 &nbsp;\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 全书三百多页，涵盖了一个世纪中国教育制度的变迁，从洋务教育的萌发至中国政府及知识份子推行的教育改革，尝试及失败。对49年以后模仿苏联至文革的震 荡，一直到今天中国大陆最突显的教育公平及品质问题，剖析深刻，行文简洁。这部教育史提供了对中国近百年政治、社会史解读的一条途径。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 86 《魔戒前传：哈比人历险记》（The Hobbit） J.R.R. Tolkien\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; 《魔戒前传》是一个充满传奇、刺激的故事。它勾画出一个令读者向往的世界，述说了比尔博如何和矮人群在一个变幻无常、善恶频争的地方去寻找由一条恶龙护守 的宝物。他们虽然历尽千辛万苦，但也结交了不少朋友，欣赏了很多佳肴雅乐。凭着比尔博的机智因缘，这支矮人国的探险队最后战胜群魔，而得宝致富。比尔博也 因此在哈比区一举成名，这是他从没梦想过的，因他是在一个好逸恶劳、饱食终日、平澹无涛的环境下长大的。也正是这个原因，他为读者们树立了一个很好的榜 样，只要下定决心去做，世界上没有不可能的事的，而且更有意义的生活正等待着读者们。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>','','','1','12','blog','66','0','0','n','n','n','y','y','','','0');
INSERT INTO emlog_blog VALUES('21','原北京大学校长王恩哥的十句话','1438686732','<p>\r\n	<strong>第一句</strong> \r\n</p>\r\n<p>\r\n	------------------------\r\n</p>\r\n<p>\r\n	结交“两个朋友”：\r\n</p>\r\n<p>\r\n	一个是运动场，一个是图书馆\r\n</p>\r\n<p>\r\n	不断地“充电”、“蓄电”、“放电”\r\n</p>\r\n<p>\r\n	<img src=\"http://photocdn.sohu.com/20150418/mp11373226_1429319062622_3.png\" /> \r\n</p>\r\n<p>\r\n	<strong>第二句</strong> \r\n</p>\r\n<p>\r\n	------------------------\r\n</p>\r\n<p>\r\n	培养“两种功夫”：\r\n</p>\r\n<p>\r\n	一个是本分，一个是本事\r\n</p>\r\n<p>\r\n	做人靠本分，做事靠本事，靠“两本”起家靠得住\r\n</p>\r\n<p>\r\n	<strong>第三句</strong> \r\n</p>\r\n<p>\r\n	------------------------\r\n</p>\r\n<p>\r\n	乐于吃“两样东西”：\r\n</p>\r\n<p>\r\n	一个是吃亏，一个是吃苦\r\n</p>\r\n<p>\r\n	做人不怕吃亏，\r\n</p>\r\n<p>\r\n	做事不怕吃苦\r\n</p>\r\n<p>\r\n	吃亏是福，吃苦是福\r\n</p>\r\n<p>\r\n	<img src=\"http://photocdn.sohu.com/20150418/mp11373226_1429319062622_4.png\" /> \r\n</p>\r\n<p>\r\n	<strong>第四句</strong> \r\n</p>\r\n<p>\r\n	------------------------\r\n</p>\r\n<p>\r\n	具备“两种力量”：\r\n</p>\r\n<p>\r\n	一种是思想的力量，一种是利剑的力量\r\n</p>\r\n<p>\r\n	思想的力量往往战胜利剑的力量\r\n</p>\r\n<p>\r\n	这是拿破仑的名言\r\n</p>\r\n<p>\r\n	一个人的思想走多远，他就有可能走多远\r\n</p>\r\n<p>\r\n	<strong>第五句</strong> \r\n</p>\r\n<p>\r\n	------------------------\r\n</p>\r\n<p>\r\n	追求“两个一致”：\r\n</p>\r\n<p>\r\n	一个是兴趣与事业一致，一个是爱情与婚姻一致\r\n</p>\r\n<p>\r\n	兴趣与事业一致，\r\n</p>\r\n<p>\r\n	就能使你的潜力最大限度地得以发挥\r\n</p>\r\n<p>\r\n	恩格斯说，婚姻要以爱情为基础\r\n</p>\r\n<p>\r\n	没有爱情的婚姻是不道德的婚姻\r\n</p>\r\n<p>\r\n	也不会是牢固的婚姻\r\n</p>\r\n<p>\r\n	<strong>第六句</strong> \r\n</p>\r\n<p>\r\n	------------------------\r\n</p>\r\n<p>\r\n	插上“两个翅膀”：\r\n</p>\r\n<p>\r\n	一个叫理想，一个叫毅力\r\n</p>\r\n<p>\r\n	如果一个人有了这“两个翅膀”，他就能飞得高，飞得远\r\n</p>\r\n<p>\r\n	<img src=\"http://photocdn.sohu.com/20150418/mp11373226_1429319062622_5.png\" /> \r\n</p>\r\n<p>\r\n	<strong>第七句</strong> \r\n</p>\r\n<p>\r\n	------------------------\r\n</p>\r\n<p>\r\n	构建“两个支柱”：\r\n</p>\r\n<p>\r\n	一个是科学，一个是人文\r\n</p>\r\n<p>\r\n	<strong>第八句</strong> \r\n</p>\r\n<p>\r\n	------------------------\r\n</p>\r\n<p>\r\n	配备两个“保健医生”：\r\n</p>\r\n<p>\r\n	一个叫运动，一个叫乐观\r\n</p>\r\n<p>\r\n	运动使你生理健康，乐观使你心理健康\r\n</p>\r\n<p>\r\n	日行万步路，夜读十页书。\r\n</p>\r\n<p>\r\n	<strong>第九句</strong> \r\n</p>\r\n<p>\r\n	------------------------\r\n</p>\r\n<p>\r\n	记住“两个秘诀”：\r\n</p>\r\n<p>\r\n	健康的秘诀在早上，成功的秘诀在晚上\r\n</p>\r\n<p>\r\n	爱因斯坦说过：人的差异产生于业余时间\r\n</p>\r\n<p>\r\n	业余时间能成就一个人，也能毁灭一个人\r\n</p>\r\n<p>\r\n	<img src=\"http://photocdn.sohu.com/20150418/mp11373226_1429319062622_6.png\" /> \r\n</p>\r\n<p>\r\n	<strong>第十句</strong> \r\n</p>\r\n<p>\r\n	------------------------\r\n</p>\r\n<p>\r\n	追求“两个极致”：\r\n</p>\r\n<p>\r\n	一个是把自身的潜力发挥到极致\r\n</p>\r\n<p>\r\n	一个是把自己的寿命健康延长到极致\r\n</p>','','','1','11','blog','58','0','0','n','n','n','y','y','','','0');
INSERT INTO emlog_blog VALUES('22','为学者，切忌急进','1438694759','<p>\r\n	这些天，总在寻思着很多事情，又是学英语，又是学JS，又想学摄影，又是学日语，还要想按照七年之约书单多看些书，还有一大堆的计算机新技术想去涉猎学习……我也是醉了。事多了就容易产生浮躁心理，这个没学多久就去学那个，每样东西都是浅尝辄止，到头来其实啥也没学到，这是为学者最忌讳的一件事情。其实读了这么多年书，我也应该清楚地知道，无论是看一本书还是学一门技术，最重要在于专注，应该持之以恒的去做，全身心的投入去做，而不是将精力分散于众多事情当中，这样收效甚微，很难真正学到东西。想学是件好事，但是不能盲目的学，凡所学必先分个轻重缓急，因此针对自己想去学的东西，我需要做一个分析，看看什么是最紧迫的？什么是最喜欢的?什么是最受益的?然后根据分析结果做好相应的时间安排。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<ul>\r\n	<li>\r\n		最紧迫的：学英语咯，开学马上就要进行研究生英语入学考试，这是毋庸置疑的（每天上午学英语，主要是研究6套真题，其余闲暇时间可以使用手机软件乐词背单词）\r\n	</li>\r\n	<li>\r\n		最喜欢的：摄影（已入手NikonD7000，一定要好好学习喔，每天学点）、多看些书（本周推荐《论语》）、涉猎计算机新技术（主要是一些比较前沿的技术涉猎，增广见闻）、打理网站（凡做事皆贵在坚持，希望自己能坚持下去）\r\n	</li>\r\n	<li>\r\n		最受用的：日语（说来惭愧，资料早在大一就买了，大学都毕业了居然还是学了个五十音图，看来还得加把劲了）\r\n	</li>\r\n</ul>\r\n<p>\r\n	<br />\r\n</p>\r\n<p align=\"center\">\r\n	<a target=\"_blank\" href=\"http://www.mryu.top/content/uploadfile/201508/a3531438696955.jpg\" id=\"ematt:18\"><img src=\"http://www.mryu.top/content/uploadfile/201508/thum-a3531438696955.jpg\" title=\"点击查看原图\" alt=\"学习金字塔.jpg\" height=\"251\" border=\"0\" width=\"420\" /></a> \r\n</p>\r\n<p align=\"left\">\r\n	最后附一张学习金字塔图，没事多看看，受益颇多。最重要的是要区分被动学习与主动学习，多让自己主动去学习，提高学习内容留存率，达到事半功倍的效果~\r\n</p>','','','1','7','blog','99','2','1','n','n','n','y','y','','','0');
INSERT INTO emlog_blog VALUES('23','假如生活欺骗了你','1438826202','<p align=\"center\">\r\n	<a target=\"_blank\" href=\"http://www.mryu.top/content/uploadfile/201508/84aa1438826320.jpg\" id=\"ematt:20\"><img src=\"http://www.mryu.top/content/uploadfile/201508/84aa1438826320.jpg\" title=\"点击查看原图\" alt=\"u=2244564734,50411888&amp;fm=21&amp;gp=0.jpg\" height=\"220\" border=\"0\" width=\"329\" /></a>\r\n</p>\r\n<p align=\"center\">\r\n	<br />\r\n</p>\r\n<p align=\"center\">\r\n	假如生活欺骗了你\r\n</p>\r\n<p align=\"center\">\r\n	文/普希金\r\n</p>\r\n<p align=\"center\">\r\n	<br />\r\n</p>\r\n<p align=\"center\">\r\n	<br />\r\n</p>\r\n<p align=\"center\">\r\n	假如生活欺骗了你，\r\n</p>\r\n<p align=\"center\">\r\n	不要悲伤，\r\n</p>\r\n<p align=\"center\">\r\n	不要心急！\r\n</p>\r\n<p align=\"center\">\r\n	忧郁的日子里须要镇静：\r\n</p>\r\n<p align=\"center\">\r\n	相信吧，\r\n</p>\r\n<p align=\"center\">\r\n	快乐的日子将会来临！\r\n</p>\r\n<p align=\"center\">\r\n	心儿永远向往着未来；\r\n</p>\r\n<p align=\"center\">\r\n	现在却常是忧郁。\r\n</p>\r\n<p align=\"center\">\r\n	一切都是瞬息，\r\n</p>\r\n<p align=\"center\">\r\n	一切都将会过去；\r\n</p>\r\n<p align=\"center\">\r\n	而那过去了的，\r\n</p>\r\n<p align=\"center\">\r\n	就会成为亲切的怀恋。\r\n</p>\r\n<p align=\"center\">\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>','','','1','8','blog','41','0','1','n','n','n','y','y','','','0');
INSERT INTO emlog_blog VALUES('24','妒忌是如何毁掉你的幸福的','1438827988','<p>\r\n	文 | 廖玮雯\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	听过这样一件事儿，某次聚会，一个朋友抱怨自己其实是个穷鬼。然而真相却是，他刚刚购置了一套豪华别墅，买了一辆新车，新婚燕尔，喜迁新居。\r\n</p>\r\n<p>\r\n	他说，他是一个可怜的穷鬼。\r\n</p>\r\n<p>\r\n	在场的单身汪和租房客还有车奴房奴们都已经磨刀霍霍向着他了好么。\r\n</p>\r\n<p>\r\n	他接着解释，他所买的那个别墅小区里边，左邻右舍动辄全是百万豪车，他的几十万的车子根本就不入流，就拿装修来说，别人院子里的一棵树就够他装修一个院子了……\r\n</p>\r\n<p>\r\n	总而言之，住在那儿，他就是处处不如人，媳妇儿一唠叨，这个原本意气风发，斗志昂扬的年轻人就像一只斗败的公鸡一般，垂头丧气，自叹弗如。\r\n</p>\r\n<p>\r\n	我背负了那么大的压力，付出了那么多的努力，才换来了今天的生活，而我身边的邻居却开着豪车，住着真正的豪宅，日子过得无比舒服与轻松。你说，这公平么？\r\n</p>\r\n<p>\r\n	我想，这位朋友还没在自己的新车豪宅里享受几天好日子呢，怎么变得比以前租着房子，为了美好的未来（也就是他的现在）而拼命的时候还要痛苦呢？\r\n</p>\r\n<p>\r\n	这里我们除了可以看到人的欲壑难填之外，我看到的更多是妒忌。\r\n</p>\r\n<p>\r\n	这位朋友没有对富可敌国的沙特王子嫉妒有加，却对院子里的一棵树就比他的整个院子值钱的邻居有点儿咬牙切齿。\r\n</p>\r\n<p>\r\n	富人之间如此，穷人之间也是一样，一个街头乞丐一般不会嫉妒百万富翁，但是他会对他身边那个讨钱每每比他多的另一个乞丐感到非常嫉恨。\r\n</p>\r\n<p>\r\n	孩子之间也会存在妒忌，如果一群孩子之中有个孩子总是受到重视和关爱的话，身边的其他孩子难免产生妒忌的情绪，接下来这个孩子容易受到其他孩子的排挤和孤立。\r\n</p>\r\n<p>\r\n	重要的问题在于，得到更多“好处”的孩子错了么？\r\n</p>\r\n<p>\r\n	他没有。因为他也没搞清楚自己究竟是为什么得到了这些关爱与重视。\r\n</p>\r\n<p>\r\n	所以，即便看起来，这种做法对于其他孩子而言并不公平，但是这个孩子是无辜的。\r\n</p>\r\n<p>\r\n	有人会说，不对，妒忌其实可以带来公平，而我认为，妒忌在绝大多数的时候，都会带来无尽的痛苦。带来公平的不是嫉妒，而是理性与爱。\r\n</p>\r\n<p>\r\n	举个例子；\r\n</p>\r\n<p>\r\n	一，如果你的工资加了500，你会不会感到非常高兴？\r\n</p>\r\n<p>\r\n	答：当然。\r\n</p>\r\n<p>\r\n	二，如果你的工资加了500，但是，其他员工都加1000，你还会不会感到高兴？\r\n</p>\r\n<p>\r\n	答：这不公平。\r\n</p>\r\n<p>\r\n	三，如果你的工资加了500，但是，公司里几个你觉得业务能力，为人处世都远不如你的人，他们加了2000，你还会不会感到有那么一丁点儿高兴？\r\n</p>\r\n<p>\r\n	答：凭什么？那些笨蛋？我哪儿不如他们啦？\r\n</p>\r\n<p>\r\n	你有没有发现，产生对比之后，让人产生一种“不患寡而患不均”的痛苦。理性来看，只要存在加500这个事实，你的工资就已有所增长，无论如何，你应该感到高兴才对？至少汽车可以多加一次油啊。\r\n</p>\r\n<p>\r\n	事实显然并非如此。\r\n</p>\r\n<p>\r\n	一般而言，如果你能保持理性的话，你会寻找薪资部门求取答案。如若他们不能给你一个合理的解释的话，你很容易会去嫉恨那几个不如你的人。\r\n</p>\r\n<p>\r\n	你额外得到500块这个原本可以让你高兴的事实，被彻底地毁掉了，你陷入了嫉恨之中，甚至可能意图报复。但是，那些拿到更多钱的人，并没有直接对你进行剥夺和伤害，从某个角度而言，他们和上文无辜的孩子其实是一样的。\r\n</p>\r\n<p>\r\n	然而，嫉妒会蒙蔽人的双眼。\r\n</p>\r\n<p>\r\n	古往今来，这种事儿屡见不鲜，陈胜吴广当年大呼“王侯将相宁有种乎”，就是一种极端的嫉妒，意思就是，你们这些人凭什么过得比我好啊？\r\n</p>\r\n<p>\r\n	这种对于王侯将相巨大的仇恨，将会带来毁灭，这种革命看起来轰轰烈烈，可是并不能解决问题，他们败了，百姓倒霉，他们胜了，他们会成为新的“王侯将相”，然后等待未来的“陈胜吴广”降临，百姓还是倒霉。\r\n</p>\r\n<p>\r\n	无论成败与否，这种因为仇恨所引发的战争都会给人类带来巨大的灾难，而且这种因为彼此嫉恨带来的反抗与镇压将永无休止。\r\n</p>\r\n<p>\r\n	有人会问，难道我们要任人欺压么？\r\n</p>\r\n<p>\r\n	不是！\r\n</p>\r\n<p>\r\n	我所说的理性，指的是人们要保持清醒地去寻求造成这种不公平的根源，然后通过理性的光辉与人性中的美好的一面去改变不公平的处境，而不是去毁灭你所嫉恨的人，然后再让自己再成为和他一样的人。\r\n</p>\r\n<p>\r\n	圣雄甘地在英属殖民区所倡导的非暴力抵抗就是如此，他并不嫉妒殖民者可以享有特权作威作福，他的矛头也并没有对准殖民者本身，他并不希望通过组织反抗，消灭殖民者，然后取代他们。\r\n</p>\r\n<p>\r\n	他只是在谋求人生而为人的基本权利，只有这样，才能摆脱“王侯将相宁有种乎”的死循环，让所有人获得一个相对公平的生存环境与社会空间。\r\n</p>\r\n<p>\r\n	比如我们可以尽量对每个孩子都尽量公正些，平等些，而不是排挤和孤立那个倒霉孩子，或者设立一个不会莫名其妙地只给你500，给别人2000的薪资制度等。\r\n</p>\r\n<p>\r\n	我们回到现实生活，有个新闻，一个中年妇女仅仅因为自己的孩子身体虚弱，邻居的孩子活蹦乱跳便将邻居的孩子残忍地杀死，这让人更为深切地理解极端妒忌这种情绪对人对己的一种毁灭。\r\n</p>\r\n<p>\r\n	在我的后台有过几条留言，说的是自己的好朋友很多方面都比自己优秀而让自己忿忿不平，尤其是当自己认为自己付出了巨大的努力却依然技不如人时所带来的痛苦简直让人寝食难安，包括上面提到的那个朋友也曾有过翻越围墙到邻居的院子里砍了那棵树的冲动……\r\n</p>\r\n<p>\r\n	关于嫉妒心如何克制，我想提到的是另一个词——羡慕，我们常常羡慕一个人的智慧与学识，羡慕一个人过人的技艺与出众的才华，我们对此发出由衷的赞叹，但是从来没有想过对他进行恶意中伤甚至进行毁灭，尤其是对在某个领域取得伟大成就的杰出人物。\r\n</p>\r\n<p>\r\n	我们会去感慨他们杰出的成就和伟大的思想，甚至意图斗胆追随他们曾走过的步伐，期待自己也可以对他们所抵达的境界有所接近，是的，对智慧的赞美和对善的赞颂会让我们远离妒忌之心，日趋完满。\r\n</p>\r\n<p>\r\n	有人会说，别扯那么远的，你不嫉妒“圣人”那是因为你对他们的成就难以望其项背而遥不可及。\r\n</p>\r\n<p>\r\n	你就说说，我有一个一棵树就比我的整个院子还贵的邻居，或者有一个蹲在我旁边个每次讨钱都比我多出一倍的破乞丐，还有我的那个整天瞎晃随随便便轻轻松松一考成绩就是优秀，而我每天头悬梁锥刺股也远远比不上他的的同桌，你说我该怎么办？\r\n</p>\r\n<p>\r\n	说到这里，我不得不提到我遇见的一个那慈眉善目与世无争的老奶奶，我几乎从未见过她动过脾气，与谁起过争执，她的房子她的田地从来不是村里最好的，子女也不是所谓最能赚钱最有出息的，但是，她就是乐呵呵地与人为善，不与人争长短，不扎堆论是非，我觉得她简直就是一个完全“活在当下”的哲学家。\r\n</p>\r\n<p>\r\n	她常和我说：现在的生活可比以前好太多啦，我就每天早睡早起，保证精神饱满，吃好喝好，身体倍儿好，看看电视，溜溜狗，儿孙子女自有福分也不必我去操心，所以，我每天过得特别开心。\r\n</p>\r\n<p>\r\n	老天，还有什么比每天过得快乐更让人羡慕嫉妒恨的么？\r\n</p>\r\n<p>\r\n	我问那位朋友：如果你的邻居也有邻居，而他家一个马桶盖子就可以买十几棵那种树，那么，他的日子该怎么过下去呢？\r\n</p>\r\n<p>\r\n	朋友若有所思；我觉着虽然你没我有钱，可是你这样每天乐呵呵的洒脱劲儿可着实让人羡慕。\r\n</p>\r\n<p>\r\n	值得庆幸的是，他说的是羡慕而不是嫉妒。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>','','','1','11','blog','48','0','0','n','n','n','y','y','','','0');
INSERT INTO emlog_blog VALUES('15','关于新叶子我想说些话','1438488592','<p align=\"left\">\r\n	最开始学做建立个人网站的时候是2011年，那时的我还是大一，就读于物理系。凭借着对计算机的爱好，便在主机屋买了域名(<a href=\"http://www.yuwenyan,net\">www.yuwenyan,net</a>)和虚拟主机，采用框架搭建了我人生中的第一个个人网站---真实的自己。依稀记得，搭建网站成功的那一刻，自己心中的那种难以言说的喜悦，而后的几年里我也一直在用心的维护着这个网站，数数日子也快四年了。\r\n</p>\r\n<p align=\"left\">\r\n	去年下半年一直在忙着考研以及毕设的问题，很少去打点网站。直到前些日子，才发现域名已过期，当我去主机屋续费的时候竟然不能续费，貌似因为被人注册了而无法续费。当时我就有点生气，前几年域名到期主机屋客服都会发短信或发邮件通知的，今年居然一声不吭。不过话又说回来，这也怪不得人家，可能是主机屋系统升级出了点问题吧，要是我经常打理的话也不会出现这样的问题。\r\n</p>\r\n<p align=\"left\">\r\n	用了三年的域名就这样说没就没了，心里挺郁闷的，那也没其他招了。于是就寻思着再注册一个，也就是现在的这个。由于之前的网站后台绑定域名了，当我用在现在的域名解析是，问题出现了，无法登陆后台，问题解决的办法可以去改源文件，可是我自己闲麻烦了，就想换个框架试试，便想到了这个博客程序。其实早在前些年我就有接触过这个开源内容管理系统，看到它的代码比较清晰明了易于学习，就萌发了读它的主意，想通过学习它来提高自己的建站的功力，但是由于学业的问题一直苦于没有时间，今日终于得以如愿以偿。我想我应该利用大学毕业与研究生开学中间的这点空隙来好好的研究下它。通过这次的教训，我完全放弃了主机屋提供的虚拟主机服务和域名服务改用了无忧主机。\r\n</p>\r\n<p align=\"left\">\r\n	<span style=\"line-height:2;font-family:Arial Black;\"><span style=\"line-height:1.5;font-family:FangSong_GB2312;\">&nbsp;&nbsp;&nbsp; </span><br />\r\n</span>\r\n</p>\r\n<p align=\"left\">\r\n	<br />\r\n<span style=\"line-height:2;font-family:FangSong_GB2312;\">&nbsp;&nbsp;</span> \r\n</p>\r\n<p align=\"left\">\r\n	<br />\r\n</p>','','','1','7','blog','311','0','0','n','n','n','y','y','','','0');
INSERT INTO emlog_blog VALUES('19','七年之约读书养成计划','1438667493','<p>\n	&nbsp;&nbsp;&nbsp;&nbsp;古人常说：万般皆下品，惟有读书高，读书那些好事。一直一来是我所喜好做的事情。一杯茶、一书桌、一本书足矣！以前读书比较散漫，没有什么规划，喜欢读什么书就读什么书，读完了就扔在一边不管了，也不做什么读书笔记，过了几年全然不记得。这种读书方法尽管满足了当时的读书感觉却没能让自己真正的成长起来。子曰：学而时习之，不亦说乎！说的非常的有道理，读过的书，学过的东西要时常拿出来温故，要学会时常的总结，才能汲取到更多的养分。故此列此书单送给即将步入研究生生活的自己，希望自己能针对书单不断提升自我。\n</p>\n<p>\n	&nbsp;&nbsp;&nbsp;&nbsp;由于一时难以列出合适的书目，故7年之约书单借鉴香港中文大学推荐书单（共86本）作为读书养成计划的首要书目，今后针对不同的兴趣以及专业技能可以自行不断扩充书单。凡所读之书均用绿色标记，且需要单独设立页面作为读书笔记，未读之书均用红色标记。\n</p>\n<p>\n	<br />\n</p>\n<ol>\n	<li>\n		<span style=\"color:#E53333;\"><strong>1 《一百分妈妈》 马以工</strong></span> \n	</li>\n	<li>\n		<span style=\"color:#E53333;\"><strong>2 《一路走来一路读 》 林达</strong></span> \n	</li>\n	<li>\n		<span style=\"color:#E53333;\"><strong>3 《二胡》 陈若曦</strong></span> \n	</li>\n	<li>\n		<span style=\"color:#E53333;\"><strong>4 《人在欧洲》 龙应台</strong></span> \n	</li>\n	<li>\n		<span style=\"color:#009900;\"><strong>5 《十万个为什么》 严济远</strong></span> \n	</li>\n	<li>\n		<span style=\"color:#E53333;\"><strong>6 《三十五年的新闻追踪: 一个日本记者眼中的中国》 吉田实着; 王武云, 朱新建译</strong></span> \n	</li>\n	<li>\n		<span style=\"color:#009900;\"><strong>7 《小王子》 圣修伯理&nbsp;&nbsp;&nbsp;&nbsp;已读 2013<br />\n</strong></span> \n	</li>\n	<li>\n		<span style=\"color:#E53333;\"><strong><span style=\"color:#009900;\">8 《千年一叹》 余秋雨&nbsp;&nbsp;&nbsp;&nbsp;已读 2011</span> <br />\n</strong></span> \n	</li>\n	<li>\n		<span style=\"color:#E53333;\"><strong>9《大汗之国：西方眼中的中国》（The Chan\'s Great Continent） Jonathan D. Spence</strong></span> \n	</li>\n	<li>\n		<span style=\"color:#E53333;\"><strong>10 《大学之理念》 金耀基</strong></span> \n	</li>\n	<li>\n		<span style=\"color:#E53333;\"><strong>11 《大学道上：教育理想与实践的反思》 郭少棠</strong></span> \n	</li>\n	<li>\n		<span style=\"color:#E53333;\"><strong>12 《文华集》 林太乙</strong></span> \n	</li>\n	<li>\n		<span style=\"color:#009900;\"><strong>13 《文化苦旅》 余秋雨&nbsp;&nbsp;&nbsp; 已读 2011<br />\n</strong></span> \n	</li>\n	<li>\n		<span style=\"color:#E53333;\"><strong>14 《少年凯歌（龙血树）》 陈凯歌</strong></span> \n	</li>\n	<li>\n		<span style=\"color:#E53333;\"><strong>15 《中国大历史》(China—A Macro History) 黄仁宇</strong></span> \n	</li>\n	<li>\n		<span style=\"color:#E53333;\"><strong>16 《中国文化要义》 梁漱溟</strong></span> \n	</li>\n	<li>\n		<span style=\"color:#E53333;\"><strong>17 《中国美术史百题》 谭天</strong></span> \n	</li>\n	<li>\n		<span style=\"color:#E53333;\"><strong>18 《分成两半的子爵》（II Visconte Dimezzato） 卡尔维诺</strong></span> \n	</li>\n	<li>\n		<span style=\"color:#E53333;\"><strong>19 《未来城》（A Scientist in The City） 詹姆斯．特菲尔</strong></span> \n	</li>\n	<li>\n		<span style=\"color:#E53333;\"><strong>20 《失窃的未来：生命的隐形浩劫》（Our Stolen Future） Theo Colborn, Dianne Dumanoski, John Peterson Myers</strong></span> \n	</li>\n	<li>\n		<span style=\"color:#E53333;\"><strong>21 《生命的奋进》 梁漱溟、牟宗三、唐君毅、徐复观</strong></span> \n	</li>\n	<li>\n		<span style=\"color:#E53333;\"><strong>22 《失明给我的挑战》 程文辉</strong></span> \n	</li>\n	<li>\n		<span style=\"color:#E53333;\"><strong>23 《生活简单就是享受》（Simplify Your Life） Elaine St. James</strong></span> \n	</li>\n	<li>\n		<span style=\"color:#009900;\"><strong>24 《生命中不能承受之轻》 米兰．昆德拉&nbsp;&nbsp;&nbsp; 已读 2014<br />\n</strong></span> \n	</li>\n	<li>\n		<span style=\"color:#E53333;\"><strong>25 《生命中不可错过的智慧》（All I Really Need to Know I Learned in Kindergarten） 罗勃．傅刚 (Robert Fulghum)</strong></span> \n	</li>\n	<li>\n		<span style=\"color:#E53333;\"><strong>26 《世界著名科学家演说精粹》 朱长超</strong></span> \n	</li>\n	<li>\n		<span style=\"color:#E53333;\"><strong>27 《打倒符碌──做个有料的大学生》 刘绍麟</strong></span> \n	</li>\n	<li>\n		<span style=\"color:#E53333;\"><strong>28 《西潮》 蒋梦麟</strong></span> \n	</li>\n	<li>\n		<span style=\"color:#E53333;\"><strong>29 《共悟人间：父女两地书》 刘再复、刘剑梅</strong></span> \n	</li>\n	<li>\n		<span style=\"color:#E53333;\"><strong>30 《朱元璋传》 吴</strong></span> \n	</li>\n	<li>\n		<span style=\"color:#E53333;\"><strong>31 《成功，你敢吗？》 周融</strong></span> \n	</li>\n	<li>\n		<span style=\"color:#E53333;\"><strong>32 《成长 ——发现最好的自己 》 凌志军</strong></span> \n	</li>\n	<li>\n		<span style=\"color:#E53333;\"><strong>33 《老虎．伍兹传奇》（A Biography of Tiger Woods） 史崔吉</strong></span> \n	</li>\n	<li>\n		<span style=\"color:#E53333;\"><strong>34 《安杰拉的灰烬》（Angela\'s Ashes） Frank McCourt</strong></span> \n	</li>\n	<li>\n		<span style=\"color:#E53333;\"><strong>35 《地图集: 一个想象的城市的考古学》 董启章</strong></span> \n	</li>\n	<li>\n		<span style=\"color:#009900;\"><strong>36 《沈从文自传》 沈从文</strong></span> \n	</li>\n	<li>\n		<span style=\"color:#E53333;\"><strong>37 《我是谢坤山》 谢坤山</strong></span> \n	</li>\n	<li>\n		<span style=\"color:#E53333;\"><strong>38 《我对总理说实话》 李昌平</strong></span> \n	</li>\n	<li>\n		<span style=\"color:#E53333;\"><strong>39 《李光耀回忆录》 李光耀</strong></span> \n	</li>\n	<li>\n		<span style=\"color:#E53333;\"><strong>40 《见证香港五十年》 周永新</strong></span> \n	</li>\n	<li>\n		<span style=\"color:#E53333;\"><strong>41 《余纯顺孤身徒步走西藏》 余纯顺</strong></span> \n	</li>\n	<li>\n		<span style=\"color:#E53333;\"><strong>42 《波动》 赵振开</strong></span> \n	</li>\n	<li>\n		<span style=\"color:#E53333;\"><strong>43 《林语堂传》 林太乙</strong></span> \n	</li>\n	<li>\n		<span style=\"color:#E53333;\"><strong>44 《幸福之路》（The Conquest of Happiness） 罗素 (B. Russell)</strong></span> \n	</li>\n	<li>\n		<span style=\"color:#E53333;\"><strong>45 《物理五千年》 朱恒足</strong></span> \n	</li>\n	<li>\n		<span style=\"color:#E53333;\"><strong>46 《奇的巧克力工厂》（Charlie And The Chocolate Factory） 罗?达尔</strong></span> \n	</li>\n	<li>\n		<span style=\"color:#E53333;\"><strong>47 《阿城小说: 棋王、树王、孩子王》 阿城</strong></span> \n	</li>\n	<li>\n		<span style=\"color:#009900;\"><strong>48 《红楼梦》 曹雪芹&nbsp;&nbsp;&nbsp; 已读 2009<br />\n</strong></span> \n	</li>\n	<li>\n		<span style=\"color:#E53333;\"><strong>49 《胡雪岩》《红顶商人》《灯火楼台》高阳</strong></span> \n	</li>\n	<li>\n		<span style=\"color:#E53333;\"><strong>50 《城市心灵》 郭少棠</strong></span> \n	</li>\n	<li>\n		<span style=\"color:#E53333;\"><strong>51 《革命之子》 梁恒、夏竹丽</strong></span> \n	</li>\n	<li>\n		<span style=\"color:#E53333;\"><strong>52 《美丽新世界》 赫胥黎</strong></span> \n	</li>\n	<li>\n		<span style=\"color:#E53333;\"><strong>53 《娃娃看天下：玛法达的世界》（共六册） 三毛</strong></span> \n	</li>\n	<li>\n		<span style=\"color:#E53333;\"><strong>54 《科学史上的悬案》 江苏科普创作协会基础科学委员会</strong></span> \n	</li>\n	<li>\n		<span style=\"color:#E53333;\"><strong>55 《思想方法五讲新编》（修订版） 劳思光</strong></span> \n	</li>\n	<li>\n		<span style=\"color:#E53333;\"><strong>56 《美德书 : 伟大励志故事的宝藏》（The Book of Virtues: A Treasury of Great Moral Stories） William J. Bennett</strong></span> \n	</li>\n	<li>\n		<span style=\"color:#009900;\"><strong>57 《时间简史》（A Brief History of Time） Stephen W. Hawking&nbsp;&nbsp;&nbsp; 已读 2009<br />\n</strong></span> \n	</li>\n	<li>\n		<span style=\"color:#E53333;\"><strong>58 《记忆像铁轨一样长》 余光中</strong></span> \n	</li>\n	<li>\n		<span style=\"color:#E53333;\"><strong>59 《站在时代的转折点上》 沈清松</strong></span> \n	</li>\n	<li>\n		<span style=\"color:#E53333;\"><strong>60 《站在美妙新世纪的门坎上:陈方正论文自选集1984-2000 》陈方正</strong></span> \n	</li>\n	<li>\n		<span style=\"color:#E53333;\"><strong>61 《犁耙集》 农妇</strong></span> \n	</li>\n	<li>\n		<span style=\"color:#E53333;\"><strong>62 《健康忠告》 洪昭光</strong></span> \n	</li>\n	<li>\n		<span style=\"color:#009900;\"><strong>63 《从一到无穷大》 G．盖莫夫&nbsp;&nbsp;&nbsp; 已读2012</strong></span> \n	</li>\n	<li>\n		<span style=\"color:#E53333;\"><strong>64 《带一本书去巴黎》 林达</strong></span> \n	</li>\n	<li>\n		<span style=\"color:#E53333;\"><strong>65 《陈之藩文集》 陈之藩</strong></span> \n	</li>\n	<li>\n		<span style=\"color:#E53333;\"><strong>66 《梵高传》 欧文．斯通</strong></span> \n	</li>\n	<li>\n		<span style=\"color:#E53333;\"><strong>67 《从活字版到万维网 》 张信刚</strong></span> \n	</li>\n	<li>\n		<span style=\"color:#E53333;\"><strong>68 《从牛顿定律到爱因斯坦相对论》 方励之、褚耀泉</strong></span> \n	</li>\n	<li>\n		<span style=\"color:#E53333;\"><strong>69 《规范与对称之美－杨振宁传》 江才健</strong></span> \n	</li>\n	<li>\n		<span style=\"color:#009900;\"><strong>70 《围城》 钱钟书&nbsp;&nbsp;&nbsp; 已读 2011</strong></span> \n	</li>\n	<li>\n		<span style=\"color:#E53333;\"><strong>71 《乡土中国》 费孝通</strong></span> \n	</li>\n	<li>\n		<span style=\"color:#E53333;\"><strong>72 《傅雷家书》 傅雷</strong></span> \n	</li>\n	<li>\n		<span style=\"color:#E53333;\"><strong>73 《为什么我不敢告诉你我是谁》 包约翰</strong></span> \n	</li>\n	<li>\n		<span style=\"color:#E53333;\"><strong>74 《富饶的贫困》 王小强、白南风</strong></span> \n	</li>\n	<li>\n		<span style=\"color:#E53333;\"><strong>75 《干校六记》 杨绛</strong></span> \n	</li>\n	<li>\n		<span style=\"color:#E53333;\"><strong>76 《倾城之恋》 张爱玲</strong></span> \n	</li>\n	<li>\n		<span style=\"color:#E53333;\"><strong>77 《万历十五年》 黄仁宇</strong></span> \n	</li>\n	<li>\n		<span style=\"color:#E53333;\"><strong>78 《与法有缘》（The Seventh Child and The Law） 余叔韶</strong></span> \n	</li>\n	<li>\n		<span style=\"color:#E53333;\"><strong>79 《谁搬走了我的奶酪？》（Who Moved My Cheese?） Spencer Johnson</strong></span> \n	</li>\n	<li>\n		<span style=\"color:#E53333;\"><strong>80 《蒋廷黻回忆录》 蒋廷黻</strong></span> \n	</li>\n	<li>\n		<span style=\"color:#E53333;\"><strong>81 《激发心灵潜力》Unlimited Power） 安东尼．罗宾</strong></span> \n	</li>\n	<li>\n		<span style=\"color:#E53333;\"><strong>82 《禅学的黄金时代》 吴经熊</strong></span> \n	</li>\n	<li>\n		<span style=\"color:#E53333;\"><strong>83 《鸿》（Wild Swans） 张戎</strong></span> \n	</li>\n	<li>\n		<span style=\"color:#E53333;\"><strong>84 《戏曲故事》 张晓风</strong></span> \n	</li>\n	<li>\n		<span style=\"color:#E53333;\"><strong>85 《艰难的日出: 中国现代教育的20世纪》 杨东平</strong></span> \n	</li>\n	<li>\n		<span style=\"color:#E53333;\"><strong>86 《魔戒前传：哈比人历险记》（The Hobbit） J.R.R. Tolkien</strong></span> \n	</li>\n</ol>\n<p>\n	<br />\n</p>\n<p>\n	以上86本书是<a target=\"_blank\" href=\"http://www.mryu.top/books/20.html\"><strong><span style=\"color:#337FE5;\">香港中文大学推荐书单</span></strong></a>中书目\n</p>\n<p>\n	<br />\n</p>\n<p>\n	<br />\n</p>','','','1','12','blog','67','0','0','n','n','n','y','y','','','0');
INSERT INTO emlog_blog VALUES('17','世界名列前茅的企业名字怎么来的','1438578567','<p align=\"left\">\r\n	马化腾为什么把公司叫腾讯？马云公司要叫阿里巴巴？这些现在耳熟能详的公司名字，当时创始人为什么会取这个名字呢？就让小编慢慢揭秘吧！\r\n</p>\r\n<p align=\"left\">\r\n	本文摘自<span class=\"internal\">留芳网</span>\r\n</p>\r\n<div align=\"left\">\r\n<pre><code>1、腾讯 </code></pre>\r\n</div>\r\n<p align=\"left\">\r\n	马化腾给公司起名为腾讯，饱含着深长的意味。一方面，马化腾的名字里有个“腾”字，公司和自己密切相关；另一方面，“腾”也有腾飞、发达的意思。后\r\n缀为 \r\n“讯”，更多是因为老东家润讯对马化腾的影响。至于英文命名Tencent，更多是参考著名的通讯公司朗讯(lucent)而起的。后来腾讯在香港上\r\n市，Tencent的英文名被香港人称为“十分钱”(ten \r\ncent)，正好那时的腾讯至多是家SP(短信内容提供商)公司，用户所发的短信腾讯收费一毛钱一条，正好十分钱，起名很是贴切和形象。\r\n</p>\r\n<div align=\"left\">\r\n<pre><code>2、阿里巴巴 </code></pre>\r\n</div>\r\n<p align=\"left\">\r\n	马云有一次在美国一家餐厅吃饭时，他突发奇想，找来了餐厅服务员，问他是否知道阿里巴巴这个名字。服务员回答说知道，并且还跟马云说阿里巴巴打开宝\r\n藏的咒语是“芝麻开门”。之后马云又在各地反复地询问他人，经过这个测试，马云发现阿里巴巴的故事被全世界的人所熟知，并且不论语种，发音也近乎一致。\r\n“从我外婆到我儿子，他们都读阿里巴巴。”就这样，一锤定音，马云将“阿里巴巴”确定为公司的名字。\r\n</p>\r\n<div align=\"left\">\r\n<pre><code>3、百度 </code></pre>\r\n</div>\r\n<p align=\"left\">\r\n	“百度”二字源于中国宋朝词人辛弃疾的《青玉案·元夕》诗句：“众里寻她千百度，那人却在灯火阑珊处”。李彦宏说：我从小就喜欢唐诗宋词，对中国传\r\n统文化的认同度非常强。当时的想法是，网站的名字要有中文的含义，要让中国人能明白。还要有简单的拼音，能表现搜索的含义，但不能很直接地就叫搜什么，要\r\n有文化的底蕴。想来想去，突然就冒出辛弃疾的这句词，当即就拍板了。因为“百度”两字把我们想要表达的东西全都涵盖了。\r\n</p>\r\n<div align=\"left\">\r\n<pre><code>4、苹果 </code></pre>\r\n</div>\r\n<p align=\"left\">\r\n	比较官方的说法是，乔布斯当年雄心勃勃，立志要把公司打造成神级大牛，所以一开始设计的标志是牛顿拿着一个苹果，因为牛顿被苹果砸出万有引力定律的\r\n传说已经家喻户晓。后来觉得太复杂了，就把牛顿删了，只留下苹果。还有一个说法是，因为乔布斯喜欢苹果，所以标志选了个苹果，但是设计好以后发现远远看去\r\n仿佛一个屁股，恐怕要被人嘲笑，所以就在苹果上咬一口，这样大家就知道这是一个苹果而绝不是一个屁股。\r\n</p>\r\n<div align=\"left\">\r\n<pre><code>5、Google </code></pre>\r\n</div>\r\n<p align=\"left\">\r\n	“googol”一词，指的是10的100次幂，写出的形式为数字1后跟100个零，可用来代表在互联网+上可以获得的海量的资源。但想出此名字的\r\n是斯坦福大学学生肖恩·安德森，他在互联网域名注册数据库里面搜索时，把这个词打成了“google .com”，他发现这个域名刚好可以使用。\r\n</p>\r\n<div align=\"left\">\r\n<pre><code>6、NOKIA </code></pre>\r\n</div>\r\n<p align=\"left\">\r\n	诺基亚(Nokia)指的是生活在芬兰的一种长着黑色长毛与利爪的野兽，这种野兽主要生活在芬兰境内一条河的两岸，因此，人们就以这个怪兽的名字来\r\n命名了这条河，称之为诺基亚河(River Nokia).1865年，当弗雷德里克-依德斯塔姆(Fredrik \r\nIdestam)准备开始创业的时候，选择了在诺基亚河的岸边设立它的公司。\r\n</p>\r\n<div align=\"left\">\r\n<pre><code>7、Yahoo </code></pre>\r\n</div>\r\n<p align=\"left\">\r\n	《格列佛游记》的作者Jonathan \r\nSwift发明了yaho这个单词。在小说里，它代表了一个在外表和行为举止上都令人厌恶的家伙，简直都不能算是一个人。David \r\nFilo和杨致远坚持选择这个名称的原因是他们喜欢字典里对Yahoo的定义“粗鲁，不通世故，粗俗”，觉得他们自己就是这种人。\r\n</p>','','','1','11','blog','97','0','0','n','n','n','y','y','','','0');
INSERT INTO emlog_blog VALUES('18','大道至简，悟者天成','1438618764','<p>\r\n	自从1号开始使用Emblog框架起，就一直在寻找一个适合自己的模板，断断续续的花了一天多的时间，我几乎把Emblog官方的所有免费模板都试用过了，依旧没能找到一款自己满意的。折腾了这么久，真的有点疲倦了，其实我的标准很简单，就是“简单”。不要有其他冗杂的功能，只要能简洁的显示文章列表，能够简洁的显示微语，能够简洁的显示友链，其他的可有可无的功能大可去掉。\r\n</p>\r\n<p>\r\n	今日静下来，终于想通一个问题，既然别人那么多功能，而我又不想要那么多功能，那么我何不找一个相对简单的模板，然后把自己不想要的地方去掉，然后改成自己喜欢的样子呢？说干就干，经过晚上的努力，最后以现在的样子出现了。\r\n</p>\r\n<p>\r\n	<a target=\"_blank\" href=\"http://www.mryu.top/content/uploadfile/201508/74611438619596.jpg\" id=\"ematt:14\"><img src=\"http://www.mryu.top/content/uploadfile/201508/thum-74611438619596.jpg\" title=\"点击查看原图\" alt=\"mryu20150804.JPG\" height=\"234\" border=\"0\" width=\"420\" /></a> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	自我感觉良好，看着还算比较舒服，符合自己的审美观，比较简单清新，在这里要感谢Tod分享的原模板。由于刚刚入手Emblog不久，所以很多东西还需要慢慢学，慢慢积累，暂时先用着这个吧。等过段时间真正有闲功夫了，再自己重新弄个模板使使。说句实在话，对于自己的前端技术是在不敢恭维，还需要好好的努力。\r\n</p>\r\n<p>\r\n	关于网站的一些工作到现在可以告一段落了，接下来要好好的学习一下JS以及准备即将到来的研究生入学英语考试，听学长说，此次英语考试过了70分，研究生阶段可以免修英语哟，加油加油~\r\n</p>','','','1','7','blog','57','0','1','n','n','n','y','y','','','0');
INSERT INTO emlog_blog VALUES('25','2015.08.11布衣日记','1439286444','<p style=\"text-indent:2em;\">\r\n	隔两天才过来打理站点了，这两天在背单词。由于台风苏迪罗的影响，连日的暴雨，导致心里比较压抑，我向来就是一个心情比较容易受天气支配的人，比较难控制自己的情绪，这就是一种不成熟的标志。对于未来的思考，让我陷入了迷茫，虽说读研让我对未来有了一个大致的方向，但是当学长问我更远一步的规划时我又陷入了困惑，因为我不知道，这样走下去究竟人生的意义在哪里？学长说其实研究生真正的三年学的东西还是很少的，同于本科教育还是有很多的无用功在里面，国内的教育本就如此，还不如大学毕业就出去工作，还能提早的步入社会，在这个熔炉里面摸爬滚打。我真的不想成为大多数那样的为了读研而读研，仅仅只是为了拿那么一张研究生证书，这并不是我的初衷。尽管最初儿时的博士梦是我读书比较大的一个推力，但是随着年龄的增长、人生阅历的增加我发现我已经对于念书有了新的理解，读硕读博也有了自己的理解。往小的说，是为了让自己有一技之长，提高自己的学习能力，得到一块敲门砖，将来出来找一个好的工作，往大的说希望自己在读硕读博期间，能够在一个狭小的区域，做出世界一流的成绩，当后人提起这个领域时，首先想到的是我。不过这也就是个梦想，不必太过于苛求，但是一定要努力去追求。\r\n</p>\r\n<p style=\"text-indent:2em;\">\r\n	今年我21，是奔三的第一年，听人说从二十岁到三十岁这十年非常的重要，而我正处于这关键十年的开始，接下来的这段路该怎么去走，对于我今后的人生来说是至关重要的。\r\n</p>','','','1','7','blog','54','0','0','n','n','n','y','y','111001125','','0');
INSERT INTO emlog_blog VALUES('26','解决Sublime Text 2中文显示乱码问题','1439693308','<p>\r\n	支持GB2312和gbk。具体的步骤如下：\r\n</p>\r\n<p>\r\n	1、安装Sublime Package Control。\r\n</p>\r\n<p>\r\n	在Sublime Text 2上用Ctrl+～打开控制台并在里面输入以下代码，Sublime Text 2就会自动安装Package Control。\r\n</p>\r\n<p>\r\n	import urllib2,os; pf=\'Package Control.sublime-package\'; ipp=sublime.installed_packages_path(); os.makedirs(ipp) if not os.path.exists(ipp) else None; urllib2.install_opener(urllib2.build_opener(urllib2.ProxyHandler())); open(os.path.join(ipp,pf),\'wb\').write(urllib2.urlopen(\'http://sublime.wbond.net/\'+pf.replace(\' \',\'%20\')).read()); print(\'Please restart Sublime Text to finish installation\')\r\n</p>\r\n<p>\r\n	2、重启Sublime Text 2\r\n</p>\r\n<p>\r\n	3、然后使用Ctrl+Shift+P打开命令行模式，在里面输入Install Package即可搜索需要的Package。\r\n</p>\r\n<p>\r\n	4、一般使用“ConvertToUTF8”和“gbk Encoding Support”即可正常读取和写入cjk格式的文件了。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<br />','','','1','10','blog','61','0','0','n','n','n','y','y','','','0');
INSERT INTO emlog_blog VALUES('31','【ubuntu使用手记】ubuntu打开windows下txt文档乱码问题的解决','1443176683','<div align=\"center\">\r\n	<p align=\"center\">\r\n		<strong><span style=\"font-size:18px;\">ubuntu打开windows下txt文档乱码问题的解决</span></strong> \r\n	</p>\r\n	<p align=\"left\">\r\n		<span style=\"font-size:18px;\"></span> \r\n	</p>\r\n	<p align=\"left\">\r\n		<strong>问题：</strong>今天使用ubuntu时，在打开原来windows中TXT文件的时候发现中文乱码问题\r\n	</p>\r\n	<p align=\"left\">\r\n		<br />\r\n	</p>\r\n	<p align=\"left\">\r\n		<strong>方法：</strong>在网上查了一下，一些网友提供了下面的方法：\r\n	</p>\r\n	<p align=\"left\">\r\n		方法一 “按Alt+F2，打开“运行应用程序”对话框，输入“gconf-editor”，回车打开“配置编辑器”，展开/apps/gedit-2 /preferences/encodings节点，打开auto_detected键的“编辑键”对话框，点击“添加”，输入“GB2312”，回车， 再点击“向上”把GB2312移到第一项，点击“确定”后退出。再打开含中文的文件的时候，就不会出现乱码了。”\r\n	</p>\r\n	<p align=\"left\">\r\n		方法二、按照上面的步骤操作，打开“配置管理器”的时候发现没有ecoding这一项。\r\n	</p>\r\n	<p align=\"left\">\r\n		网上提供了另一种解决方案：\r\n	</p>\r\n	<p align=\"left\">\r\n		在终端输入sudo apt-get install dconf-tools 安装dconf-editor\r\n	</p>\r\n	<p align=\"left\">\r\n		然后输入命令dconf-editor打开\r\n	</p>\r\n	<p align=\"left\">\r\n		dconf-editor下org---&gt;gnome---&gt;gedit---&gt;preferences---&gt;encodings---&gt;auto-detected\r\n	</p>\r\n	<p align=\"left\">\r\n		最前面添加\r\n	</p>\r\n	<p align=\"left\">\r\n		\'GB18030\', \'GB2312\', \'GBK\',\r\n	</p>\r\n	<p align=\"left\">\r\n		即可打开txt文档。\r\n	</p>\r\n	<p align=\"left\">\r\n		<br />\r\n	</p>\r\n	<p align=\"left\">\r\n		<strong>点评：</strong>亲测方法可用\r\n	</p>\r\n<br />\r\n	<p>\r\n		<br />\r\n	</p>\r\n<span style=\"font-size:18px;\"></span> \r\n	<p>\r\n		<br />\r\n	</p>\r\n</div>','','','1','13','blog','33','0','0','n','n','n','y','y','','','0');
INSERT INTO emlog_blog VALUES('32','【ubuntu使用手记】vi 编辑器的使用','1443186846','<div align=\"left\">\r\n	<p align=\"center\">\r\n		<span style=\"font-size:18px;\"><em><strong><strong></strong></strong></em>vi 编辑器的使用&nbsp;&nbsp; <br />\r\n</span> \r\n	</p>\r\n	<p align=\"left\">\r\n		<span style=\"font-size:18px;\"><span style=\"font-size:12px;\">用linux怎么可能不会用vi编辑器呢，这也太low了吧，今晚学习vi的使用方法，要努力的熟练掌握vi，争取早日脱离鼠标，解脱右手[/偷笑]</span></span> \r\n	</p>\r\n	<p align=\"left\">\r\n		<span style=\"font-size:18px;\"><span style=\"font-size:12px;\"></span><br />\r\n</span> \r\n	</p>\r\n	<p>\r\n		<strong><em><u><span style=\"background-color:#FFFFFF;font-size:14px;color:#E53333;\">命令模式 &nbsp;插入模式 和命令行模式 替换模式</span></u></em></strong> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:14px;\"></span> \r\n	</p>\r\n	<p>\r\n		按i进入当前行输入模式（插入模式)可以编辑内容 按o（在光标下新一行插入）\r\n	</p>\r\n	<p>\r\n		按ESC键退回到命令模式 &nbsp; &nbsp; &nbsp; 按shift+；进入命令行模式 然后wq 退出保存\r\n	</p>\r\n	<p>\r\n		在命令模式下按一个大写的R就可以进入替换模式\r\n	</p>\r\n	<p>\r\n		大V 可视行 &nbsp; &nbsp;小v 可视 &nbsp; &nbsp; ctrl+v 可视块\r\n	</p>\r\n	<p>\r\n		<br />\r\n	</p>\r\n	<p>\r\n		<br />\r\n	</p>\r\n	<p>\r\n		<strong><em><u><span style=\"background-color:#337FE5;\"><span style=\"background-color:#FFFFFF;color:#337FE5;\">复制粘贴</span><span style=\"color:#FFFFFF;\"></span></span></u></em></strong>等都只能在<span style=\"background-color:#FFFFFF;color:#337FE5;\"><strong><em>命令模式</em></strong></span>下使用\r\n	</p>\r\n	<p>\r\n		1.先按ctrl+v 进入可视块，选择你想要的区块 然后按y进行复制\r\n	</p>\r\n	<p>\r\n		2。移动到你想要粘贴的位置 按o（在光标下新一行插入）进入到插入模式，\r\n	</p>\r\n	<p>\r\n		3。按ESC进入到命令模式下 再按p粘贴\r\n	</p>\r\n	<p>\r\n		<br />\r\n	</p>\r\n	<p>\r\n		<br />\r\n	</p>\r\n	<p>\r\n		<span style=\"background-color:#FFFFFF;color:#337FE5;\"><strong>命令行模式</strong></span> \r\n	</p>\r\n	<p>\r\n		:q 如果未对文件进行操作则退出 ：w保存不退出\r\n	</p>\r\n	<p>\r\n		：wq 保存退出（跟:x是一样的）加了！一般有强制的意思（因为有些是只读文件）\r\n	</p>\r\n	<p>\r\n		<br />\r\n	</p>\r\n	<p>\r\n		<br />\r\n	</p>\r\n	<p>\r\n		<span style=\"background-color:#FFFFFF;color:#337FE5;\"><strong>命令模式</strong></span> \r\n	</p>\r\n	<p>\r\n		page up（down）翻页 end（$也可以)一行尾 ，home（0也可以）一行首\r\n	</p>\r\n	<p>\r\n		行间跳转： gg（1G）跳到文件行首，G跳到末行 nG第N行\r\n	</p>\r\n	<p>\r\n		set nu 设置vim文本中行号\r\n	</p>\r\n	<p>\r\n		<span style=\"background-color:#FFFFFF;color:#337FE5;\"><strong><em>删除</em></strong></span><span style=\"background-color:#FFFFFF;color:#337FE5;\"> &nbsp;</span> dd删除这一个行 （x 单个字符）u(undo)可以撤销 删掉向下多行ndd &nbsp;\r\n	</p>\r\n	<p>\r\n		d+$符号表示删除光标所在位置至这一行行尾，d+^ ……至这一行行首\r\n	</p>\r\n	<p>\r\n		<br />\r\n	</p>\r\n	<p>\r\n		<strong><em><span style=\"background-color:#FFFFFF;color:#337FE5;\">复制</span></em></strong><span style=\"background-color:#FFFFFF;color:#337FE5;\"> &nbsp;</span> &nbsp;yy复制这一行 小p向下粘贴 &nbsp;nyy复制n行 y也可以复制选中的东西\r\n	</p>\r\n	<p>\r\n		n下一个\r\n	</p>\r\n	<p>\r\n		<br />\r\n	</p>\r\n	<p>\r\n		<strong><em><span style=\"background-color:#FFFFFF;color:#337FE5;\">查找 </span></em></strong><span style=\"background-color:#FFFFFF;color:#337FE5;\">&nbsp;</span>/word 从上往下 ？word从下往上 ctrl+r恢复 重复模式 可以按\".\"就是重复操作\r\n	</p>\r\n	<p>\r\n		<br />\r\n	</p>\r\n	<p>\r\n		<strong><span style=\"background-color:#FFFFFF;color:#337FE5;\"><em>查找并替换</em></span></strong>（在命令行模式下面）\r\n	</p>\r\n	<p>\r\n		：s/old/new/g 将当前行中“old”替换成“new”\r\n	</p>\r\n	<p>\r\n		：##s/old/new/g 在“##”范围内替换所有 &nbsp;:%s/old/new/g整篇文件 最后加c而不是g的话表示是替换动作会进行用户确认\r\n	</p>\r\n	<p>\r\n		读入或者打开新文件 :r /etc/passwd 表示当前文件读入其他文件的内容 :e/etc/passwd 表示打开新的文件进行编辑 &nbsp;另存为 :w /tmp/hello.txt 在末行模式下面将当前文件另存为/tmp目录下的hello.txt\r\n	</p>\r\n	<p>\r\n		剪切选中的文本（x）\r\n	</p>\r\n	<p>\r\n		<br />\r\n	</p>\r\n	<p>\r\n		<br />\r\n	</p>\r\n	<p>\r\n		<strong><em><span style=\"color:#337FE5;\">多个文件如何操作</span></em></strong>（在末行模式下面）\r\n	</p>\r\n	<p>\r\n		vim aa bb cc可以新建多个文件 :n可以切换到下一个文件 :N可以切换到上一个文件 n表示next\r\n	</p>\r\n	<p>\r\n		:file列出所有的文件\r\n	</p>\r\n	<p>\r\n		:set nu (nonu)显示和不显示行号\r\n	</p>\r\n	<p>\r\n		vim ~/.vimc &nbsp;可以在这个配置文件里面写些东西，vim编辑器启动的时候会加载里里面的配置。出现相应的功能\r\n	</p>\r\n	<p>\r\n		eg：set nu 设置显示行号\r\n	</p>\r\n	<p>\r\n		&nbsp; &nbsp;set ts=4 设置一个tab键占四个空格的位置\r\n	</p>\r\n	<p>\r\n		&nbsp; &nbsp;set cursorline 在光标所在行显示下划线\r\n	</p>\r\n	<p>\r\n		<br />\r\n	</p>\r\n</div>','','','1','13','blog','22','0','0','n','n','n','y','y','','','0');
INSERT INTO emlog_blog VALUES('33','【人机交互作业】第1讲（2）GUI中目标移动问题','1443239643','题目：考虑在图形用户界面（Graphical User Interface，GUI）中，将图标从A处移动到B处的过程与什么因素相关？请分析原因。<br />\r\n<br />\r\n根据Fitts\'s Law 在图形用户界面中,将图标从A处移动到B处的过程所需时间由两个因数决定:<br />\r\n1.A点到B点的距离D<br />\r\n2.目标点B的大小S<br />\r\n<br />\r\n用数学公式表达为时间T=a+blog2(D/S+1)&nbsp; 其中a,b参数因人而异<br />\r\n<br />\r\n一 若B是静止的,且用户可沿任意轨迹移动,则移动过程满足Fitts\' Law.移动过程受两个因数制约A点到B点的距离D以及目标点B的大小S. D越大,S越小则移动所耗费的时间T越长.<br />\r\n<br />\r\n原因分析:易知同一个用户移动光标速度一定的情况下,距离D越大,移动所耗费的时间T也越长.一般来说目标越小,人定位它所耗费的时间也越长.人在操作光标的时候有一个灵敏度范围,我们在捕获目标B并把光标移动到目标B上实际上是一个人脑的认知目标,并控制手移动鼠标的过程,目标越小,我们在目标附近最后定位的时间也越长.<br />\r\n<br />\r\n二 若B是静止的,但用户需要沿某一特定轨迹移动,则移动的过程所耗费的时间受三个因素决定:<br />\r\n1.A点到B点的距离D<br />\r\n2.目标点B的大小S<br />\r\n3.轨迹的宽度W (W越大,移动所耗费的时间越小)<br />\r\n<br />\r\n原因分析:可以利用微分的思想,将轨迹切割为N个目标Cn,则A-&gt;C1 ,C1-&gt;C2.....Ci-&gt;Ci+1....Cn-&gt;B每一个移动过程都符合Fitts\' Law.轨迹越宽,所切割的目标元也就越大,每一个子移动过程所耗费的时间也越短再叠加起来,那么整个移动过程所耗费的时间也就越短.<br />\r\n<br />\r\n<br />\r\n三 若B是运动的,那么移动过程所耗费的时间将还受目标B移动的速度影响以及B的运动轨迹是否已知,也就是说移动的过程受五个因素决定<br />\r\n<br />\r\n1.A点到B点的距离D<br />\r\n2.目标点B的大小S<br />\r\n3.轨迹的宽度W <br />\r\n4.目标B的移动速度V (V越大,移动所耗费的时间越大)<br />\r\n5.目标B的移动轨迹是否已知 (若运动轨迹已知,则移动所耗费的时间越小)<br />\r\n<br />\r\n原因分析:在其他因素不变的情况下,目标运动的速度越大,一方面人捕捉它的难度也就越大,另一方面在捕捉过程中光标实际移动的距离也会变大,总的移动所耗费的时间也自然变大.在其他因素不变的情况下,若运动的轨迹已知,则人捕捉它的难度也会降低,捕捉它的所耗费的时间也会相应的减小,总的移动所耗费的时间也就变小了.<br />\r\n<br />\r\n<br />','','','1','14','blog','102','0','0','n','n','n','y','y','','','0');
INSERT INTO emlog_blog VALUES('34','【英语课堂演讲】As the century turns','1443495502','<p align=\"center\">\r\n	<strong><span style=\"font-size:18px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; As the century turns（Reading 4）</span></strong> \r\n</p>\r\n<p align=\"center\">\r\n	<br />\r\n</p>\r\n<div align=\"center\">\r\n	Speaker: Wenyan Yu<br />\r\nClass: 710<br />\r\nDepartment: Computer and Control Institute<br />\r\nPersonal website: www.mryu.top\r\n</div>\r\n<p align=\"center\">\r\n	Date: 2015.9.29\r\n</p>\r\n<p align=\"center\">\r\n	<br />\r\n</p>\r\n<p align=\"center\">\r\n	<br />\r\n</p>\r\n<div align=\"center\">\r\n	<span style=\"font-size:16px;\">&nbsp; 1.NEW VOCABULARY</span><br />\r\n<br />\r\n<strong>●&nbsp; millennium</strong><br />\r\n●&nbsp; Many people refer to the year 2000 as the Millennium.<br />\r\n● <strong>decree</strong><br />\r\n<br />\r\n<a href=\"http://www.mryu.top/content/uploadfile/201509/8d411443495599.jpeg\" target=\"_blank\" title=\"decree.jpeg\"><img alt=\"\" title=\"\" src=\"http://www.mryu.top/content/uploadfile/201509/8d411443495599.jpeg\" align=\"\" border=\"0\" height=\"254\" width=\"336\" /></a><br />\r\n● In July he issued a decree ordering all unofficial armed groups<br />\r\n&nbsp; in the country to disband.<br />\r\n● <strong>venerable</strong><br />\r\n<br />\r\n<a href=\"http://www.mryu.top/content/uploadfile/201509/03ad1443495599.jpeg\" target=\"_blank\" title=\"venerable.jpeg\"><img alt=\"\" title=\"\" src=\"http://www.mryu.top/content/uploadfile/201509/03ad1443495599.jpeg\" align=\"\" border=\"0\" height=\"237\" width=\"335\" /></a><br />\r\n<br />\r\n● May Day has become a venerable institution<br />\r\n● <strong>equinox</strong><br />\r\n<br />\r\n<a href=\"http://www.mryu.top/content/uploadfile/201509/4afc1443495599.jpeg\" target=\"_blank\" title=\"equinox.jpeg\"><img alt=\"\" title=\"\" src=\"http://www.mryu.top/content/uploadfile/201509/4afc1443495599.jpeg\" align=\"\" border=\"0\" height=\"261\" width=\"335\" /></a><br />\r\n&nbsp; In the Chinese calendar, the Spring Equinox always occurs in<br />\r\n&nbsp; the second month.<br />\r\n●<strong> flaw</strong><br />\r\n<a href=\"http://www.mryu.top/content/uploadfile/201509/14dc1443495599.jpeg\" target=\"_blank\" title=\"flaw.jpeg\"><img src=\"http://www.mryu.top/content/uploadfile/201509/14dc1443495599.jpeg\" align=\"absmiddle\" border=\"0\" height=\"220\" width=\"334\" /></a><br />\r\n<br />\r\n<br />\r\n</div>\r\n<p align=\"center\">\r\n	● It was a large diamond, but it had a flaw\r\n</p>\r\n<p align=\"center\">\r\n	●&nbsp;<strong> revelers</strong><br />\r\n<a href=\"http://www.mryu.top/content/uploadfile/201509/f3141443495598.jpg\" target=\"_blank\" title=\"revelers.jpg\"><img alt=\"\" title=\"\" src=\"http://www.mryu.top/content/uploadfile/201509/f3141443495598.jpg\" align=\"\" border=\"0\" height=\"226\" width=\"336\" /></a><br />\r\n<br />\r\n●&nbsp; The clubs attract revelers as young as thirteen.\r\n</p>\r\n<p align=\"center\">\r\n	<br />\r\n</p>\r\n<p align=\"center\">\r\n	<span style=\"font-size:16px;\"><strong>2.STRUCTURE</strong></span> \r\n</p>\r\n<p align=\"center\">\r\n	<span style=\"font-size:16px;\"><strong><br />\r\n</strong></span> \r\n</p>\r\n<div align=\"center\">\r\n	Argumentation<br />\r\n=&gt;The first paragraph: lead to the topic. People celebrated the<br />\r\narrival of the millennium in advance.<br />\r\n=&gt;The second section: the development history of the Gregorian<br />\r\ncalendar<br />\r\n</div>\r\n<p align=\"center\">\r\n	=&gt;The third section: the Gregorian calendar now.\r\n</p>\r\n<p align=\"center\">\r\n	<br />\r\n</p>\r\n<p align=\"center\">\r\n	<strong><span style=\"font-size:16px;\">3.EXERCISE</span></strong> \r\n</p>\r\n<p align=\"center\">\r\n	&nbsp;B&nbsp; C&nbsp; D&nbsp;&nbsp; D C A\r\n</p>\r\n<p align=\"center\">\r\n	<br />\r\n</p>\r\n<p align=\"center\">\r\n	<strong><span style=\"font-size:16px;\">4.Reading extension</span></strong> \r\n</p>\r\n<p align=\"center\">\r\n	<strong><span style=\"font-size:16px;\"><br />\r\n</span></strong> \r\n</p>\r\n<div align=\"center\">\r\n	问题： 21 世纪源于何年？<br />\r\n观点：<br />\r\n一方从科学角度出发认为 21 世纪和新千年源于 2001 年 1 月 1 日<br />\r\n一方从习惯角度出发认为 21 世纪和新千年源于 2000 年 1 月 1 日<br />\r\n分析：造成这一混乱的根源是国际通用的公历纪年是 1400 年前提<br />\r\n</div>\r\n<p align=\"center\">\r\n	出的基督纪年，它的起点定为公元 1 年。\r\n</p>\r\n<p align=\"center\">\r\n	<br />\r\n</p>\r\n<p align=\"center\">\r\n	<br />\r\n</p>\r\n<p align=\"center\">\r\n	<span style=\"font-size:32px;\">Thank you!</span> \r\n</p>\r\n<p align=\"center\">\r\n	<br />\r\n</p>\r\n<p align=\"center\">\r\n	<span style=\"font-size:16px;\"><em>注：演讲PPT下载：<span class=\"attachment\"><a target=\"_blank\" href=\"http://www.mryu.top/content/uploadfile/201509/c93b1443496094.pdf\">计控710余文艳.pdf</a></span></em></span><br />\r\n<span style=\"font-size:32px;\"></span> \r\n</p>\r\n<p align=\"center\">\r\n	<span style=\"font-size:32px;\"><br />\r\n</span> \r\n</p>','','','1','15','blog','30','0','6','n','n','n','y','y','','','0');
INSERT INTO emlog_blog VALUES('35','【求学国科大】国庆长假计划','1443615852','<div align=\"center\">\r\n	<strong><span style=\"font-size:16px;\">国庆长假计划（2015.10.1-2015.10.7）（共计7天）</span></strong><br />\r\n</div>\r\n<br />\r\n转眼来到国科大就快一个月了，明天就是十一，还和以往一样学四天，玩三天！废话不多说把目前课程内需完成的作业归纳如下：<br />\r\n<br />\r\n1.高级软件工程<br />\r\n<br />\r\na.HW1:Ruby intro （优先级1）<br />\r\nb.复习课堂笔记（优先级2）<br />\r\nc.利用tryruby.org在线学习平台先把ruby基础过一遍（优先级3）<br />\r\n<br />\r\n2.模式识别与机器学习<br />\r\n<br />\r\na.自主学习授课课件，针对每一节进行学习笔记的整理，该推导的公式要亲自推导，并形成相应的电子文档（优先级2）<br />\r\nb.完成相关的课后作业（优先级2）<br />\r\n<br />\r\n3.高级人工智能<br />\r\n<br />\r\na.考虑下自己survey主题（优先级3）<br />\r\n<br />\r\n4.人机交互<br />\r\n<br />\r\na.完成课后作业第一题初稿，以及思考下Project的主题（优先级3）<br />\r\n<br />\r\n5.网络数据挖掘<br />\r\n<br />\r\na.自主学习授课课件，针对每一节进行学习笔记的整理，该推导的公式要亲自推导，并形成相应的电子文档（优先级2）<br />\r\n<br />\r\nb.精度下面论文，假设需要在实验室进行汇报,写20页左右的幻灯片汇报此论文(包括推导过程)（优先级1）<br />\r\nDaniel D. Lee and H. Sebastian Seung:Algorithms for Non-negative Matrix Factorization. NIPS 2000.(NIPS Classic Paper Award)<br />\r\n自己动手推导multiplicative update rules<br />\r\n<br />\r\n6.认知计算<br />\r\n<br />\r\na.认真研读老师授课笔记，并进行相关算法的拓展学习（优先级2）<br />\r\n<br />\r\n7.英语A<br />\r\n<br />\r\na.English writing!作文号：491768 Deadline:Oct. 13（优先级2）<br />\r\n<br />\r\n<br />\r\n以上7条均为课程内安排，课程之外的任务由于都是一些长期计划，所以这里就没有什么优先级的了。主要是以下三件，它们也讲贯穿我目前的整个学习阶段。<br />\r\n<br />\r\n1.初步掌握LaTex的使用<br />\r\n<br />\r\n2.提高自己的英语词汇量<br />\r\n<br />\r\n3.熟练掌握Linux的使用<br />\r\n<br />\r\n<br />\r\n说完了学习，接下来该计划下出去玩的安排了，大概是四号进京玩一天、五号去普罗旺斯逛逛、六号从G111往北骑行，看看那边的一路好风景，傍晚如果有闲情的话可以骑着车去雁栖湖转转。<br />\r\n<br />\r\n<br />\r\n<br />\r\n<p>\r\n	最后祝：国庆快乐，即学到又休息到~（突然想起了大学寝室老大床头牌的一句话--睡觉睡到自然醒，数钱数到手抽筋[/偷笑]）\r\n</p>\r\n<p align=\"center\">\r\n	<br />\r\n</p>\r\n<br />\r\n<br />\r\n<br />\r\n<br />','','','1','7','blog','34','0','1','n','n','n','y','y','','','0');
INSERT INTO emlog_blog VALUES('36','【ubuntu使用笔记】ubuntu下deb包的安装方法','1443847092','<p>\r\n	deb是debian linus的安装格式，跟red hat的rpm非常相似，最基本的安装命令是：dpkg -i file.deb\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	dpkg 是Debian Package的简写，是为Debian 专门开发的套件管理系统，方便软件的安装、更新及移除。所有源自Debian的Linux发行版都使用dpkg，例如Ubuntu、Knoppix 等。\r\n</p>\r\n<p>\r\n	以下是一些 Dpkg 的普通用法：\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	1、dpkg -i &lt;package.deb&gt;\r\n</p>\r\n<p>\r\n	安装一个 Debian 软件包，如你手动下载的文件。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	2、dpkg -c &lt;package.deb&gt;\r\n</p>\r\n<p>\r\n	列出 &lt;package.deb&gt; 的内容。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	3、dpkg -I &lt;package.deb&gt;\r\n</p>\r\n<p>\r\n	从 &lt;package.deb&gt; 中提取包裹信息。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	4、dpkg -r &lt;package&gt;\r\n</p>\r\n<p>\r\n	移除一个已安装的包裹。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	5、dpkg -P &lt;package&gt;\r\n</p>\r\n<p>\r\n	完全清除一个已安装的包裹。和 remove 不同的是，remove 只是删掉数据和可执行文件，purge 另外还删除所有的配制文件。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	6、dpkg -L &lt;package&gt;\r\n</p>\r\n<p>\r\n	列出 &lt;package&gt; 安装的所有文件清单。同时请看 dpkg -c 来检查一个 .deb 文件的内容。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	7、dpkg -s &lt;package&gt;\r\n</p>\r\n<p>\r\n	显示已安装包裹的信息。同时请看 apt-cache 显示 Debian 存档中的包裹信息，以及 dpkg -I 来显示从一个 .deb 文件中提取的包裹信息。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	8、dpkg-reconfigure &lt;package&gt;\r\n</p>\r\n<p>\r\n	重新配制一个已经安装的包裹，如果它使用的是 debconf (debconf 为包裹安装提供了一个统一的配制界面)。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	转自：http://blog.csdn.net/kevinhg/article/details/5934462\r\n</p>\r\n<p>\r\n	版权声明：本站文章若为转载均已注明出处，感谢原作者的分享，如有不妥请联系我。否则均为原创，未经博主允许不得私自转载。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>','','','1','13','blog','18','0','0','n','n','n','y','y','','','0');
INSERT INTO emlog_blog VALUES('37','【ubuntu使用笔记】ubuntu 下使用wps替代office的方案','1443848312','<p>\r\n	问题：\r\n</p>\r\n<p>\r\n	在完全离开windows，使用一段时间ubuntu之后我发现学习生活中，ppt演示文稿的使用率很高，然而ubuntu 自带的office与微软的office兼容性很差，而且很多功能并不好用。\r\n</p>\r\n<p>\r\n	今天我在麒麟系统的官网上意外的发现了wps-for-ubuntukylin，非常的开心。wps是国际领先的桌面办公套件，可以实现办公软件最常用的文字、表格、演示等多种功能。ubuntu kylin团队与金山公司合作，继续为优麒麟个人用户免费提供wps-for-ubuntukylin使用。值得欣慰的他们所提供的版本并不局限于麒麟系统，此外还提供了ubuntu12.04 ubuntu13.04 ubuntu14.04三个版本，在此先对这些开源工作者表示由衷的感谢。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p align=\"left\">\r\n	方法：1.首先进入麒麟官网下载wps deb安装包 地址：http://www.ubuntukylin.com/applications/showimg.php?lang=cn&amp;id=19.\r\n</p>\r\n<p align=\"left\">\r\n	Tips:注意选择自己系统对应的deb安装包,若你的系统足够老，那就下12.04版本的，祝你好运!\r\n</p>\r\n<p align=\"left\">\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 2.然后就是安装啦\r\n</p>\r\n<pre class=\"prettyprint lang-bsh linenums\">dpkg -i  &lt;deb包名&gt;</pre>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 3.最后然是打开啦，如果不出意外你会看到如下~\r\n<p align=\"center\">\r\n	<a target=\"_blank\" href=\"http://www.mryu.top/content/uploadfile/201510/b7671443850073.png\" id=\"ematt:33\"><img src=\"http://www.mryu.top/content/uploadfile/201510/thum-b7671443850073.png\" title=\"点击查看原图\" alt=\"Selection_013.png\" border=\"0\" height=\"267\" width=\"420\" /></a>\r\n</p>\r\n<p align=\"left\">\r\n	<br />\r\n</p>\r\n<p align=\"center\">\r\n	<br />\r\n</p>\r\n<p align=\"left\">\r\n	点评：亲测好使，很开心~\r\n</p>\r\n<p align=\"left\">\r\n	版权声明：本站文章若为转载均已注明出处，感谢原作者的分享，如有不妥请联系我。否则均为原创，未经博主允许不得私自转载。\r\n</p>\r\n<p align=\"left\">\r\n	<br />\r\n</p>','','','1','13','blog','32','0','1','n','n','n','y','y','','','0');
INSERT INTO emlog_blog VALUES('29','【ubuntu使用手记】Y470安装ubuntu12.04发热问题解决办法','1442756351','<p align=\"center\">\r\n	<strong><span style=\"font-size:18px;\">Y470安装ubuntu12.04发热问题：Bumblebee安装与使用</span></strong> \r\n</p>\r\n<p>\r\n	<strong><span style=\"font-size:18px;\"></span></strong> \r\n</p>\r\n<p>\r\n	<strong>问题：</strong>Y470在安装ubuntu12.04后，机器发热现象明显，风扇高速运转\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<strong>方法：</strong>在Ubuntu中安装Bumblebee的时候，下面这篇英文文章帮助了我，把它翻译成中文，希望能帮助更多的人。\r\n</p>\r\n<p>\r\n	英文原文： https://wiki.ubuntu.com/Bumblebee\r\n</p>\r\n<p>\r\n	原文更新： Bumblebee (2013-04-25 13:39:24由openlaptop编辑)\r\n</p>\r\n<p>\r\n	译者博客： http://blog.csdn.net/milkcu\r\n</p>\r\n<p>\r\n	感谢The Bumblebee Project Team提供如此优秀的软件。\r\n</p>\r\n<p>\r\n	Bumblebee 项目\r\n</p>\r\n<p>\r\n	Bumblebee旨在为使用GNU/Linux发行版的笔记本提供NVIDIA显卡智能切换支持。使用Bumblebee，你可以你的图像在使用Intel显卡的时候得到补偿。\r\n</p>\r\n<p>\r\n	安装\r\n</p>\r\n<p>\r\n	Bumblebee 3.2被12.04到13.04版本的Ubuntu支持。\r\n</p>\r\n<p>\r\n	你需要打开终端，并输入下面的命令。\r\n</p>\r\n<p>\r\n	如果在12.04.2，把linux-headers-generic替换成linux-headers-generic-lts-quantal。\r\n</p>\r\n<p>\r\n	&nbsp; &nbsp;sudo add-apt-repository ppa:bumblebee/stable\r\n</p>\r\n<p>\r\n	&nbsp; &nbsp;为了获得最新的Nvidia显卡驱动，你需要添加另外一个PPA源。至于12.04,，这对Nvidia GT 6xxM型号的显卡是必须的。但对GT 4xxM和GT 5xxM系列的显卡是可选安装的。当你迟疑不决的时候，那就安装它吧。命令是：sudo add-apt-repository ppa:ubuntu-x-swat/x-updates\r\n</p>\r\n<p>\r\n	&nbsp; &nbsp;sudo apt-get update\r\n</p>\r\n<p>\r\n	&nbsp; &nbsp;在使用Nvidia显卡的情况下安装Bumblebee：sudo apt-get install bumblebee virtualgl linux-headers-generic\r\n</p>\r\n<p>\r\n	&nbsp; &nbsp;重启或者注销再登陆\r\n</p>\r\n<p>\r\n	对于高级用户，如果你不想用Nvidia显卡或者它的32位库（比如你只想用Bumblebee来节能），用下面的命令安装BumBlebee：\r\n</p>\r\n<p>\r\n	sudo apt-get install --no-install-recommends bumblebee linux-headers-generic\r\n</p>\r\n<p>\r\n	linux-headers-generic是必需的，因为bbswitch（节能组件）是一个系统组件。\r\n</p>\r\n<p>\r\n	升级\r\n</p>\r\n<p>\r\n	如果你已经在你的系统上安装了Bumblebee，那就升级到最新版本Bumblebee 3.x。\r\n</p>\r\n<p>\r\n	请遵循这个wiki页面的指示：https://github.com/Bumblebee-Project/Bumblebee/wiki/Upgrading-on-Ubuntu\r\n</p>\r\n<p>\r\n	使用\r\n</p>\r\n<p>\r\n	若要独立地使用NVIDIA显卡运行程序，在终端中运行下面的命令：\r\n</p>\r\n<p>\r\n	$ optirun [options] &lt;application&gt; [application-parameters]\r\n</p>\r\n<p>\r\n	例如：\r\n</p>\r\n<p>\r\n	$ optirun firefox\r\n</p>\r\n<p>\r\n	运行下面命令可以得到optirun的所有参数：\r\n</p>\r\n<p>\r\n	$ optirun --help\r\n</p>\r\n<p>\r\n	通常对于你的窗口管理器、程序安装或者对显示性能要求不高的程序不需要任何参数。optirun命令通常在使用对显卡要求过高的程序或游戏使用。\r\n</p>\r\n<p>\r\n	电源管理\r\n</p>\r\n<p>\r\n	这个项目基本的目标不仅是在需要的时候使用GPU作为性能补充，而且是在不使用GPU的时候起到节能的作用。我们在Bumblebee中使用bbswitch（一个组件）或vga_switcheroo（内核组件，测试功能）实现这些功能。\r\n</p>\r\n<p>\r\n	从Bumblebee 3.0开始，这个功能被默认使用bbswitch。这可以让电源管理功能在不需要任何设置的情况下自动启动。\r\n</p>\r\n<p>\r\n	如果电源管理功能在你的笔记本上不能正常工作，请到Power Management (PM)页面并帮助我们提高Bumblebee。\r\n</p>\r\n<p>\r\n	网络聊天\r\n</p>\r\n<p>\r\n	如果你想要帮助我们测试和制作安装包，请加入我们的在Freenode的频道#bumblebee。\r\n</p>\r\n<p>\r\n	报告漏洞或问题\r\n</p>\r\n<p>\r\n	首先：如果你有任何问题请读这篇文章： http://wiki.Bumblebee-Project.org/Troubleshooting\r\n</p>\r\n<p>\r\n	如果你的问题没有解决，你可以加入#bumblebee寻求帮助（推荐）。或者查看http://wiki.Bumblebee-Project.org/Reporting-Issues\r\n</p>\r\n<p>\r\n	如果你被要求创建一份错误报告，在终端中运行下一条命令： sudo bumblebee-bugreport\r\n</p>\r\n<p>\r\n	卸载\r\n</p>\r\n<p>\r\n	如果你对Bumblebee不满意，可以通过下面的方式卸载：\r\n</p>\r\n<p>\r\n	&nbsp; &nbsp;sudo apt-get install ppa-purge\r\n</p>\r\n<p>\r\n	&nbsp; &nbsp;sudo ppa-purge ppa:bumblebee/stable\r\n</p>\r\n<p>\r\n	如果你想要保留一些来自bumblebee仓库的程序，你可以只删除Bumblebee（包括存在依赖关系的组件）：\r\n</p>\r\n<p>\r\n	&nbsp; &nbsp;sudo apt-get purge bumblebee\r\n</p>\r\n<p>\r\n	&nbsp; &nbsp;sudo apt-get --purge autoremove\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	点评：方法亲测可用，按照该方案操作后，笔记本发热现象，明显下降\r\n</p>\r\n<br />','','','1','13','blog','56','0','0','n','n','n','y','y','','','0');
INSERT INTO emlog_blog VALUES('30','【求学国科大】2015.9.25随笔','1443153476','<p>\r\n	转眼之间来到国科大已经快18天了，说句实在话，我真的很喜欢这里，除了这里舒适的环境，高大上的寝室以外，我深深的被这里的学习氛围给吸引住了，毫无疑问这里是我求学以来，所遇到的最适合学习的地方，安静的军都山下，美丽的雁栖湖畔，没有大都市的那种灯红酒绿，有的只是一群致力于学习，科研的小伙伴们以及工作在科研一线的老师们。来到这里，我发现我是真的静下来了，静下来去做自己许久没去做但又渴望去做的事情，静下来好好的思考一下的自己的人生，思考自己未来的路到底该怎么去走？\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	今天来到这里写些东西是有原因的，因为之前一直是在windows环境下用记事本每天写些东西，由于学习的需要以及个人的喜欢，四天前我开始把学习工作的平台完全搬到了linux上了，四天了体验式的完全使用ubuntu给我带来了前所未有的舒适感，仿佛它就是为了给向我这样的程序员设计的，稳定、高效、自由是它给我的第一感觉。尽管在使用的期间也遇到了很多问题，但都通过自己努力，参考已有的解决方案都已解决，比如ubuntu 下中文输入的问题、Y470笔记本GPU 持续工作过高温的问题、鼠标闪烁的问题、QQ 的安装及使用的问题等等，过几天有空我会把自己在使用ubuntu过程中所遇到的问题以及我所查阅的解决办法给整理出来。好吧，说偏了，还是回到为什么隔这么长时间又开始在这里写些的东西的原因：正是因为ubuntu的使用让我开始对开源文化的感激和崇敬，这就促使我希望自己能更加的去适应这种开源文化，不断的提升自己的实力，希望有一天自己能为开源社区添砖加瓦，而持续更新网站，分享自己学习的心得就是一个很好的开始。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	来国科大的这18天里，我有了如下几点改变：\r\n</p>\r\n<ol>\r\n	<li>\r\n		心更静了，对于学习的渴望更强了，对于未来的方向也似乎抓住了一点光。\r\n	</li>\r\n	<li>\r\n		把日常工作和学习平台从windows转移到了Linux.\r\n	</li>\r\n	<li>\r\n		认识到对于英语学习的需要更加迫切了。\r\n	</li>\r\n</ol>\r\n<p>\r\n	最后我想这里对于我来讲是一块栖息地，在这里我可以写日记，作笔记，写随感，分享心得，我希望这里能够记录我成长的足迹，就像建站一开始写的那句话一样，待到繁华褪去，剩下的只有生活的点滴。开拓这里的目的是纯粹的，这里也应该是简单的，简简单单的记录生活。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>','','','1','7','blog','31','0','0','n','n','n','y','y','','','0');
INSERT INTO emlog_blog VALUES('38','闲聊','1444321993','<div align=\"center\">\r\n	<p>\r\n		<span class=\"con\"> </span>\r\n	</p>\r\n	<div class=\"para\">\r\n		<p>\r\n			<span style=\"color:#337FE5;\"><strong>神秀说：</strong></span>\r\n		</p>\r\n		<p>\r\n			<span style=\"color:#337FE5;\"><strong>“身是菩提树，心如明镜台，时时勤拂拭，勿使惹尘埃。”</strong></span>\r\n		</p>\r\n<br />\r\n	</div>\r\n	<p>\r\n		<br />\r\n	</p>\r\n	<p>\r\n		<span class=\"con\"> </span>\r\n	</p>\r\n	<div class=\"para\">\r\n		<p>\r\n			<span style=\"color:#337FE5;\"><strong>惠能说：</strong></span>\r\n		</p>\r\n		<p>\r\n			<span style=\"color:#337FE5;\"><strong>“菩提本无树，明镜亦非台，本来无一物，何处惹尘埃。”</strong></span>\r\n		</p>\r\n<br />\r\n	</div>\r\n	<p>\r\n		<br />\r\n	</p>\r\n	<p>\r\n		<br />\r\n<span class=\"con\"></span> \r\n		<iframe style=\"width:400px;height:96px;border:none;\" id=\"bd_player_iframe\" name=\"bd_player_iframe\" src=\"http://box.baidu.com/widget/flash/bdspacesong.swf?from=tiebasongwidget&amp;name=%E5%A5%BD%E4%B9%85%E4%B8%8D%E8%A7%81&amp;artist=%E9%99%88%E5%A5%95%E8%BF%85&amp;extra=The%201St%20Eleven%20Years%20%E7%84%B6%E5%90%8E%E5%91%A2%3F%20(Digital%20Only)\" frameborder=\"0\">\r\n		</iframe>\r\n	</p>\r\n</div>\r\n<p align=\"center\">\r\n	<br />\r\n</p>\r\n<p align=\"center\">\r\n	<strong><span style=\"font-size:14px;color:#E53333;\">软件分享：</span></strong> \r\n</p>\r\n<p align=\"center\">\r\n	<span class=\"attachment\"><a target=\"_blank\" href=\"http://www.mryu.top/content/uploadfile/201510/fa2f1444227810.zip\">桌面小画笔：zhuomianxhb.zip（给牛牛的）--（不好用，别下了）</a></span> \r\n</p>\r\n<p align=\"center\">\r\n	<br />\r\n<span class=\"attachment\"></span> \r\n</p>\r\n<p align=\"center\">\r\n	<span class=\"attachment\"> </span> \r\n</p>\r\n<h1 align=\"center\">\r\n	<strong><span style=\"font-size:14px;background-color:#E53333;\"><span style=\"background-color:#FFFFFF;color:#E53333;\">使用 RSpec 來測試 App</span><span style=\"background-color:#FFFFFF;\"></span></span></strong> \r\n</h1>\r\n<div align=\"center\">\r\n	<p>\r\n		<a target=\"_blank\" href=\"http://railsgirls.tw/testing-rspec/\">http://railsgirls.tw/testing-rspec/</a> \r\n	</p>\r\n	<p>\r\n		<br />\r\n	</p>\r\n	<p>\r\n		<br />\r\n	</p>\r\n	<p>\r\n		<em><strong><span style=\"font-size:16px;color:#E53333;\">高级软件工程HW1测试文件下载地址：</span></strong><a target=\"_blank\" href=\"http://course.trustie.net/projects/671/repository/revisions/master/show/spec\"><strong><span style=\"font-size:16px;color:#FF9900;\"><br />\r\n</span></strong></a></em> \r\n	</p>\r\n	<p>\r\n		<em><a target=\"_blank\" href=\"http://course.trustie.net/projects/671/repository/revisions/master/show/spec\"><strong><span style=\"font-size:16px;color:#FF9900;\">http://course.trustie.net/projects/671/repository/revisions/master/show/spec</span></strong></a></em> \r\n	</p>\r\n	<p>\r\n		<br />\r\n	</p>\r\n</div>\r\n<p>\r\n	<br />\r\n</p>\r\n<br />\r\n<br />\r\n<br />\r\n<br />\r\n<br />\r\n<br />','','guestbook','1','-1','page','0','2','1','n','n','n','y','y','','','0');
INSERT INTO emlog_blog VALUES('39','【博客文摘】十年学会程序设计','1444216324','<strong><span style=\"font-size:18px;\">为何大家如此匆忙?</span></strong> \r\n<p>\r\n	走进任何一家书店，你会看到书架上一排不见尽头的放着如 《7天自学Java语言》以及几天或者几小时学会Windows, 因特网或者Visual Basic这类书。我在<a href=\"http://www.amazon.com/\" title=\"Amazon\">Amazon</a>网上书店用以下的方式进行<a href=\"http://www.amazon.com/exec/obidos/tg/browse/-/468558/104-5938873-6579160\" title=\"高级搜索\">高级搜索</a>:\r\n</p>\r\n<p>\r\n	<a href=\"http://www.amazon.com/exec/obidos/search-handle-url/ix=books&amp;rank=%2Bfeaturedrank&amp;fqp=power%01pubdate%3A%20after%201992%20and%20title%3A%20days%20and%0D%20%28title%3A%20learn%20or%20title%3A%20teach%20yourself%29&amp;sz=25&amp;pg=1/ref=s_b_np\" title=\"出版年份: 1992以后 书名包括：“天” 和 “学习” 或 “自学”\">出版年份: 1992以后　书名包括：“天” 和 “学习” 或“自学”</a> \r\n</p>\r\n<p>\r\n	得到了268条搜索结果，其中前78条都是计算机书（第79条是<a href=\"http://www.amazon.com/exec/obidos/ASIN/0781802245/\" title=\"30天学会孟加拉语\">30天学会孟加拉语</a>）。\r\n</p>\r\n<p>\r\n	我用“<a href=\"http://www.amazon.com/exec/obidos/search-handle-url/ix=books&amp;rank=%2Bfeaturedrank&amp;fqp=power%01pubdate%3A%20after%201992%20and%20title%3A%20hours%20and%0D%20%28title%3A%20learn%20or%20title%3A%20teach%20yourself%29&amp;sz=25&amp;pg=3/ref=s_b_np\" title=\"小时\">小时</a>”代替“天”作为关键字，得到了神奇般类似的结果：这次有253本书，前77本是计算机书，第78本是<a href=\"http://www.amazon.com/exec/obidos/ASIN/0028638999/\" title=\"24小时自学语法和写作风格\">24小时自学语法和写作风格</a>。排名前200的书中有96%是计算机书。\r\n</p>\r\n<p>\r\n	由此可见，人们要不就是急着想学会计算机，要不就是计算机相比于其他事情太容易学会了。比如说把，没有书是写在几天弹奏贝多芬或几天学会量子物理，甚至也没有几天学会帮小狗打扮这样的书。\r\n</p>\r\n<p>\r\n	让我们分析一下<a href=\"http://www.amazon.com/exec/obidos/ISBN=1556225679/4094-7934802-027992\" title=\"三天学会Pascal语言\">三天学会Pascal语言</a> [英文网页] 这样的标题表达了什么意思：\r\n</p>\r\n<h2>\r\n	1. 学会:\r\n</h2>\r\n<p>\r\n	在三天内，你没有时间去写几个有意义的程序，或者从成功和失败中学到东西。你也没时间跟有经验的程序员一起工作，所以也无法了解在真正编程是什么样\r\n子。简短的说，就学会而言，时间显然不够。所以这些书只是浮于表面的熟悉，而不是深刻的理解。如同Alexander Pope \r\n所说，一知半解是危险的。\r\n</p>\r\n<h2>\r\n	2. Pascal 语言:\r\n</h2>\r\n<p>\r\n	三天内你可能学会Pasacl语言的语法（如果你已经掌握一个类似的编程语言），但你无法学会如何合理运用这些语法。简言之，如果你是个Basic\r\n程序员，你可以用Pascal 语言写出类似Basic风格的程序，但你学不到Pascal语言的优点（还有缺点）到底在哪。重点是什么呢?\r\n</p>\r\n<p>\r\n	<a href=\"http://www-pu.informatik.uni-tuebingen.de/users/klaeren/epigrams.html\" title=\"Alan Perlis\">Alan Perlis</a>曾说: “如果编程语言不能影响你的编程思维，那就不值得去学.”\r\n</p>\r\n<p>\r\n	另一个可能是，你必须学会一点点Pascal语言（或是像VB语言、Javascript等），因为你需要跟现成的工具组合完成特定的工作。不过这个时候，你实际上学的不是怎么写程序，而是要学着如何完成工作。\r\n</p>\r\n<h2>\r\n	3. 三天\r\n</h2>\r\n<p>\r\n	不幸的是三天根本不够；下面的章节会告诉你为什么\r\n</p>\r\n<h1>\r\n	十年学会程序设计\r\n</h1>\r\n<p>\r\n	研究者 <a href=\"http://www.amazon.com/exec/obidos/ASIN/0805803092\" title=\"Hayes\">Hayes</a>, <a href=\"http://www.amazon.com/exec/obidos/ASIN/034531509X/\" title=\"Bloom\">Bloom</a> 的研究表明，在几乎所有的各种领域，大约要十年才能培养出专业技能。这些领域包括下西洋棋、音乐作曲、绘画、钢琴、游泳、网球，及神经心理学和数学拓扑学。似乎没有真正的捷径－－即便是莫扎特在四岁就展露出音乐天才，在他写出世界级的音乐之前仍然用了超过十三年的时间。\r\n</p>\r\n<p>\r\n	再看另一种类型的领域。披头士乐团似乎是在１９６４年的Ed Sullivan剧场表演突然地火起来并成为第一乐队的。但其实他们从 1957年开始，就在利物浦、汉堡等地的小型俱乐部表演。虽然他们很早就显现强大的吸引力，但他们决定性的成功作品<span style=\"font-style:italic;\"> Sgt Pepper</span> 也到1967年才发行。Samuel Johnson则认为或许还不止十年才行，他说：任何领域的卓越成就都必须用一生的努力才能取得;稍微低一点的代价都是换不到的。Chaucer 则感叹道：“生命如此短促，学习技艺却要这么地长”\r\n</p>\r\n<p>\r\n	以下是我在编程上成功的秘诀:\r\n</p>\r\n<ul>\r\n	<li>\r\n		对编程产生感兴趣并因为乐趣而写程序。确信你自始至终都能乐在其中，这样你才愿意将十年光阴投入编程事业.\r\n	</li>\r\n	<li>\r\n		与其他程序员交流；阅读别人的代码。这比任何书任何培训都重要。\r\n	</li>\r\n	<li>\r\n		不断地编写。最好的学习方法是<a href=\"http://www.engines4ed.org/hyperbook/nodes/NODE-120-pg.html\" title=\"在实践中学习\">在实践中学习</a>。从技术角度说，”在特定领域的个人最高效率并不因为经验够多就会自动获得；但若有意识的通过努力去提升经验，个人效率会变高”（<a href=\"http://www2.umassd.edu/swpi/DesignInCS/expertise.html\" title=\"第336页\">第336页</a>）而“高效的学习一般需要明确的任务和因人而异的适当难度，以及及时的反馈和重复或者修正错误的机会”（20～21页）<a href=\"http://www.amazon.com/exec/obidos/ASIN/0521357349\" title=\"Cognition in Practice: Mind, Mathematics, and Culture in Everyday Life\">Cognition in Practice: Mind, Mathematics, and Culture in Everyday Life</a>（<span style=\"font-style:italic;\">实践中认知：心智、数学与日常文化</span>）是这个观点的一本有趣参考书籍。\r\n	</li>\r\n	<li>\r\n		如果你愿意，你可以去读四年大学（或再读研究生）。这可以让你满足一些工作的学历要求，同时也可让你对这个领域有更深的认识。但如你不喜欢上学，\r\n你也能（得有牺牲）通过工作获得类似的经验。无论如何，只读书是不够的。《New Hacker’s Dictionary》的作者Eric \r\nRaymond 曾经说过:“计算机的教育无法让人成为编程的专家，正如研究画笔与颜料不能让人成为专业画家一样.” \r\n一个在我所有招聘过的人中属于最优秀之一的程序员只有高中毕业，但他写出很多<a href=\"http://www.xemacs.org/\" title=\"很棒\">很棒</a>的<a href=\"http://www.mozilla.org/\" title=\"程序\">程序</a>，他甚至有自己的<a href=\"http://groups.google.com/groups?q=alt.fan.jwz&amp;meta=site%3Dgroups\" title=\"新闻组\">新闻组</a>。他获得的股票期权使得他可以拥有自己的<a href=\"http://http/en.wikipedia.org/wiki/DNA_Lounge\" title=\"午夜酒吧\">午夜酒吧</a>.\r\n	</li>\r\n	<li>\r\n		跟其他程序员一起完成项目。在一些项目中成为最好的程序员；在一些中则充当最差的一个。当你是最佳的，你要测试自己领导项目的能力，并以你的能力鼓励他人。当你是最差的，要看看高手做些什么，他们不喜欢做什么(因为他们会叫你去帮他们做).\r\n	</li>\r\n	<li>\r\n		接手别的程序员完成项目。全心投入并理解别人的程序。当原作者不在的时候，看看在理解与修改时有什么要注意的。想想如何设计你的程序使得后来维护的人容易上手。\r\n	</li>\r\n	<li>\r\n		至 少学会六门编程语言。一种要支持类/对象(class abstractions)的语言,如Java或C++; \r\n一种函数式(functional abstraction)语言, 如 LISP 或 ML;一种支持语法抽象(syntactic \r\nabstraction) 的语言 如 LISP;一种声明式语言, 如Prolog或 C++模版; 一种支持协同式(coroutines)编程,如\r\n Icon 或 Scheme; 还有一种支持并行(parallelism)的语言, 如 Sisal.\r\n	</li>\r\n	<li>\r\n		记住在 “计算机科学” 中包括”计算机”这个词。要知道你的计算机执行一条指令需要多久，到内存中取一个字需要多久(缓存是否击中),到磁盘读取连续的字需要多久，而磁盘的定位又需要多久. (解答见文末)\r\n	</li>\r\n	<li>\r\n		进行语言标准化的工作。可以像是由ANSI　C++委员会，或由你自己的团队，来决定你们的编码风格，譬如说缩排是2或4个空格。不管怎样，你都能学到别人到底喜欢什么，对语言的感受有多深，甚至能了解到一点他们为什么有这样的感觉。\r\n	</li>\r\n	<li>\r\n		并具备良好的判断力，也别老纠缠在语言标准化上.\r\n	</li>\r\n</ul>\r\n<p>\r\n	谈了上面所有的想法后，我不禁要问究竟能从书上学到多少。在第一个孩子出生前，我读完了所有的“怎样…”的书，仍觉得自己是个一无所知的（照顾孩子的）菜鸟。30个月后，第二个孩子出世，我要重回这些书好好复习么?\r\n</p>\r\n<p>\r\n	不!取而代之的是，我开始相信自己的个人经验。这些难得的经验，比专家写的几千页手册还要有用，而且让我重新找到了自信.\r\n</p>\r\n<p>\r\n	Fred Brooks (译注: 《人月神话》作者) 在他的文章<a href=\"http://citeseer.nj.nec.com/context/7718/0\" title=\"没有银弹\">没有银弹</a>中指出，发掘卓越软体设计者的三部曲：\r\n</p>\r\n<ol>\r\n	<li>\r\n		尽早尽可能地以系统化的方式发掘最佳设计人员。\r\n	</li>\r\n	<li>\r\n		给有潜力者指派生涯规划师，并谨慎地规划他们的职业生涯。\r\n	</li>\r\n	<li>\r\n		提供机会给正在成长的程序员，让他们能相互影响，彼此激励。\r\n	</li>\r\n</ol>\r\n<p>\r\n	这里假定了某些人已具备成为卓越设计师的必要潜能；工作只是诱导他们前进。 <a href=\"http://www-pu.informatik.uni-tuebingen.de/users/klaeren/epigrams.html\" title=\"Alan Perlis\">Alan Perlis</a> 说得更简洁了，你可以教任何人学雕塑，但对米开朗基罗而言，要教他的反倒是有哪些事不要做, 卓越的程序员也一样。\r\n</p>\r\n<p>\r\n	所以，尽管买那些 Java书吧！你或许能从中找到点有用的，但是在24小时，几天或者几个月中，这些都不会改变你的人生，你也不能掌握一个真正的程序员应该具备的真正的综合的技能。\r\n</p>\r\n<h1>\r\n	参考文献\r\n</h1>\r\n<ul>\r\n	<li>\r\n		Bloom, Benjamin (ed.) <a href=\"http://www.amazon.com/exec/obidos/ASIN/034531509X\">Developing Talent in Young People</a>, Ballantine, 1985.\r\n	</li>\r\n	<li>\r\n		Brooks, Fred, <a href=\"http://citeseer.nj.nec.com/context/7718/0\">No Silver Bullets</a>, IEEE Computer, vol. 20, no. 4, 1987, p. 10-19.\r\n	</li>\r\n	<li>\r\n		Hayes, John R., <a href=\"http://www.amazon.com/exec/obidos/ASIN/0805803092\">Complete Problem Solver</a> Lawrence Erlbaum, 1989.\r\n	</li>\r\n	<li>\r\n		Lave, Jean, <a href=\"http://www.amazon.com/exec/obidos/ASIN/0521357349\">Cognition in Practice: Mind, Mathematics, and Culture in Everyday Life</a>, Cambridge University Press, 1988.\r\n	</li>\r\n</ul>\r\n<h1>\r\n	解答\r\n</h1>\r\n<p>\r\n	各种操作的时间，以2001年夏季，典型配置的 1GHz 个人计算机为标准：\r\n</p>\r\n<table>\r\n	<thead>\r\n		<tr>\r\n			<th>\r\n				命令\r\n			</th>\r\n			<th>\r\n				速度\r\n			</th>\r\n		</tr>\r\n	</thead>\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				执行单一指令\r\n			</td>\r\n			<td>\r\n				1 纳秒\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				从L1 高速缓存取一个字\r\n			</td>\r\n			<td>\r\n				2 纳秒\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				从内存取一个字\r\n			</td>\r\n			<td>\r\n				10 纳秒\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				从磁盘取连续存放的一个字\r\n			</td>\r\n			<td>\r\n				200 纳秒\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				磁盘寻址并取字\r\n			</td>\r\n			<td>\r\n				8 毫秒\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<h1>\r\n	附录 I: 语言的选择\r\n</h1>\r\n<p>\r\n	好几个人问过我一开始应该先学哪个计算机编程语言，这个问题没有唯一的答案，不过选择的时候可以从以下的几个方面考虑：\r\n</p>\r\n<ul>\r\n	<li>\r\n		朋友在用的. \r\n当人们问我:”我该用什么操作系统的时候”，我通常的回答是：“用你朋友用的”.这样的好处是从朋友那儿学习可以弥补复杂的操作系统差异或者编程语言差异\r\n(给你造成的困惑)。这里也要考虑你未来的朋友：如果你一直使用的话，开发社区会是你的朋友。你选择了一个具有有巨大的增长的开发社区还是一个小的快消失\r\n的开发社区的语言?它有相关的书，网站和论坛可以获取解答么?你喜欢那些论坛上的人么?\r\n	</li>\r\n	<li>\r\n		<span style=\"font-weight:bold;\">保持简单.</span>诸如C++和Java是为那些关心代码执行效率的有经验的大型团队的开发人员设计的。因此这些语言中有些为这些特殊场合设计的部件。你只是关心编程而不需要关心复杂情况。你需要一个为新学编程的人设计的容易学习和理解的语言。\r\n	</li>\r\n	<li>\r\n		<span style=\"font-weight:bold;\">实践.</span>什么是学习钢琴的好方法呢?<br />\r\n是一边听音一边弹奏的“交互式”的方法呢，还是全听完整首歌然后再弹奏的那种“批处理”方式呢? 很显然,交互学习的方式能够让学钢琴变得简单–这也适用于编程.选取一种交互式的编程语言并且坚持使用.\r\n	</li>\r\n</ul>\r\n<p>\r\n	基于以上的这些标准，我对于第一次接触编程的人推荐 Python 或 Scheme.但是情况各有不同，或许也有其他的选择. 如果你不满10岁，你可能会喜欢Alice或者Squeak(年龄大的人或许也喜欢这些). 重要的是在选择后,立即开始学习和使用.\r\n</p>\r\n<h1>\r\n	附录II: 书和其他资源:\r\n</h1>\r\n<p>\r\n	很多人问我该从什么书或者什么网页开始看起。我重申一句：“仅仅看书是不够的”，不过我也推荐一下的一些：\r\n</p>\r\n<ul>\r\n	<li>\r\n		<strong>Scheme:</strong> <a href=\"http://www.amazon.com/gp/product/0262011530\">计算机程序的结构和释义 (Abelson &amp; Sussman)</a> 可能是计算机科学最好的导论了, 他还告诉程序员怎么理解计算机科学,你可以访问这本书的 <a href=\"http://www.swiss.ai.mit.edu/classes/6.001/abelson-sussman-lectures/\">在线视频讲座</a> 和 <a href=\"http://mitpress.mit.edu/sicp/full-text/book/book.html\">全文在线</a> .这本书也很有挑战性，可能会排除掉一些可能在其他领域成功的人.\r\n	</li>\r\n	<li>\r\n		<strong>Scheme:</strong> <a href=\"http://www.amazon.com/gp/product/0262062186\">怎样设计程序(Felleisen <span style=\"font-style:italic;\">等</span>)</a> 是讲解怎样设计优雅的函数式语言的最好的书之一.\r\n	</li>\r\n	<li>\r\n		<strong>Python:</strong> <a href=\"http://www.amazon.com/gp/product/1887902996\">Python 编程，面向计算机科学的导论(Zelle)</a> 是用Python介绍计算机科学的好书.\r\n	</li>\r\n	<li>\r\n		<strong>Python:</strong> 一些关于Python的 <a href=\"http://wiki.python.org/moin/BeginnersGuide\">入门教程</a> 可以在 <a href=\"http://python.org/\">Python.org</a> 上找到.\r\n	</li>\r\n	<li>\r\n		<strong>Oz:</strong> <a href=\"http://www.amazon.com/gp/product/0262220695\">计算机编程的概念，技术和模型(Van Roy &amp; Haridi)</a> 可以视为第一本书的现代版.他是关于编程的一些总揽，包含了比第一本书更加广泛也更加容易阅读和理解的领域。这本书使用了一个不太为人所知的编程语言叫Oz, 不过这个可以作为学习其他编程语言的一个基础。\r\n	</li>\r\n</ul>\r\n<p>\r\n	注: T. Capey 指出，在Amazon 的 问题彻底解决者的页面上购买了这本书的人还买了: “21天学孟加拉语” 和 “自学语法和写作风格”这两本书，我估计大部分是我这个页面带过去的用户.\r\n</p>\r\n<ul>\r\n	<li>\r\n		<a href=\"http://www.norvig.com/index.html\" title=\"Peter Norvig (Copyright 2001)\">Peter Norvig (Copyright 2001)</a> \r\n	</li>\r\n	<li>\r\n		<a href=\"http://www.youxu.info/\" title=\"Eric You XU\">Eric You XU</a> 翻译，2007年4月\r\n	</li>\r\n</ul>\r\n<p>\r\n	<br />\r\n</p>\r\n<hr />\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	这是一篇转载文章。\r\n</p>\r\n<ul>\r\n	<li>\r\n		<a href=\"http://www.norvig.com/index.html\">Peter Norvig (Copyright 2001)</a> \r\n	</li>\r\n	<li>\r\n		<a href=\"http://www.norvig.com/21-days.html\">原文网址</a> \r\n	</li>\r\n	<li>\r\n		<a href=\"http://blog.youxu.info/21-days/\">中译文出处</a> \r\n	</li>\r\n</ul>\r\n<hr />\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	版权声明：本站文章若为转载均已注明出处，感谢原作者的分享，如有不妥请联系我。否则均为原创，未经博主允许不得私自转载。\r\n</p>','','','1','11','blog','12','0','0','n','n','n','y','y','','','0');
INSERT INTO emlog_blog VALUES('40','【ubuntu使用笔记】ubuntu12.04 上如何安装Ruby On Rails','1444216950','<p>\r\n	英文原文：<a href=\"http://zengrong.net/post/%5C%22https://www.digitalocean.com/community/articles/how-to-install-ruby-on-rails-on-ubuntu-12-04-lts-precise-pangolin-with-rvm%5C%22\">How To Install Ruby on Rails on Ubuntu 12.04 LTS (Precise Pangolin) with RVM</a> \r\n</p>\r\n<p>\r\n	中文译文：http://zengrong.net/post/1933.htm\r\n</p>\r\n<h2>\r\n	关于Ruby on Rails\r\n</h2>\r\n<p>\r\n	Ruby on Rails是一个快速创建web程序的框架。\r\n</p>\r\n<p>\r\n	在一个虚拟服务器上安装Ruby on Rails需要花点时间，不过幸运的是，有个有用的工具能帮我们简化安装过程。\r\n</p>\r\n<h2>\r\n	第一步——用RVM安装Ruby\r\n</h2>\r\n<p>\r\n	在开始之前，我们需要在VPS上运行一次快速更新，确保所有的包都是最新的：\r\n</p>\r\n<pre lang=\"\\&quot;BASH\\&quot;\">sudo apt-get update\r\n</pre>\r\n<p>\r\n	更新完毕之后，我们可以开始安装RVM(Ruby Version Manager). 这是个让我们能在一台服务器上使用多个Ruby版本的优秀程序。不过，我们将只使用它来安装最新的Ruby版本。<span id=\"more-1933\"></span> \r\n</p>\r\n<p>\r\n	如果在你的系统上没有 curl ，你需要安装它：\r\n</p>\r\n<pre lang=\"\\&quot;BASH\\&quot;\">sudo apt-get install curl\r\n</pre>\r\n<p>\r\n	要安装RVM，在终端中输入下面的命令：\r\n</p>\r\n<pre lang=\"\\&quot;BASH\\&quot;\">curl -L https://get.rvm.io | bash -s stable\r\n</pre>\r\n<p>\r\n	安装完毕后，载入RVM。你需要先退出shell然后重新开启一个新的shell会话。\r\n</p>\r\n<pre lang=\"\\&quot;BASH\\&quot;\">source ~/.rvm/scripts/rvm\r\n<pre class=\"prettyprint lang-html linenums\">&lt;B&gt;<front color=\"red\">（云中布衣注：此处为载入RVM，很重要的喔，每次打开一次shell都要重新载入RVM~）</front>&lt;/B&gt;</pre>\r\n</pre>\r\n<p>\r\n	(zrong: 我没有退出和重启shell会话，所以我使用的是 <code>source /usr/local/rvm/scripts/rvm</code> ）\r\n</p>\r\n<p>\r\n	为了能正常工作，RMV必须安装一些依赖。你可以让RVM自动安装它们：\r\n</p>\r\n<pre lang=\"\\&quot;BASH\\&quot;\">rvm requirements\r\n</pre>\r\n<p>\r\n	你可能需要输入root密码来允许安装这些依赖。\r\n</p>\r\n<p>\r\n	某些情况下，系统可能会提示找不到zlib包。RVM 页面详细描述了<a href=\"http://zengrong.net/post/%5C%22https://rvm.io/packages/zlib%5C%22\">这个问题以及解决方法</a>。\r\n</p>\r\n<h2>\r\n	第二步——安装 Ruby\r\n</h2>\r\n<p>\r\n	一旦你使用了 RVM，安装Ruby就简单了。\r\n</p>\r\n<pre lang=\"\\&quot;BASH\\&quot;\">rvm install ruby\r\n</pre>\r\n<p>\r\n	最新版本的的Ruby现在已经被安装。不过，因为我们会同时使用多个 Ruby 版本，我们需要告诉系统应该使用刚才安装的 Ruby 作为默认版本。\r\n</p>\r\n<pre lang=\"\\&quot;BASH\\&quot;\">rvm use ruby --default\r\n</pre>\r\n<p>\r\n	(zrong: RVM并不能管理系统自带的Ruby，如果想返回系统管理的Ruby，可以执行 <code>rvm reset</code> ）\r\n</p>\r\n<h2>\r\n	第三步——安装 RubyGems\r\n</h2>\r\n<p>\r\n	确认我们已经安装了 Ruby on Rails 所需的所有组件。现在可以继续使用 RVM 安装 gems。在终端中输入下面的命令：\r\n</p>\r\n<pre lang=\"\\&quot;BASH\\&quot;\">rvm rubygems current\r\n</pre>\r\n<h2>\r\n	第四步——安装 Rails\r\n</h2>\r\n<p>\r\n	万事俱备，是安装 Rails 的时候了。打开终端输入下面的命令：\r\n</p>\r\n<pre lang=\"\\&quot;BASH\\&quot;\">gem install rails\r\n</pre>\r\n<p>\r\n	这次安装需要一些时间和耐心。它将安装 Ruby on Rails 到你的服务器上。\r\n</p>\r\n<h2>\r\n	更多\r\n</h2>\r\n<p>\r\n	在服务器上安装了 Ruby on Rails之后，你就可以开始为你的站点创建 <a href=\"http://zengrong.net/post/%5C%22https://www.digitalocean.com/community/articles/how-to-create-a-ssl-certificate-on-apache-for-ubuntu-12-04%5C%22\">SSL证书</a> 或者安装 <a href=\"http://zengrong.net/post/%5C%22https://www.digitalocean.com/community/articles/how-to-set-up-vsftpd-on-ubuntu-12-04%5C%22\">FTP服务器</a> 了。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	点评：很好用～\r\n</p>','','','1','13','blog','27','0','0','n','n','n','y','y','','','0');
INSERT INTO emlog_blog VALUES('41','【ubuntu使用笔记】GitHub使用指南!(ubuntu)','1444239635','<p align=\"center\">\r\n	<span style=\"font-family:宋体, serif;\"> Github </span>使用指南！（下文针对<span style=\"font-family:宋体, serif;\">linux</span>系统而言，特指<span style=\"font-family:宋体, serif;\">ubuntu</span>系统）\r\n</p>\r\n<p>\r\n	第一步：\r\n</p>\r\n<p>\r\n	<span style=\"font-family:宋体, serif;\"> </span>下载安装<span style=\"font-family:宋体, serif;\">Git</span>。\r\n</p>\r\n<ol>\r\n	<li>\r\n		&nbsp;\r\n		<ol>\r\n			<li>\r\n				&nbsp;\r\n				<ol>\r\n					<li>\r\n						<p>\r\n							使用新立得软件包管理工具<span style=\"font-family:宋体, serif;\">(Synaptic\r\n			Package Manager)</span>安装最新版本的<span style=\"font-family:宋体, serif;\">git</span>。\r\n						</p>\r\n						<p>\r\n							推荐选择安装<span style=\"font-family:宋体, serif;\">git-core,git-gui,git-doc</span>。\r\n						</p>\r\n					</li>\r\n				</ol>\r\n			</li>\r\n		</ol>\r\n	</li>\r\n</ol>\r\n<p>\r\n	第二步：设置<span style=\"font-family:宋体, serif;\">SSH\r\nKeys</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:宋体, serif;\"> github</span>使用<span style=\"font-family:宋体, serif;\">ssh\r\nkeys</span>来确保你的电脑与<span style=\"font-family:宋体, serif;\">github</span>的连接有安全性。设置过程很简单。\r\n</p>\r\n<p>\r\n	<span style=\"font-family:宋体, serif;\"> </span>但是有几个步骤。\r\n</p>\r\n<p>\r\n	<span style=\"font-family:宋体, serif;\"> </span>步骤一：检查已有的<span style=\"font-family:宋体, serif;\">ssh\r\nkeys</span>。\r\n</p>\r\n<p>\r\n	<span style=\"font-family:宋体, serif;\"> <span style=\"color:#808000;\"> </span><span style=\"color:#808000;\"><span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\"><span><span>$\r\ncd ~/.ssh</span></span></span></span></span></span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#666666;\"> </span><span style=\"color:#000000;\">如果提示说，没有这样的文件或者目录。<span style=\"font-family:宋体, serif;\"><span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\"><span><span>(No\r\nsuch file or directory) </span></span></span></span></span></span><span style=\"color:#000000;\">则跳到步骤三。否<span style=\"font-family:宋体, serif;\"><span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\"><span><span> </span></span></span></span></span></span><span style=\"color:#000000;\">则继续。</span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#666666;\"> </span><span style=\"color:#000000;\"> 步骤二：备份已有的<span style=\"font-family:宋体, serif;\"><span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\"><span><span>ssh\r\nkeys</span></span></span></span></span></span><span style=\"color:#000000;\">。</span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#000000;\"> 步骤三：产生一个新的 <span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">ssh\r\nkey</span></span>。</span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#000000;\"><span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\"> </span></span>我的产生过程如下：</span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#808000;\"><span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\"> banxi1988@banxi:~$\r\nssh-keygen -t rsa -C \"banxi1988@gmail.com\" </span></span></span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#808000;\"><span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">Generating\r\npublic/private rsa key pair. </span></span></span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#808000;\"><span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">Enter\r\nfile in which to save the key (/home/banxi1988/.ssh/id_rsa): </span></span></span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#808000;\"><span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">Enter\r\npassphrase (empty for no passphrase): </span></span></span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#808000;\"><span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">Enter\r\nsame passphrase again: </span></span></span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#808000;\"><span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">Passphrases\r\ndo not match.  Try again. </span></span></span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#808000;\"><span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">Enter\r\npassphrase (empty for no passphrase): </span></span></span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#808000;\"><span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">Enter\r\nsame passphrase again: </span></span></span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#808000;\"><span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">Your\r\nidentification has been saved in /home/banxi1988/.ssh/id_rsa. </span></span></span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#808000;\"><span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">Your\r\npublic key has been saved in /home/banxi1988/.ssh/id_rsa.pub. </span></span></span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#808000;\"><span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">The\r\nkey fingerprint is: </span></span></span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#808000;\"><span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">0c:a5:4d:a7:d8:e8:42:b2:5c:75:d0:4c:e5:ff:6a:7f\r\nbanxi1988@gmail.com </span></span></span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#808000;\"><span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">The\r\nkey\'s randomart image is: </span></span></span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#808000;\"><span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">+--[\r\nRSA 2048]----+ </span></span></span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#808000;\"><span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">|\r\n     o==.o      | </span></span></span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#808000;\"><span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">|\r\n    . Xo+       | </span></span></span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#808000;\"><span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">|\r\n . o = + .      | </span></span></span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#808000;\"><span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">|\r\n. = . o   .     | </span></span></span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#808000;\"><span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">|\r\n o . . S   .    | </span></span></span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#808000;\"><span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">|\r\n    .       .   | </span></span></span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#808000;\"><span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">|\r\n             .  | </span></span></span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#808000;\"><span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">|\r\n           ..  E| </span></span></span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#808000;\"><span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">|\r\n          ..... | </span></span></span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#808000;\"><span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">+-----------------+ </span></span></span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#808000;\"><span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">banxi1988@banxi:~$ </span></span></span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#000000;\"><span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\"> </span></span></span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#000000;\"><span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\"> </span></span>步骤四：</span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#000000;\"><span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\"> </span></span>在<span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">gitHub</span></span>里单击账户设置。点击<span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">SSH </span></span>公钥 ，点击添加另一个公钥。</span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#000000;\">打开前面生成的<span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">id_rsa.pub </span></span>文件。（可能要显示隐藏文件才能看到用<span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">gedit</span></span>或者其它的编辑器打开。</span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#000000;\">注意不要编辑，添加空格或者换行。）</span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#000000;\">然后把里面的内容复制粘贴到下面的<span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">key</span></span>输入栏中。</span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#000000;\"> 步骤五：测试一下。</span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#808000;\"><span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">banxi1988@banxi:~$\r\nssh git@github.com </span></span></span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#808000;\"><span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">/etc/ssh/ssh_config:\r\nline 20: Bad configuration option: X11Forwrding </span></span></span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#808000;\"><span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">/etc/ssh/ssh_config:\r\nterminating, 1 bad configuration options </span></span></span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#808000;\"><span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">banxi1988@banxi:~$ </span></span></span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#000000;\"><span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\"> After\r\nmodify the ssh_config.</span></span>中间要求输入上面输入的<span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">passphrase</span></span>。</span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#808000;\"><span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">banxi1988@banxi:~$\r\nssh git@github.com </span></span></span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#808000;\"><span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">The\r\nauthenticity of host \'github.com (207.97.227.239)\' can\'t be\r\nestablished. </span></span></span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#808000;\"><span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">RSA\r\nkey fingerprint is 16:27:ac:a5:76:28:2d:36:63:1b:56:4d:eb:df:a6:48. </span></span></span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#808000;\"><span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">Are\r\nyou sure you want to continue connecting (yes/no)? yes </span></span></span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#808000;\"><span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">Warning:\r\nPermanently added \'github.com,207.97.227.239\' (RSA) to the list of\r\nknown hosts. </span></span></span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#808000;\"><span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">PTY\r\nallocation request failed on channel 0 </span></span></span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#808000;\"><span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">Hi\r\nbanxi1988! You\'ve successfully authenticated, but GitHub does not\r\nprovide shell access. </span></span></span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#808000;\"> <span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">Connection\r\nto github.com closed. </span></span></span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#808000;\"><span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">banxi1988@banxi:~$ </span></span></span> \r\n</p>\r\n<p>\r\n	&nbsp;\r\n</p>\r\n<p>\r\n	<span style=\"color:#000000;\">步骤六：设置<span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">git</span></span>的个人信息。 </span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#000000;\"><span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\"> 1. </span></span>设置用户名和<span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">email</span></span>。<span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">git</span></span>使用这个来验证每个提交者。除此之外<span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">github</span></span>会使用这些信息来 </span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#000000;\"><span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\"> </span></span>与你的<span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">github</span></span>账号相联系。下面的名字应该是你真实的名字。而不是要<span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">GitHub</span></span>的用户名。 </span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#808000;\"><span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\"> banxi1988@banxi:~$\r\ngit config --global user.name \"Li HaiZhen\" </span></span></span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#808000;\"> <span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">banxi1988@banxi:~$\r\ngit config --global user.email \"banxi1988@gmail.com\" </span></span></span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#808000;\"> <span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">banxi1988@banxi:~$ </span></span></span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#000000;\"> <span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">2. </span></span>设置<span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">GitHub</span></span>的指令环。 </span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#000000;\"> <span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\"> </span></span>有些工具不使用<span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">ssh</span></span>来连接。为了使用这些工具。你应该找出配置好你的<span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">API\r\nToken</span></span>。 </span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#000000;\"> <span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\"> </span></span>在<span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">GitHub</span></span>上点击。账号设置（<span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">Account\r\nSettings) </span></span>，然后点击 账号管理<span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">(Account\r\nAdmin)</span></span>。 </span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#000000;\"> <span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\"> </span></span>在<span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">API\r\nToken</span></span>一栏里有你的<span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">API\r\nToKen</span></span>。 </span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#000000;\"> <span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\"> </span></span>在命令行下运行下面的代码。 </span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#000000;\"> <span style=\"color:#808000;\"> <span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\"> banxi1988@banxi:~$\r\ngit config --global github.user banxi1988 </span></span></span></span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#808000;\"> <span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\"> banxi1988@banxi:~$\r\ngit config --global github.token e5ebe68d43d9820ed8d05a3d2633d7f3 </span></span></span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#808000;\"><span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\"> banxi1988@banxi:~$ </span></span></span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#000000;\"><span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\"> </span></span>上面使用的<span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">user</span></span>是指<span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">GitHub</span></span>的用户名了。 </span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#000000;\"><span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\"> </span></span></span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#000000;\">最后：恭喜你！你已经正确的设置了<span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">git</span></span>和<span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">gitHub</span></span>。 </span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#000000;\"><span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\"> </span></span>接下来。要做的就是。<span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">1. </span></span>创建一个仓库。<span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">2.\r\nFork </span></span>一个仓库。 <span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">3.</span></span>社区化。</span> \r\n</p>\r\n<p>\r\n	&nbsp;\r\n</p>\r\n<p>\r\n	&nbsp;\r\n</p>\r\n<p>\r\n	&nbsp;\r\n</p>\r\n<p>\r\n	<span style=\"color:#000000;\">第二节<span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">:</span></span>创建一个仓库<span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">(Create\r\nA Repo  Repositories)</span></span></span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#000000;\"><span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\"> </span></span>直接在自己的登录后进入<span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">github.com</span></span>首页就可以看到<span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">, </span></span>下面一栏有四步<span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">.</span></span>用来创建<span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">Repository.</span></span></span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#000000;\"><span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\"> </span></span>直接填入项目名称就可以了<span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">.</span></span>其它的可以不填<span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">.</span></span>要填<span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">,</span></span>这个表单也足够自解释了<span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">.</span></span></span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#000000;\"> 创建后之后<span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">.</span></span>会跳转到一个页面<span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">.</span></span>其中有指示接下来该怎么做的<span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">.</span></span></span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#000000;\">如下<span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">:</span></span></span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#000000;\"><span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\"><a href=\"mailto:git@github.com\">git@github.com</a>:banxi1988/tasteHibernate.git</span></span></span> \r\n</p>\r\n<p>\r\n	&nbsp;\r\n</p>\r\n<p>\r\n	<span style=\"color:#000000;\"><span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\"> </span></span>接下来给你自己的项目创建一个基本的<span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">Readme</span></span>文件吧<span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">.</span></span></span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#000000;\"><span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\"> </span></span>详细操作过程如下<span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">:</span></span></span> \r\n</p>\r\n<h2 class=\"cjk\">\r\n	<a name=\"t0\"></a><span style=\"color:#000000;\"><span style=\"font-family:helvetica, arial, freesans, clean, sans-serif;\"><span style=\"font-size:x-small;\">Global\r\nsetup:</span></span></span> \r\n</h2>\r\n<pre name=\"code\" class=\"cjk\"><span style=\"color:#000000;\"> </span><span style=\"font-family:文泉驿等宽正黑, monospace;\"><span style=\"color:#000000;\"><span style=\"font-family:\'Bitstream Vera Sans Mono\', Courier, monospace;\"><span style=\"font-size:x-small;\"><span><span>Download and install </span></span></span></span></span><a href=\"http://git-scm.com/download\" target=\"_blank\"><span style=\"color:#4183c4;\"><span><span style=\"font-family:\'Bitstream Vera Sans Mono\', Courier, monospace;\"><span style=\"font-size:x-small;\"><span><span>Git</span></span></span></span></span></span></a></span> <span style=\"color:#ffffff;\"> <span style=\"color:#808000;\"> </span><span style=\"font-family:文泉驿等宽正黑, monospace;\"><span style=\"color:#808000;\"><span style=\"font-family:\'Bitstream Vera Sans Mono\', Courier, monospace;\"><span style=\"font-size:x-small;\"><span><span>git config --global user.name \"banxi1988\"</span></span></span></span></span></span></span> <span style=\"color:#808000;\"> <span style=\"font-family:文泉驿等宽正黑, monospace;\"><span style=\"font-family:\'Bitstream Vera Sans Mono\', Courier, monospace;\"><span style=\"font-size:x-small;\"><span><span>git config --global user.email banxi1988@gmail.com</span></span></span></span></span></span> <span style=\"color:#000000;\"> </span></pre>\r\n<h2 class=\"cjk\">\r\n	<a name=\"t1\"></a> <span style=\"color:#000000;\"><span style=\"font-family:helvetica, arial, freesans, clean, sans-serif;\"><span style=\"font-size:x-small;\">Next\r\nsteps:</span></span></span> \r\n</h2>\r\n<pre name=\"code\" class=\"cjk\"><span style=\"color:#808000;\"> \r\n<pre class=\"prettyprint lang-bsh linenums\">mkdir tasteHibernate  cd tasteHibernate  git init  touch README  git add README  git commit -m \'first commit\'  git remote add origin git@github.com:banxi1988/tasteHibernate.git  git push -u origin master  </pre>\r\n</span><span style=\"color:#000000;\"></span></pre>\r\n<h2 class=\"cjk\">\r\n	<a name=\"t2\"></a> <span style=\"color:#000000;\"><span style=\"font-family:helvetica, arial, freesans, clean, sans-serif;\"><span style=\"font-size:x-small;\">Existing\r\nGit Repo?</span></span></span> \r\n</h2>\r\n<pre name=\"code\" class=\"cjk\"><span style=\"color:#808000;\"> <span style=\"font-family:文泉驿等宽正黑, monospace;\"><span style=\"font-family:\'Bitstream Vera Sans Mono\', Courier, monospace;\"><span style=\"font-size:x-small;\"><span><span>cd existing_git_repo</span></span></span></span></span></span> <span style=\"color:#808000;\"> <span style=\"font-family:文泉驿等宽正黑, monospace;\"><span style=\"font-family:\'Bitstream Vera Sans Mono\', Courier, monospace;\"><span style=\"font-size:x-small;\"><span><span>git remote add origin git@github.com:banxi1988/tasteHibernate.git</span></span></span></span></span></span> <span style=\"color:#808000;\"> <span style=\"font-family:文泉驿等宽正黑, monospace;\"><span style=\"font-family:\'Bitstream Vera Sans Mono\', Courier, monospace;\"><span style=\"font-size:x-small;\"><span><span>git push -u origin master</span></span></span></span></span></span> <span style=\"color:#000000;\"> </span></pre>\r\n<h2 class=\"cjk\">\r\n	<a name=\"t3\"></a> <span style=\"color:#000000;\"><span style=\"font-family:helvetica, arial, freesans, clean, sans-serif;\"><span style=\"font-size:x-small;\">Importing\r\na Subversion Repo?</span></span></span> \r\n</h2>\r\n<pre name=\"code\" class=\"cjk\"><span style=\"color:#000000;\"> </span><span style=\"font-family:文泉驿等宽正黑, monospace;\"><a href=\"https://github.com/banxi1988/tasteHibernate/imports/new\"><span style=\"color:#4183c4;\"><span><span style=\"font-family:\'Bitstream Vera Sans Mono\', Courier, monospace;\"><span style=\"font-size:x-small;\"><span><span>Click here</span></span></span></span></span></span></a></span> <span style=\"color:#000000;\"> </span></pre>\r\n<h2 class=\"cjk\">\r\n	<a name=\"t4\"></a> <span style=\"color:#000000;\"><span style=\"font-family:helvetica, arial, freesans, clean, sans-serif;\"><span style=\"font-size:x-small;\">When\r\nyou\'re done:</span></span></span> \r\n</h2>\r\n<pre name=\"code\" class=\"cjk\"><span style=\"color:#000000;\"> </span><span style=\"font-family:文泉驿等宽正黑, monospace;\"><a href=\"https://github.com/banxi1988/tasteHibernate\"><span style=\"color:#4183c4;\"><span><span style=\"font-family:\'Bitstream Vera Sans Mono\', Courier, monospace;\"><span style=\"font-size:x-small;\"><span><span>Continue</span></span></span></span></span></span></a></span></pre>\r\n<p>\r\n	&nbsp;\r\n</p>\r\n<pre class=\"prettyprint lang-bsh linenums\">banxi1988@banxi:~/github/tasteHibernate$ git init\r\n\r\nInitialized empty Git repository in /home/banxi1988/github/tasteHibernate/.git/\r\n\r\nbanxi1988@banxi:~/github/tasteHibernate$ touch README\r\n\r\nbanxi1988@banxi:~/github/tasteHibernate$ vi README\r\n\r\nbanxi1988@banxi:~/github/tasteHibernate$ git add README\r\n\r\nbanxi1988@banxi:~/github/tasteHibernate$ git commit -m \'first commit\'\r\n\r\n[master (root-commit) 6ec8aae] first commit\r\n\r\n1 files changed, 6 insertions(+), 0 deletions(-)\r\n\r\ncreate mode 100644 README\r\n\r\nbanxi1988@banxi:~/github/tasteHibernate$ git remote add origin git@github.com:banxi1988/tasteHibernate.git\r\n\r\nbanxi1988@banxi:~/github/tasteHibernate$ git push origin master\r\n\r\nERROR: banxi1988/tasteHibernate.git doesn\'t exist. Did you enter it correctly?\r\n\r\nfatal: The remote end hung up unexpectedly\r\n\r\nbanxi1988@banxi:~/github/tasteHibernate$ git push -u origin master\r\n\r\nCounting objects: 3, done.\r\n\r\nDelta compression using up to 2 threads.\r\n\r\nCompressing objects: 100% (2/2), done.\r\n\r\nWriting objects: 100% (3/3), 383 bytes, done.\r\n\r\nTotal 3 (delta 0), reused 0 (delta 0)\r\n\r\nTo git@github.com:banxi1988/tasteHibernate.git\r\n\r\n* [new branch] master -&gt; master\r\n\r\nBranch master set up to track remote branch master from origin.\r\n\r\nbanxi1988@banxi:~/github/tasteHibernate$ </pre>\r\n<br />\r\n<p>\r\n	&nbsp;\r\n</p>\r\n<p>\r\n	<span style=\"color:#000000;\">关于<span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">Git</span></span>的命令请参见<span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">Git</span></span>手册<span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">.</span></span></span> \r\n</p>\r\n<p>\r\n	&nbsp;\r\n</p>\r\n<p>\r\n	<span style=\"color:#000000;\">现在我们已经可以创建了一个库了<span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">.</span></span>创建了一个文件<span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">,</span></span>并且提交了<span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">.</span></span>并且把它推向了<span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">github.</span></span></span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#000000;\">接下来我们将做什么呢<span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">?</span></span></span> \r\n</p>\r\n<p>\r\n	&nbsp;\r\n</p>\r\n<p>\r\n	&nbsp;\r\n</p>\r\n<p>\r\n	<span style=\"color:#000000;\">第三节<span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">:\r\nFork A Repo </span></span></span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#000000;\"><span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\"> </span></span>有些时候你发现自己想要为别人的项目做贡献<span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">.</span></span>或者希望来使用别人的项目做为自己的起点<span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">.</span></span>也就称之为<span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">Fork.</span></span></span> \r\n</p>\r\n<ol>\r\n	<li>\r\n		&nbsp;\r\n		<ol>\r\n			<li>\r\n				&nbsp;\r\n				<ol>\r\n					<li>\r\n						<p>\r\n							<span style=\"color:#000000;\"><span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">Fork</span></span>一个项目<span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">. </span></span>在你想<span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">fork</span></span>的项目的首页<span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">.</span></span>找到<span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">fork</span></span>按钮<span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">.</span></span>点击<span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">.</span></span></span> \r\n						</p>\r\n					</li>\r\n					<li>\r\n						<p>\r\n							<span style=\"color:#000000;\">接下来设置你本地仓库<span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">.</span></span></span> \r\n						</p>\r\n						<p>\r\n							<span style=\"color:#000000;\"><span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">A\r\n			. </span></span>克隆项目<span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">.</span></span></span> \r\n						</p>\r\n						<p>\r\n							<span style=\"color:#000000;\"><span style=\"color:#808000;\"> <span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">$\r\n			git clone <a href=\"mailto:git@github.com\">git@github.com</a>:username/projectname.git</span></span></span></span> \r\n						</p>\r\n						<p>\r\n							<span style=\"color:#000000;\"><span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">B. </span></span>远程配置<span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">.</span></span></span> \r\n						</p>\r\n						<p>\r\n							<span style=\"color:#000000;\">当你克隆了一个项目之后<span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">.</span></span>它有一个默认的<span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">remote.</span></span>叫做<span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">.origin.</span></span>这是指你是在<span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">github</span></span>上<span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">fork</span></span>的<span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">.</span></span>而不是在原来的仓库<span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">.</span></span>为了跟踪原本的仓库<span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">,</span></span>你需要添加另一个叫做<span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">upstream</span></span>的选项<span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">.</span></span></span> \r\n						</p>\r\n						<p>\r\n							<span style=\"color:#808000;\"><span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">$cd\r\n			projectname</span></span></span> \r\n						</p>\r\n						<p>\r\n							<span style=\"color:#808000;\"><span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">$\r\n			git remote add upstream git://github.com/username/projectname.git</span></span></span> \r\n						</p>\r\n						<p>\r\n							<span style=\"color:#808000;\"><span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">$\r\n			git fetch upstream</span></span></span> \r\n						</p>\r\n					</li>\r\n				</ol>\r\n			</li>\r\n		</ol>\r\n	</li>\r\n</ol>\r\n<ol>\r\n	<li>\r\n		&nbsp;\r\n		<ol>\r\n			<li>\r\n				&nbsp;\r\n				<ol>\r\n					<li>\r\n						<p>\r\n							<span style=\"color:#000000;\">接下来<span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">.</span></span>你要做的就是<span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">.</span></span></span> \r\n						</p>\r\n						<p>\r\n							<span style=\"color:#000000;\"> <span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">A. </span></span>推送提交<span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">. </span></span></span> \r\n						</p>\r\n						<p>\r\n							<span style=\"color:#000000;\">一旦你做出了某些提交到你<span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">fork</span></span>的仓库里<span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">,</span></span>你可能想要将其推送到你<span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">fork</span></span>的项目去<span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">.</span></span>你要做就跟平常的项目一样<span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">.</span></span></span> \r\n						</p>\r\n						<p>\r\n							<span style=\"color:#808000;\"><span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">$git\r\n			push origin master</span></span></span> \r\n						</p>\r\n					</li>\r\n				</ol>\r\n			</li>\r\n		</ol>\r\n	</li>\r\n</ol>\r\n<ol>\r\n	<li>\r\n		&nbsp;\r\n		<ol>\r\n			<li>\r\n				&nbsp;\r\n				<ol>\r\n					<li>\r\n						<p>\r\n							<span style=\"color:#000000;\">接收<span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">upstream </span></span>变更<span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">.</span></span></span> \r\n						</p>\r\n						<p>\r\n							<span style=\"color:#000000;\">如果你<span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">fork</span></span>的那个原来的仓库改变了<span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">,</span></span>你可以使用下面的命令来更新你<span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">fork</span></span>到本地的仓库<span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">.</span></span></span> \r\n						</p>\r\n						<p>\r\n							<span style=\"color:#808000;\"><span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">$\r\n			git fetch upstream</span></span></span> \r\n						</p>\r\n						<p>\r\n							<span style=\"color:#808000;\"><span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">$\r\n			git merge upstream/master</span></span></span> \r\n						</p>\r\n					</li>\r\n				</ol>\r\n			</li>\r\n		</ol>\r\n	</li>\r\n</ol>\r\n<p>\r\n	<span style=\"color:#000000;\">后面的更多使用指南请参考相关文档<span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">.</span></span>例如创建分支等<span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">.</span></span></span> \r\n</p>\r\n<p>\r\n	&nbsp;\r\n</p>\r\n<p>\r\n	<span style=\"color:#000000;\"><span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\"> </span></span><span style=\"color:#800000;\">本指南是由<span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">banxi1988</span></span></span><span style=\"color:#800000;\">根据<span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">github</span></span></span><span style=\"color:#800000;\">提供的帮助<span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">.</span></span></span><span style=\"color:#800000;\">进行的实践之后<span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">,</span></span></span><span style=\"color:#800000;\">翻译过来的<span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">.</span></span></span></span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#800000;\">由于本人水平有限<span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">.</span></span>所以翻译得不好<span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">,</span></span>你可以向我提意见或者直接到<span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">github</span></span>里面的去看帮助<span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">.</span></span></span> \r\n</p>\r\n<p>\r\n	<br />\r\n<span style=\"color:#800000;\"><span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\"></span></span></span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#800000;\"><span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\">原文地址：http://blog.csdn.net/banxi1988/article/details/6555293</span></span></span> \r\n</p>\r\n<p>\r\n	<br />\r\n<span style=\"color:#800000;\"><span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\"></span></span></span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#800000;\"><span style=\"font-family:Monaco, \'Courier New\', \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', monospace;\"><span style=\"font-size:x-small;\"><br />\r\n</span></span></span> \r\n</p>','','','1','13','blog','12','0','0','n','n','n','y','y','','','0');
INSERT INTO emlog_blog VALUES('45','【高级软件工程】使用 RSpec 來測試 App','1444100415','<article class=\"container\">\r\n<div class=\"span10\">\r\n	<h1>\r\n		<p>\r\n			<em><strong><span style=\"font-size:14px;color:#E53333;\">高级软件工程HW1测试文件下载地址：</span></strong><a target=\"_blank\" href=\"http://course.trustie.net/projects/671/repository/revisions/master/show/spec\"><strong><span style=\"font-size:14px;color:#FF9900;\">http://course.trustie.net/projects/671/repository/revisions/master/show/spec</span></strong></a></em> \r\n		</p>\r\n	</h1>\r\n	<h1>\r\n		使用 RSpec 來測試 App\r\n	</h1>\r\n	<p>\r\n		<em>Created by Clemens Helm, <a href=\"https://twitter.com/clemenshelm\">@clemenshelm</a> and Floor Drees, <a href=\"https://twitter.com/floordrees\">@floordrees</a></em> \r\n	</p>\r\n	<p>\r\n		<em>Translation by JuanitoFatas, <a href=\"https://twitter.com/juanitofatas\">@JuanitoFatas</a></em> \r\n	</p>\r\n	<p>\r\n		RSpec 是 Ruby 語言的測試框架之一，使用看起來很像 Ruby 的語法，來描述 App 的行為。測試結果會輸出在終端機，所以妳也可以測試妳的閱讀技巧呢（開個玩笑）。\r\n	</p>\r\n	<p>\r\n		<strong>教練：</strong> 講講測試與行為驅動開發是什麼。\r\n	</p>\r\n	<h3>\r\n		安裝 RSpec\r\n	</h3>\r\n	<p>\r\n		安裝 RSpec 與所有依賴的 RubyGems：\r\n	</p>\r\n	<div class=\"highlight\">\r\n<pre><code class=\"language-sh\" data-lang=\"sh\">gem install rspec</code></pre>\r\n	</div>\r\n	<p>\r\n		接著到終端機裡執行：\r\n	</p>\r\n	<div class=\"highlight\">\r\n<pre><code class=\"language-sh\" data-lang=\"sh\">rspec --init</code></pre>\r\n	</div>\r\n	<p>\r\n		這條命令會在專案目錄下產生 <code>.rspec</code>，以及 <code>spec</code> 目錄下產生 <code>spec_helper.rb</code>。\r\n	</p>\r\n	<p>\r\n		Rubyist 通常交互使用“測試”（test）與“規範”（specification）這兩個詞，這也是為什麼測試是存在 <code>spec</code> 目錄下。\r\n	</p>\r\n	<p>\r\n		在 <code>spec</code> 目錄建立一個 <code>idea_spec.rb</code> 檔案：\r\n	</p>\r\n	<div class=\"highlight\">\r\n<pre><code class=\"language-sh\" data-lang=\"sh\">touch spec/models/idea_spec.rb</code></pre>\r\n	</div>\r\n	<p>\r\n		填入以下內容：\r\n	</p>\r\n	<div class=\"highlight\">\r\n<pre><code class=\"language-ruby\" data-lang=\"ruby\"><span class=\"nb\">require</span> <span class=\"s1\">\'spec_helper\'</span> <span class=\"nb\">require</span> <span class=\"s1\">\'idea\'</span></code></pre>\r\n	</div>\r\n	<p>\r\n		接著讓我們來描述一個“規範”（寫一個測試）看看：\r\n	</p>\r\n	<div class=\"highlight\">\r\n<pre><code class=\"language-ruby\" data-lang=\"ruby\"><span class=\"n\">describe</span> <span class=\"no\">Idea</span> <span class=\"k\">do</span> <span class=\"n\">it</span> <span class=\"s1\">\'has a name\'</span> <span class=\"c1\"># your examples (tests) go here</span> <span class=\"k\">end</span></code></pre>\r\n	</div>\r\n	<p>\r\n		在終端機執行：\r\n	</p>\r\n	<div class=\"highlight\">\r\n<pre><code class=\"language-sh\" data-lang=\"sh\">rspec spec/models/idea_spec.rb</code></pre>\r\n	</div>\r\n	<p>\r\n		會告訴你測試處於 pending 狀態，因為測試的內容還沒實作。\r\n	</p>\r\n	<p>\r\n		<strong>教練：</strong> 講講如何使用終端機的輸出來 Google 解決問題\r\n	</p>\r\n	<p>\r\n		讓我們填入具體的測試內容！\r\n	</p>\r\n	<div class=\"highlight\">\r\n<pre><code class=\"language-ruby\" data-lang=\"ruby\"><span class=\"n\"> \r\n<pre class=\"prettyprint lang-rb linenums\">\r\n\r\n\r\n\r\n\r\n\r\n<pre class=\"prettyprint lang-rb linenums\">describe Idea do\r\n  it \"has a name\" do # yep, you can totally use \'it\'\r\n    idea = Idea.new # creating a new idea \'instance\'\r\n    idea.name.should be_true # this is our expectation\r\n  end\r\nend</pre>\r\n<br />\r\n</pre>\r\n<br />\r\n</span><span class=\"k\"></span></code></pre>\r\n	</div>\r\n	<p>\r\n		啊哈！妳的第一個測試！\r\n	</p>\r\n	<p>\r\n		<strong>教練：</strong> 逐行解釋剛剛的測試\r\n	</p>\r\n	<h4>\r\n		重構\r\n	</h4>\r\n	<p>\r\n		剛剛那兩行其實也可以寫成一行：\r\n	</p>\r\n	<div class=\"highlight\">\r\n<pre><code class=\"language-ruby\" data-lang=\"ruby\"><span class=\"n\">describe</span> <span class=\"no\">Idea</span> <span class=\"k\">do</span> <span class=\"n\">its</span><span class=\"p\">(</span><span class=\"ss\">:name</span><span class=\"p\">)</span> <span class=\"p\">{</span> <span class=\"n\">should</span> <span class=\"n\">be_true</span> <span class=\"p\">}</span> <span class=\"k\">end</span></code></pre>\r\n	</div>\r\n	<p>\r\n		這看起來好多了，但背後隱藏了許多魔法。現在我們只要知道，“重構”是根據經驗把大塊程式碼轉成小塊程式碼。\r\n	</p>\r\n	<p>\r\n		<strong>教練：</strong> 講講什麼是重構\r\n	</p>\r\n	<h4>\r\n		讓測試稍後再測（待辦事項）\r\n	</h4>\r\n	<p>\r\n		耶！待辦事項。太棒了！一個俏皮的 RSpec 功能，讓妳可以把特定的測試標註為“待辦”（pending）。\r\n	</p>\r\n	<p>\r\n		怎麼樣把一個測試變成待辦事項呢？\r\n	</p>\r\n	<p>\r\n		沒有 <code>do</code> 與 <code>end</code> 的 <code>it</code>：\r\n	</p>\r\n	<div class=\"highlight\">\r\n<pre><code class=\"language-ruby\" data-lang=\"ruby\"><span class=\"n\">it</span> <span class=\"s2\">\"has a name\"</span></code></pre>\r\n	</div>\r\n	<p>\r\n		或是在 <code>it</code> 前面加上 <code>x</code>：\r\n	</p>\r\n	<div class=\"highlight\">\r\n<pre><code class=\"language-ruby\" data-lang=\"ruby\"><span class=\"n\">describe</span> <span class=\"no\">Idea</span> <span class=\"k\">do</span> <span class=\"n\">xit</span> <span class=\"s2\">\"has a name\"</span> <span class=\"k\">do</span> <span class=\"o\">.</span><span class=\"n\">.</span><span class=\"o\">.</span> <span class=\"k\">end</span> <span class=\"k\">end</span></code></pre>\r\n	</div>\r\n	<p>\r\n		或者是把 <code>it</code> 換成 <code>pending</code> 也可以哦！\r\n	</p>\r\n	<div class=\"highlight\">\r\n<pre><code class=\"language-ruby\" data-lang=\"ruby\"><span class=\"n\">describe</span> <span class=\"no\">Idea</span> <span class=\"k\">do</span> <span class=\"n\">pending</span> <span class=\"s2\">\"has a name\"</span> <span class=\"k\">do</span> <span class=\"o\">.</span><span class=\"n\">.</span><span class=\"o\">.</span> <span class=\"k\">end</span> <span class=\"k\">end</span></code></pre>\r\n	</div>\r\n	<h3>\r\n		行為驅動開發\r\n	</h3>\r\n	<p>\r\n		行為驅動開發呢，通常是先想想我們想要做什麼，寫下期望會做的事（測試），接著心中想著測試，開始一步一步做出來。\r\n	</p>\r\n	<p>\r\n		我們可能會先寫 Idea Model 的測試 (<code>touch spec/models/idea_spec.rb</code>)：\r\n	</p>\r\n	<div class=\"highlight\">\r\n<pre><code class=\"language-ruby\" data-lang=\"ruby\"><span class=\"n\"> \r\n<pre class=\"prettyprint lang-rb linenums\">\r\n\r\n\r\n\r\n\r\n\r\n<pre class=\"prettyprint lang-rb linenums\">describe Idea do\r\n  it \"has a name\"\r\n    idea = Idea.new\r\n    idea.name.should be_true\r\n    # or, alternatively: idea.name.should == true\r\n  end\r\nend</pre>\r\n<span class=\"k\"></span></pre>\r\n</span></code>現在跑測試看看 <code>rspec spec/models/idea_spec.rb</code> 會告訴你。幸運的是，錯誤並不可怕，實際上還會幫助妳寫出需要的程式碼呢！</pre>\r\n	</div>\r\n	<p>\r\n		接著便可以實作 Idea Model 了...。\r\n	</p>\r\n	<p>\r\n		好，試著在之後的活動裡寫測試來檢查妳是否都做對了。\r\n	</p>\r\n	<div class=\"highlight\">\r\n<pre><code class=\"language-ruby\" data-lang=\"ruby\"><span class=\"n\">describe</span> <span class=\"no\">Attendee</span> <span class=\"k\">do</span> <span class=\"n\">it</span> <span class=\"s2\">\"tests everything\"</span> <span class=\"k\">end</span></code></pre>\r\n	</div>\r\n	<p>\r\n		開個玩笑的啦！\r\n	</p>\r\n	<p>\r\n		Happy Testing！\r\n	</p>\r\n	<p>\r\n		<strong>教練：</strong> 講講測試有什麼優點。\r\n	</p>\r\n	<p>\r\n		<br />\r\n	</p>\r\n	<p>\r\n		原文地址：http://railsgirls.tw/testing-rspec/\r\n	</p>\r\n	<p>\r\n		<br />\r\n	</p>\r\n</div>\r\n</article>','','','1','11','blog','49','0','0','n','n','n','y','y','','','0');
INSERT INTO emlog_blog VALUES('42','【高级软件工程】Ruby第一次作业总结','1444242604','<p>\r\n	经过两天的奋斗Ruby的第一次作业总算圆满完成了，在此期间遇到了很多的问题，也学到了很多的东西，特在此记录，做一个简单的总结。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	Ruby第一次作业题目：<a target=\"_blank\" href=\"http://www.mryu.top/homwork/43.html\">http://www.mryu.top/homwork/43.html</a> \r\n</p>\r\n<p>\r\n	Ruby第一次作业解答：<a target=\"_blank\" href=\"http://www.mryu.top/homwork/44.html\">http://www.mryu.top/homwork/44.html</a> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	Ruby on Rails 环境搭建：<a target=\"_blank\" href=\"http://www.mryu.top/os/40.html\">http://www.mryu.top/os/40.html</a> \r\n</p>\r\n<p>\r\n	GitHub使用指南：<a target=\"_blank\" href=\"http://www.mryu.top/os/41.html\">http://www.mryu.top/os/41.html</a> \r\n</p>\r\n<p>\r\n	Trustie使用指南：<a target=\"_blank\" href=\"http://forge.trustie.net/forums/1/memos/1232\">http://forge.trustie.net/forums/1/memos/1232</a> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<h2>\r\n</h2>\r\n<h1 align=\"left\">\r\n	<span style=\"font-size:12px;background-color:#E53333;\"><span style=\"background-color:#FFFFFF;color:#E53333;font-size:12px;\"> \r\n	<p>\r\n		<br />\r\n	</p>\r\n</span></span> \r\n</h1>\r\n<p>\r\n	<span style=\"font-size:12px;background-color:#E53333;\"><span style=\"background-color:#FFFFFF;color:#E53333;font-size:12px;\"> </span></span> \r\n</p>\r\n<p>\r\n	使用 RSpec 來測試 App：<a target=\"_blank\" href=\"http://www.mryu.top/digest/45.html\">http://www.mryu.top/digest/45.html</a><a target=\"_blank\" href=\"http://www.mryu.top/digest/45.html\"><br />\r\n</a> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<h1 align=\"left\">\r\n	<span style=\"font-size:12px;background-color:#E53333;\"><span style=\"background-color:#FFFFFF;color:#E53333;font-size:12px;\"> \r\n	<p>\r\n		<br />\r\n	</p>\r\n</span></span> \r\n</h1>\r\n<br />\r\n<p>\r\n	关于代码提交的一些总结：\r\n</p>\r\n<p>\r\n	第一、分别把代码上传到UCAS和trustie上（这个比较简单）&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n</p>\r\n<p>\r\n	第二、分别把代码利用git工具push 到Github 和trustie上（稍微复杂点）\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	下面是关于把代码上传到Github和trustie上的一些笔记\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<strong><span style=\"font-size:18px;\">Github</span></strong> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<pre class=\"prettyprint lang-bsh linenums\">billcode@billcode-y470:~$ git config --global user.name \"2015E8007361074\"\r\nbillcode@billcode-y470:~$ git config --global user.email \"billcode@outlook.com\"\r\n\r\nbillcode@billcode-y470:~$ cd github\r\nbillcode@billcode-y470:~/github$ mkdir course\r\nbillcode@billcode-y470:~/github$ cd course\r\nbillcode@billcode-y470:~/github/course$ git init\r\nInitialized empty Git repository in /home/billcode/github/course/.git/\r\n\r\nbillcode@billcode-y470:~/github/course$ touch README\r\nbillcode@billcode-y470:~/github/course$ git add README \r\nbillcode@billcode-y470:~/github/course$ git commit -m \'hw1\'\r\n[master (root-commit) 8376bff] hw1\r\n 0 files changed\r\n create mode 100644 README\r\n\r\nbillcode@billcode-y470:~/github/course$ git remote add origin git@github.com:2015E8007361074/course.git\r\n\r\nbillcode@billcode-y470:~/github/course$ git push -u origin master\r\nCounting objects: 3, done.\r\nWriting objects: 100% (3/3), 207 bytes, done.\r\nTotal 3 (delta 0), reused 0 (delta 0)\r\nTo git@github.com:2015E8007361074/course.git\r\n * [new branch]      master -&gt; master\r\nBranch master set up to track remote branch master from origin.\r\nbillcode@billcode-y470:~/github/course$ \r\n</pre>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<strong><span style=\"font-size:18px;\">Trustie</span></strong><br />\r\n<strong><span style=\"font-size:18px;\"></span></strong> \r\n</p>\r\n<p>\r\n	<strong><span style=\"font-size:18px;\"></span></strong> \r\n</p>\r\n<pre class=\"prettyprint lang-bsh linenums\">billcode@billcode-y470:~/github/course$ git init (将示例文件夹初始化为git版本库 注意：该步操作只需要在第一次提交代码时执行，以后提交代码不要每次都执行)\r\nReinitialized existing Git repository in /home/billcode/github/course/.git/\r\nbillcode@billcode-y470:~/github/course$ git add .（将该项目代码添加到本地版本库的暂存区中 注意:add 和.之间有空格）\r\nbillcode@billcode-y470:~/github/course$ git remote add newrepo http://billcode_course@repository.trustie.net/billcode/course.git （让newrepo指向远程版本库地址，并与本地版本库进行关联，其中http后面为远程版本的路径）\r\nbillcode@billcode-y470:~/github/course$ git add .\r\nbillcode@billcode-y470:~/github/course$ git commit -m \"20151008 first commit\"（将提交到暂存区的修改提交到本地的master分支）\r\n# On branch master\r\nnothing to commit (working directory clean)\r\nbillcode@billcode-y470:~/github/course$ git config http.postBuffer 524288000（设置本地缓存为500M 注意：只需要在第一次提交代码时操作）\r\n\r\nerror: failed to push some refs to \'http://billcode_course@repository.trustie.net/billcode/course.git\'\r\nbillcode@billcode-y470:~/github/course$ git push -u newrepo master:master（将本地代码库master分支下的代码提交到远程版本库“newrepo”的master分支中）\r\nPassword for \'http://billcode_course@repository.trustie.net\': \r\nremote: /home/pdl/redmine-2.3.2-0/apache2/htdocs/billcode/course.git\r\nTo http://billcode_course@repository.trustie.net/billcode/course.git\r\n * [new branch]      master -&gt; master\r\nBranch master set up to track remote branch master from newrepo.\r\n</pre>\r\n<p>\r\n	<br />\r\n</p>','','','1','14','blog','34','0','0','n','n','n','y','y','','','0');
INSERT INTO emlog_blog VALUES('43','【高级软件工程】Ruby第一次作业题目','1444241792','<div class=\"textPanel\">\r\n	<b>1. Arrays, Hashes, and Enumerables</b><br />\r\n<br />\r\nCheck the Ruby 2.x documentation on Array, Hash and Enumerable as they could help tremendously with these exercises. <br />\r\n<br />\r\n&nbsp;&nbsp;&nbsp; Define a method sum(array) that takes an array of integers as an \r\nargument and returns the sum of its elements. For an empty array it \r\nshould return zero.<br />\r\n<br />\r\n&nbsp;&nbsp;&nbsp; Define a method max_2_sum(array) which takes an array of integers as\r\n an argument and returns the sum of its two largest elements. For an \r\nempty array it should return zero. For an array with just one element, \r\nit should return that element.<br />\r\n<br />\r\n&nbsp;&nbsp;&nbsp; Define a method sum_to_n?(array) that takes an array of integers and\r\n an additional integer, n, as arguments and returns true if any two \r\nelements in the array of integers sum to n. An empty array should sum to\r\n zero by definition.<br />\r\n<br />\r\nYou can check your progress by running rspec spec/part1_spec.rb, or just running autotest and leaving it running.<br />\r\n<br />\r\n<b>2. Strings and Regular Expressions</b><br />\r\n<br />\r\nCheck the documentation on String and Regexp as they could help tremendously with these exercises. <br />\r\n<br />\r\n&nbsp;&nbsp;&nbsp; Define a method hello(name) that takes a string representing a name \r\nand returns the string \"Hello, \" concatenated with the name.<br />\r\n<br />\r\n&nbsp;&nbsp;&nbsp; Define a method starts_with_consonant?(s) that takes a string and \r\nreturns true if it starts with a consonant and false otherwise. (For our\r\n purposes, a consonant is any letter other than A, E, I, O, U.) NOTE: be\r\n sure it works for both upper and lower case and for nonletters!<br />\r\n<br />\r\n&nbsp;&nbsp;&nbsp; Define a method binary_multiple_of_4?(s) that takes a string and \r\nreturns true if the string represents a binary number that is a multiple\r\n of 4. NOTE: be sure it returns false if the string is not a valid \r\nbinary number!<br />\r\n<b><br />\r\n3. Object Oriented Basics</b><br />\r\n<br />\r\nDefine a class BookInStock which represents a book with an ISBN number, \r\nisbn, and price of the book as a floating-point number, price, as \r\nattributes.<br />\r\n<br />\r\nThe constructor should accept the ISBN number (a string, since in real \r\nlife ISBN numbers can begin with zero and can include hyphens) as the \r\nfirst argument and price as second argument, and should raise \r\nArgumentError (one of Ruby\'s built-in exception types) if the ISBN \r\nnumber is the empty string or if the price is less than or equal to \r\nzero. Include the proper getters and setters for these attributes.<br />\r\n<br />\r\nInclude a method price_as_string that returns the price of the book \r\nformatted with a leading dollar sign and two decimal places, that is, a \r\nprice of 20 should format as \"$20.00\" and a price of 33.8 should format \r\nas \"$33.80\".<br />\r\n<br />\r\n<span style=\"color:#FF0000;\"><b>NOTE</b></span>: Write you code in the attached <i><b>ruby_intro.rb </b></i>(a skeleton), and sumbit your homework to:\r\n	<ul>\r\n		<li>\r\n			<b>UCAS </b>course website, and <b>Trustie </b>website\r\n		</li>\r\n		<li>\r\n			upload your code (ruby_intro.rb) to your <b>Github</b> and <b>Trustie </b>repositories\r\n		</li>\r\n	</ul>\r\n</div>','','','1','14','blog','12','0','0','n','n','n','y','y','','','0');
INSERT INTO emlog_blog VALUES('44','【高级软件工程】Ruby第一次作业解答','1444241874','<pre class=\"prettyprint lang-rb linenums\">#author:Wenyan Yu\r\n#institute:Computer and Control\r\n#class:710\r\n#student ID:2015E8007361074\r\n#personal website:www.mryu.top\r\n\r\n#Part1\r\ndef sum arr\r\n	sum_arr=0\r\n	if arr.length==0\r\n		return sum_arr\r\n	else\r\n		for i in 0...arr.length\r\n		sum_arr+=arr[i] \r\n		end#for\r\n	end#if\r\n	return sum_arr#返回数组所有元素之和\r\nend\r\n\r\ndef max_2_sum arr\r\n\r\n	if arr.length==0\r\n		return 0#若数组为空，则返回0\r\n	elsif arr.length==1\r\n		return arr[0]#若数组只有一个元素，则返回这个元素\r\n	else\r\n		large1=arr[0]\r\n		large2=arr[1]\r\n		if large1&lt;large2 then large1,large2 = large2,large1 end\r\n\r\n		for i in 2...arr.length\r\n		 	if arr[i]&gt;large2 then large2=arr[i] end\r\n			if large1&lt;large2 then large1,large2 = large2,large1 end\r\n		end#for\r\n		return large1+large2#若数组有多余一个元素，则返回它最大的两个元素之和	\r\n	end#if\r\n\r\nend\r\n\r\ndef sum_to_n? arr,n\r\n	\r\n	\r\n	if arr.empty?\r\n		return false if n==0\r\n	else \r\n		for i in 0...arr.length \r\n			for j in (i+1)...arr.length\r\n				return true if arr[i]+arr[j]==n\r\n			end#for 内\r\n		end#for 外\r\n		\r\n	end#if\r\n	return false	\r\nend\r\n\r\n#Part 2\r\n\r\ndef hello(name)\r\n	return \"Hello, \"+name\r\nend\r\n\r\ndef starts_with_consonant? s\r\n		str=\"AEIOU\"\r\n		str1=\"ABCDEFGHIJKLMNOPQRSTUVWXYZ\"\r\n		s=s.upcase#将字符串全部转换为大写		\r\n		flag=false#s是以辅音字母开头则flag置为true,否则置为false\r\n		#puts str !~ /B/\r\n		#puts str1 =~ /B/\r\n		#if str1 =~ /B/ and str !~ /B/ then flag=true end\r\n		if str1.include?s[0,1] and !str.include?s[0,1] then flag=true end#如果s的首字母是辅音字母则将flag置为true\r\n		return flag\r\nend\r\n\r\ndef binary_multiple_of_4? s\r\n\r\n	#	if s =~ /^[0-1]+$/\r\n    	#	  return s.to_i(2) % 4 == 0 unless s == \"0\"\r\n  	#	end\r\n\r\n  	#	return false\r\n	\r\n\r\n	 	\r\n	#return  s=~ /^[01]*1[01]*00$/ \r\n	if s.empty?\r\n		return false\r\n	end\r\n	for i in 0...s.length\r\n		if s[i] &lt; \"0\" || s[i] &gt; \"2\"\r\n			return false\r\n		end        \r\n	\r\n	end\r\n		\r\n	if s.to_i % 4==0\r\n		return true\r\n	else \r\n		return false\r\n	end\r\n	\r\n	\r\n	\r\n	\r\n	\r\n	\r\nend\r\n\r\n\r\n#Part 3\r\n\r\nclass BookInStock\r\n\r\n	def initialize(isbn,price)\r\n		raise ArgumentError if isbn.empty? || price &lt;= 0	\r\n		@isbn=isbn\r\n		@price=price\r\n	end#初始化方法结束\r\n	\r\n	def price_as_string\r\n		format(\"$%.2f\",@price)\r\n	end#方法结束\r\n	attr_accessor:isbn\r\n	attr_accessor:price\r\n	\r\nend	\r\n</pre>\r\n<br />','','','1','14','blog','25','0','0','n','n','n','y','y','','','0');
INSERT INTO emlog_blog VALUES('47','虾米','1444323203','<br />\n<p>\n	<strong><br />\n</strong>\n</p>\n<p>\n	<strong><span style=\"color:#009900;\"><span style=\"color:#009900;\">虾米昵称：</span><span style=\"color:#009900;\">云中布衣</span></span></strong>\n</p>\n<p>\n	<strong><strong><span style=\"color:#009900;\">虾米动态播播：实时智能的更新显示我正在听的歌曲</span></strong></strong>\n</p>\n<p>\n	<strong><strong></strong><br />\n</strong>\n</p>\n<div align=\"center\">\n	<embed src=\"http://www.xiami.com/widget/10960959_10960959_850_346_ea827f_bfe5e8_0/reviewPlayer.swf\" type=\"application/x-shockwave-flash\" width=\"850\" height=\"346\" wmode=\"opaque\" />\n</div>','','xiami','1','-1','page','0','0','0','n','n','n','y','y','','','0');
